INSERT INTO `tb_extra` (`id_extra`, `extra_name`, `extra_text`, `extra_type`, `extra_logo`, `extra_image`, `instagram`, `telegram`, `extra_hari`) VALUES (1, 'MCC', '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\"><em>&nbsp;&nbsp;Metamorph Creative Crew </em>atau biasa disingkat MCC adalah&nbsp; ekstrakulikuler jurnalistik di SMK Negeri 1 Purwosari. Cikal bakal MCC ini sudah ada sejak SMK Negeri 1 Purwosari berdiri yaitu pada tahun 2000. Namun, secara resmi memakai nama Metamorph Creative Crew pada tahun 2008.&nbsp; Metamorph berarti perubahan atau yang membawa perubahan, sesuai dengan namanya.&nbsp; <em>Creative </em>berarti siswa yang kreatif dan <em>crew </em>berarti tim. jadi didalam MCC tidak ada yang namanya kerja sendiri-sendiri.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">&nbsp;&nbsp;Ekstrakulikuler MCC ini bertugas mendokumentasi acara sekolah, membuat mading (MetaWall),&nbsp; membuat majalah digital (MetaSite ) dan majalah sekolah (MetaMagz). Biasanya MetaSite dibuat saat ada lomba mading. Di setiap bulannya, MetaWall selalu mempunyai tema yang berbeda. Nah untuk majalah sekolah, diterbitkan satu semester sekali. Dan tentunya mempunyai tema yang berbeda-beda di setiap tahunnya. MCC dibina oleh Bu Iswayunil selaku guru bahasa Indonesia di SMKN 1 Purwosari&nbsp;</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">&nbsp;&nbsp;&nbsp;Terdapat beberapa divisi yang ada di ekstrakulikuler ini. Seperti divis<em>i reporter </em>tugasnya untuk mencari informasi atau berita yang memungkinkan untuk dijadikan artikel di mading, majalah, atau mungkin sosial media. <em>Reporter</em> juga biasanya mewawancarai guru/siswa yang bersangkutan dengan artikel. Divisi <em>Editor</em> tugasnya mengedit artikel yang sudah dirangkai atau dicari oleh <em>reporter. </em>Divisi <em>editor </em>juga membuat artikel dari hasil wawancara divisi<em> reporter</em>. Divisi <em>Layouter </em>tugasnya membuat <em>desain</em> atau susunan dari artikel jadi lebih menarik untuk dibaca. Biasanya diberi gambar- gambar yang menarik dan masih berkaitan dengan isi artikel tersebut. Divisi <em>Fotografer</em> tugasnya menjadi <em>dokumenter</em> acara di sekolah. Biasanya bersangkutan dengan acara yang diselenggarakan oleh OSIS.</span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Ada beberapa artikel di MCC :</span></span></span></p>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Meta Medical(kesehatan)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaFile(tema yang dibahas)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaPedia (&nbsp; ensiklopedia)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaGallery ( Short Story dan Poem )</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaNature (alam)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaStyle (busana)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaReviews berisi tentang :</span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:80px\">\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaMusic ( lagu-lagu)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaGadget (gadget)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaMovie ( film)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaBook ( buku)&nbsp;</span></span></span></li>\r\n</ul>\r\n\r\n<ul style=\"margin-left:40px\">\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaSchooltivity (kegiatan sekolah)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaZodia (ramalan zodiak setiap bulannya atau tahun jika MetaMagz)</span></span></span></li>\r\n	<li style=\"list-style-type:disc; text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">MetaEmail (pesan dari siswa SMK Negeri 1 Purwosari kepada seseorang, suatu organisasi, dan kadang untuk di luar warga SMK Negeri 1 Purwosari)</span></span></span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'Jurnalistik', 'mcc.jpg', 'mcc.jpg', 'mcc_smknusa', '085465027785', 'Jumat - Sabtu');
INSERT INTO `tb_extra` (`id_extra`, `extra_name`, `extra_text`, `extra_type`, `extra_logo`, `extra_image`, `instagram`, `telegram`, `extra_hari`) VALUES (2, 'BC', '<p style=\"text-align:justify\">X-Gen Broadcasting atau kerap disingkat BC adalah salah satu ekstrakulikuler di SMKN 1 PURWOSARI yang mempelajari dunia Videography, Presenter, dan lain-lain.Nama X-Gen memiliki arti akan merekrut siswa kelas 10 dan akan terus berlanjut.<br />\r\nBC bertugas mendokumentasikan dalam bentuk video kegiatan-kegiatan yang diadakan di SMKN 1 Purwosari. Disamping itu, BC memiliki kegiatan membuat karya seperti film pendek, video clip, iklan layanan masyarakat, vlog dan lain lain. Ekstra ini dilaksanakan setiap hari jumat di gedung D SMKN 1 Purwosari.<br />\r\nEkstrakulikuler yang diasuh oleh Pak Lutfi Anshori, S.Kom juga memiliki tugas untuk mengelola studio radio untuk melakukan siaran pada setiap hari pada jam istirahat sekolah dengan memutarkan &nbsp;lagu-lagu pilihan dan dapat menerima permintaan lagu untuk diputarkan&nbsp;<br />\r\n&nbsp;<br />\r\nAdapun pilihan pekerjaan yang ada adalah<br />\r\n1. Cameramen&nbsp;<br />\r\n2. Director&nbsp;<br />\r\n3. Editor<br />\r\n4. Presenter<br />\r\n5. Photographer<br />\r\n6. penyiar</p>\r\n', 'BroadCaster', 'bc.jpg', 'bc.jpg', 'xgen.smknusa', '+6285655577207', 'Jumat - Sabtu');
INSERT INTO `tb_extra` (`id_extra`, `extra_name`, `extra_text`, `extra_type`, `extra_logo`, `extra_image`, `instagram`, `telegram`, `extra_hari`) VALUES (6, 'PMR', '<p style=\"text-align:justify\"><strong>Palang Merah Remaja</strong> atau <strong>PMR</strong> adalah salah satu ekstrakulikuler yang ada di SMKN 1 PURWOSARI yang beranggotakan siswa siswi tanpa membedakan jurusan. <strong>PMR</strong> sendiri merupakan organisasi binaan dari Palang Merah Indonesia yang berpusat di sekolah-sekolah ataupun kelompok-kelompok masyarakat (sanggar, kelompok belajar, dll.) yang bertujuan membangun dan mengembangkan karakter Kepalangmerahan agar siap menjadi Relawan <strong>PMI</strong> pada masa depan. <strong>PMR WIRA SMKN 1 PURWOSARI</strong> telah mengikuti berbagai kegiatan yang berhubungan dengan keselamatan, pertolongan pertama, kebersihan dan kesehatan. Bertempat di <strong>UKS SMKN 1 PURWOSARI</strong>, setiap bulannya kami membuat majalah dinding yang bertemakan tentang <strong>PMR</strong>&nbsp;yang nantinya akan dipajang di papan informasi agar para siswa dapat membaca serta memahami makna dari isi mading tersebut. Selain itu, kami juga menjalankan bank sampah dengan mengumpulkan botol-botol bekas yang ada disekolah untuk dijual dan hasilnya dimasukkan ke kas ekstra, dengan begitu kami dilatih untuk hidup bersih, kreatif dan mandiri.</p>\r\n', 'Kesehatan', '1598677505.jpg', '1598755856.jpg', 'pmr.smkn1purwosari', ' ', 'jumat - -');
INSERT INTO `tb_extra` (`id_extra`, `extra_name`, `extra_text`, `extra_type`, `extra_logo`, `extra_image`, `instagram`, `telegram`, `extra_hari`) VALUES (7, 'PKS', '<p style=\"margin-right:477px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Patroli : berkeliling&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-right:291px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Keamanan : tampat yang terhindar dari ancaman&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-right:220px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Sekolah : tampat berlangsungnya kegiatan belajar mengajar&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-right:560px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">Jadi....&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">PKS adalah suatu organisasi yang mewadahi pelajar pelopor di bidang lalu lintas yang&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-right:-1px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">bertugas menjaga keamanan dan ketertiban di lingkungan sekolah dan masyarakat. Bertujuan&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">untuk membantu masyarakat untuk menertibkan keamanan dan untuk melatih siswa agar&nbsp;</span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#000000\">disiplin dan bertanggung jawab. Kegiatan PKS juga mempelajari pengetahuan kelalulintasan&nbsp;</span></span></span></p>\r\n\r\n<p style=\"margin-right:324px\"><span style=\"font-size:12pt\"><span style=\"font-family:Arial\"><span style=\"color:#00000