INSERT INTO `tb_jurusan` (`id_jurusan`, `jurusan_nama`, `jurusan_short`, `jurusan_thumbnail`, `jurusan_text`, `id_prodi`) VALUES (3, 'Teknik Komputer dan Jaringan', 'TKJ', 'tkj.png', '<p>TEKNIK KOMPUTER DAN JARINGAN</p>\r\n\r\n<p><br />\r\nVisi<br />\r\nMewujudkan Tamatan Kompetensi Keahlian Teknik Komputer dan Jaringan yang mandiri, berkualitas, berkarakter dan berbudaya lingkungan sesuai dengan tuntutan Industri IT berskala global</p>\r\n\r\n<p><br />\r\nMisi<br />\r\nMenyelenggarakan kegiatan belajar dan mengajar yang menitik beratkan kepada aspek pengetahuan, keterampilan dan sikap secara berimbang. yang dilandasi dengan kedisiplinan, kemandirian dan budi pekerti luhur.</p>\r\n\r\n<p>Meningkatkan kualitas pendidik melalui sertifikasi kompetensi dan on the job training di berbagai lembaga diklat dan DUDI.</p>\r\n\r\n<p>Mengembangan potensi siswa melalui pembinaan mental dan kedisiplinan.</p>\r\n\r\n<p>Menjalin kerja sama dengan dunia industri yang sesuai sehingga penyelenggaraan pendidikan sistem ganda dapat direalisasikan dengan tepat.</p>\r\n\r\n<p>Melaksanakan pelayanan prima dalam pengelolaan kompetensi keahlian tkj</p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\">Pada awalnya di SMK Negeri 1 Purwosari ini hanya ada satu jurusan Teknik Komputer. Tapi, pada tahun 2004 program keahlian Teknik Informasi di bagi menjadi 2 yaitu, Teknik Komputer Jaringan (TKJ) dan Multimedia (MM). Program Keahlian Tehnik Komputer Jaringan ini akan memberikan Pendidikan dan Pelatihan yang berorientasi pada pemanfaatan teknologi Komputer dalam bentuk jaringan (Network).</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\">Teknik Komputer Jaringan sudah menjadi kebutuhan masyarakat dunia, komputer tidak akan bermanfaat lebih jika belum tersambung (terkoneksi) dengan jaringan Internet. Berbagai pola jaringan komputer akan dilatihkan di program keahlian komputer dan jaringan ini. pada Program keahlian ini&nbsp;memiliki 3 fasilitas laboratorium dan sarana miniatur untuk tower WiFi.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\">Dalam jurusan ini sekolah menyediakan beberapa fasilitas untuk praktek. Diantaranya 1 komputer untuk 2 orang, mempunyai 2 ruangan khusus yang digunakan untuk membangun jaringan lokal dan jaringan WAN dan area WIFI yang memudahkan para siswa-siswi mengakses layanan internet secara gratis. Dengan adanya sarana dan prasarana yang cukup memotivasi para siswa-siswi untuk meraih berbagai prestasi dalam berbagai pelajaran maupun dalam kompetensi.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"color:#302e22; font-family:Arial,Helvetica,sans-serif; font-size:small\">&nbsp;Tujuan</span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\">Secara khusus tujuan TKJ adalah membekali peserta didik dengan keterampilan, pengetahuan dan sikap agar kompeten</span></span></span></span></span></p>\r\n\r\n<ol style=\"margin-left:32px\">\r\n	<li><span style=\"font-size:small\">Menginstall perangkat computer personal dan menginstall system operasi dan aplikasi</span></li>\r\n	<li><span style=\"font-size:small\">Menginstall perangkat jaringan berbasis local</span></li>\r\n	<li><span style=\"font-size:small\">Menginstall perangkat jaringan berbasis luas</span></li>\r\n	<li><span style=\"font-size:small\">Merancang bangun dan mengaministrasi jaringan berbasis luas.</span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\">&nbsp;<br />\r\n<br />\r\n&nbsp;</span></span></span></span></p>\r\n\r\n<table style=\"background-color:#ffffff; color:#302e22; font-family:Arial,Helvetica,sans-serif; font-size:12px; font-style:normal; font-variant-ligatures:normal; font-weight:400; text-align:start; text-decoration-color:initial; text-decoration-style:initial; white-space:normal; width:446px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:20px; width:446px\"><strong>MATA PELAJARAN PAKET KEAHLIAN TEKNIK KOMPUTER DAN JARINGAN</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:20px\"><strong>Kelompok A (Wajib):</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 1. Pendidikan Agama dan Budi Pekerti</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 2. Pendidikan Pancasila dan Kewarganegaraan</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 3. Bahasa Indonesia</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 4. Matematika</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 5. Sejarah Indonesia</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:21px; width:446px\">&nbsp;&nbsp;&nbsp; 6. Bahasa Inggris</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"background-color:#ffffff; height:20px; width:446px\"><strong>Kelompok B (Wajib):</strong></td>\r\n		<