INSERT INTO `tb_jurusan` (`id_jurusan`, `jurusan_nama`, `jurusan_short`, `jurusan_thumbnail`, `jurusan_text`, `id_prodi`) VALUES (4, 'Teknik Body Otomotif', 'TPBO', '1632287792.jpeg', '<p style=\"text-align:start\">Visi<br />\r\nMembentuk tamatan yang ber IMTAQ kepada Tuhan Yang Maha Esa, berbudi pekerti luhur, berdaya saing tinggi pada Kompetensi Keahlian Teknik Bodi Otomotif , peduli dan berbudaya lingkungan dan Industri</p>\r\n\r\n<p style=\"text-align:start\">Misi<br />\r\n1. Membentuk siswa dalam mencapai kompetensinya (Skill) pada Teknik Perbaikan Body Otomotif dan Painting, yang berorientasi pada standart industri.<br />\r\n2.&nbsp;Mampu menciptakan produk dan jasa yang berkualitas<br />\r\n3. Mengembangkan pola kemitraan industri yang berorientasi pada keseimbangan lingkungan hidup<br />\r\n4. Membentuk siswa yang mempunyai disiplin dan etos kerja tinggi, kreatif dan enovatif</p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">LATAR BELAKANG</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">Untuk mengembangkan kemampuan siswa dalam segala aspek, agar tujuan pendidikan menengah kejuruan dapat tercapai yaitu meningkatkan kecerdasan pengetahuan, kepribadian, akhlak mulia, serta keterampilan untuk hidup mandiri dan mengikuti pendidikan lebih lanjut sesuai dengan kejuruannya di SMK. Dengan demikian semua kegiatan yang ada di jurusan mengacu pada tujuan pendidikan menengah kejuruan sehingga ada kesinambungan kemampuan siswa dengan dunia usaha/industri serta dapat terserap di perguruan tinggi yang akhirnya akan mencapai tujuan pendidikan nasional yaitu mencerdaskan kehidupan bangsa.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">TUJUAN</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Menerapkan hidup sehat, memiliki wawasan pengetahuan dan keterampilan</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Bekerja baik secara mandiri atau mengisi lowongan pekerjaan yang ada di dunia usaha dan dunia industri sebagai tenaga kerja tingkat menengah dalam bidang Bodi Otomotif</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:arial,helvetica,sans-serif\">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Memilih karir, berkompetisi dan mengembangkan sikap profesional dalam bidang teknik bodi otomotif</span></span></span></span></span></span></p>\r\n\r\n<p>TEKNIK BODY OTOMOTIF<br />\r\nVisi<br />\r\nMembangun bengkel berstandar industri</p>\r\n\r\n<p>Melaksanan pembelajaran berbasis Teaching Factory<br />\r\nMisi</p>\r\n\r\n<p>1. Kerjasama dengan industri melalui program &quot; PINTAR BERSAMA DAIHASTSU&quot;</p>\r\n\r\n<p>2. Melaksanakan budaya 5S</p>\r\n\r\n<p>3. Menjalankan UPJ TKR untuk mendukung Teaching Factory</p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-family:arial,helvetica,sans-serif\"><span style=\"font-size:small\">STRATEGI PENCAPAIAN SASARAN MUTU</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Memperbanyak pekerjaan rumah dan mengevaluasi setiap minggu</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Memberi materi teori produktif melalui modul, dan media lain yang menunjang sebagai referensi</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Mengoptimalkan jam praktek sekolah</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Memperbanyak dan menjaga hubungan baik dengan DU/DI</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Mengadakan pembelajaran outshourching dengan pihak DU/DI yang relevan</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Mempersiapkan dan memotivasi siswa kelas XII untuk ikut perekrutan DU/DI</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Mengoptimalkan mesin/alat dengan melakukan M &amp; R sesuai scedul</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Melengkapi peralatan dan perlengkapan sesuai standart DU/DI</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Menambah referensi pembelajaran dari modul industri yang sinkron dengan kurikulum sekolah</span><br />\r\n<br />\r\n<span style=\"font-size:small\">Menambah media pembelajaran power point untuk teori produktif perbaikan body otomotif</span></span></span></span></spa