INSERT INTO `tb_jurusan` (`id_jurusan`, `jurusan_nama`, `jurusan_short`, `jurusan_thumbnail`, `jurusan_text`, `id_prodi`) VALUES (6, 'Teknik Bodi Kendaraan Ringan', 'TBKR', '1632272829.jpeg', '<p>TEKNIK KENDARAAN RINGAN OTOMOTIF<br />\r\nVisi<br />\r\nMembangun bengkel berstandar industri</p>\r\n\r\n<p>Melaksanan pembelajaran berbasis Teaching Factory<br />\r\nMisi</p>\r\n\r\n<p>1. Kerjasama dengan industri melalui program &quot; PINTAR BERSAMA DAIHASTSU&quot;</p>\r\n\r\n<p>2. Melaksanakan budaya 5S</p>\r\n\r\n<p>3. Menjalankan UPJ TKR untuk mendukung Teaching Factory</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tujuan</p>\r\n\r\n<p>Membentuk tamatan yang berdayang saing tinggi dalam bidang Teknik Kendaraan Ringan.</p>\r\n\r\n<p>MO lebih mempelajari tentang bagaimana seorang siswa dapat&nbsp; merawat ataupun memelihara dengan baik suatu mesin kendaraan.Mekanik Otomotif atau yang lebih dikenal&nbsp; dengan MO lebih mempelajari tentang bagaimana seorang siswa dapat merawat ataupun memelihara dengan baik&nbsp; suatu mesin kendaraan.</p>\r\n\r\n<p>&nbsp;Pembelajaran di smkn 1 purwosari bidang keahlian MO yang diperkenalkan&nbsp; kepada siswa diantaranya :</p>\r\n\r\n<p>&nbsp; 01. Pemeliharaan Service Sistem Hidrolik</p>\r\n\r\n<p>&nbsp;02. Pemasangan Sistem Hidrolik</p>\r\n\r\n<p>&nbsp;03. Overhoul Komponen Sistem Rem</p>\r\n\r\n<p>&nbsp;04. Pemasangan, Pengujian &amp; Perbaikan Sistem Penerangan &amp; Wiring</p>\r\n\r\n<p>&nbsp;05. Perbaikan Poros Penggerak Roda</p>\r\n\r\n<p>&nbsp;06. Pemeriksaan Sistem Suspensi</p>\r\n\r\n<p>&nbsp;07. Perbaikan Ringan Pada Rangkaian Sistem Kelistrikan</p>\r\n\r\n<p>&nbsp;08. Perbaikan Sistem Rem&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&