INSERT INTO `tb_jurusan` (`id_jurusan`, `jurusan_nama`, `jurusan_short`, `jurusan_thumbnail`, `jurusan_text`, `id_prodi`) VALUES (8, 'Mekatronika', 'MKT', '1598501457.jpg', '<p>Visi Misi<br />\r\nTEKNIK MEKATRONIKA</p>\r\n\r\n<p>Visi</p>\r\n\r\n<p>Membentuk tamatan yang memiliki mutu tinggi dengan dengan dibekali denga ketrampilan dan etos kerja yang tinggi sesuai dengan standar kebutuhan industri</p>\r\n\r\n<p style=\"text-align:start\">Misi</p>\r\n\r\n<p>1. Mewujudkan capaian wajib belajar di Kompetensi Mekatronika standard pendidik dan kependidikan) berbasis industri</p>\r\n\r\n<p>2. Terlaksananya program pemagangan siswa kelas XIII</p>\r\n\r\n<p>3. Mewujudkan pembelajaran berbasis Teaching Factory</p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#302e22\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:small\"><span style=\"font-family:Arial,sans-serif\">Tujuan</span></span><br />\r\n<br />\r\n<span style=\"font-size:small\"><span style=\"font-family:Arial,sans-serif\">1.&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:Arial,sans-serif\">Menyiapkan peserta didik agar dapat bekerja, baik secara mandiri atau mengisi lowongan pekerjaan yang ada di dunia usaha atau dunia industri sebagai tenaga kerja tingkat menengah, sesuai dengan bidang program keahlian yang dimiliki.</span></span><br />\r\n<br />\r\n<span style=\"font-size:small\"><span style=\"font-family:Arial,sans-serif\">2.&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:Arial,sans-serif\">Membekali peserta didik agar mampu memilih karir, ulet dan gigi dalam berkompetisi dan mampu mengembangkan sikap profesional dalam bidang keahlia yang dimilikinya.</span></span><br />\r\n<br />\r\n<span style=\"font-size:small\"><span style=\"font-family:Arial,sans-serif\">3.&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:Arial,sans-serif\">Membekali peserta didik dengan ilmu pengetahua