INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (425, 'KEGIATAN MPLS ONLINE TAHUN PELAJARAN 2021-2022 ', '', '1626413849.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">PEMBUKAAN MPLS 2021 Hari 1</span></span></strong></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><a href=\"https://www.youtube.com/watch?v=aMi_2aOdinQ&amp;t=413s\" style=\"color:#0563c1; text-decoration:underline\">https://www.youtube.com/watch?v=aMi_2aOdinQ&amp;t=413s</a> </span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kegiatan MPLS 2021 hari Ke 2</span></span></strong></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><a href=\"https://www.youtube.com/watch?v=v4gbjYkvMX0\" style=\"color:#0563c1; text-decoration:underline\">https://www.youtube.com/watch?v=v4gbjYkvMX0</a> </span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">MPLS 2021 Hari Ketiga</span></span></strong></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><a href=\"https://www.youtube.com/watch?v=j3ggq083Njk\" style=\"color:#0563c1; text-decoration:underline\">https://www.youtube.com/watch?v=j3ggq083Njk</a> </span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 13, 453, NULL, 1, NULL, '2024-03-01 16:22:18', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (426, 'Pelaksanaan Asesmen Nasional SMK', '', '1632202846.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\">Asesmen Nasional adalah program penilaian terhadap mutu setiap sekolah, madrasah, dan program kesetaraan pada jenjang dasar dan menengah. Mutu satuan pendidikan dinilai berdasarkan hasil belajar murid yang mendasar (literasi, numerasi, dan karakter) serta kualitas proses belajar-mengajar dan iklim satuan pendidikan yang mendukung pembelajaran. Informasi-informasi tersebut diperoleh dari tiga instrumen utama, yaitu Asesmen Kompetensi Minimum (AKM), Survei Karakter, dan Survei Lingkungan Belajar.</p>\n\n<p style=\"text-align:justify\">Asesmen Nasional perlu dilakukan untuk meningkatkan mutu pendidikan. Asesmen ini dirancang untuk menghasilkan informasi akurat untuk memperbaiki kualitas belajar-mengajar, yang pada gilirannya akan meningkatkan hasil belajar murid. Asesmen Nasional menghasilkan informasi untuk memantau (a) perkembangan mutu dari waktu ke waktu, dan (b) kesenjangan antar bagian di dalam sistem pendidikan (misalnya kesenjangan antarkelompok sosial ekonomi dalam satuan pendidikan, kesenjangan antara satuan Pendidikan negeri dan swasta di suatu wilayah, kesenjangan antardaerah, atau pun kesenjangan antarkelompok berdasarkan atribut tertentu). Asesmen Nasional bertujuan untuk menunjukkan apa yang seharusnya menjadi tujuan utama satuan pendidikan, yakni pengembangan kompetensi dan karakter murid. Asesmen Nasional juga memberi gambaran tentang karakteristik esensial sebuah satuan pendidikan yang efektif untuk mencapai tujuan utama tersebut. Hal ini diharapkan dapat mendorong satuan pendidikan dan Dinas Pendidikan untuk memfokuskan sumber daya pada perbaikan mutu pembelajaran.&nbsp;</p>\n\n<p>Pelaksanaan&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/Asesmen%20Nasional\">Asesmen Nasional</a>&nbsp;Berbasis Komputer atau&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/ANBK%202021\">ANBK 2021</a>&nbsp;akan berlangsung mulai bulan&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/September\">September</a>&nbsp;ini. Sejumlah persiapan telah dilakukan setiap sekolah guna kelancaran&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/pelaksanaan\">pelaksanaan</a>&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/ANBK%202021\">ANBK 2021</a>, mulai dari simulasi hingga gladi bersih</p>\n\n<p><strong>Jadwal Pelaksanaan ANBK di SMKN 1 Purwosari&nbsp;(20-23&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/September\">September</a>&nbsp;2021)</strong></p>\n\n<p>Hari ke-1:</p>\n\n<p>- 07.30-09.40 - latihan (10 menit)<br />\n- 10.40-12.50 -&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/literasi%20membaca\">literasi membaca</a>&nbsp;(90 menit)<br />\n- 14.20-16.30 -&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/survei%20karakter\">survei karakter</a>&nbsp;(30 menit)</p>\n\n<p>Hari ke-2:</p>\n\n<p>- 07.30-09.40 - latihan (10 menit)<br />\n- 10.40-12.50 -&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/numerasi\">numerasi</a>&nbsp;(90 menit)<br />\n- 14.20-16.30 -&nbsp;<a href=\"https://bogor.pikiran-rakyat.com/tag/survei%20lingkungan%20belajar\">survei lingkungan belajar</a>&nbsp;(30 menit)</p>\n\n<p>&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 237, NULL, 1, NULL, '2024-03-01 16:23:50', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (427, 'Pelaksanaan Survei Lingkungan Belajar', '', '1632205400.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\">Survei Lingkungan Belajar bertujuan memotret berbagai aspek yang terkait dengan lingkungan belajar di kelas maupun di tingkat satuan pendidikan, sehingga hasil asesmen nasional secara komprehensif memberikan profil satuan pendidikan dari input-proses-output. Setiap respon yang bapak/ibu berikan diharapkan dapat mencerminkan kondisi satuan pendidikan sesungguhnya dan menjadi informasi reflektif. Oleh karena itu; kejujuran, keaktifan, serta kelengkapan dalam pengisian survei lingkungan belajar; menjadi kunci kualitas informasi yang akan diterima oleh satuan pendidikan.</p>\n\n<p style=\"text-align:justify\">Berikut prosedur pengisian Survey Lingkungan Belajar</p>\n\n<ul>\n	<li>Survei Lingkungan Belajar wajib diisi oleh seluruh Kepala Satuan Pendidikan dan Guru yang terdaftar pada sistem pendataan Dapodik dan Emis</li>\n	<li>Kepala Satuan Pendidikan dan Guru dapat login menggunakan data yang tercetak pada kartu Login SLB</li>\n	<li>Kartu login didapatkan dari proktor/operator pada satuan pendidikan masing-masing yang ditunjuk untuk mengakses dan mencetak kartu login pada halaman dashboard SLB.</li>\n	<li>Halaman dashboard SLB dapat diakses proktor/operator satuan pendidikan di&nbsp;<a href=\"https://dashboardslb.kemdikbud.go.id/\" target=\"_blank\">https://dashboardslb.kemdikbud.go.id/</a></li>\n	<li>Waktu pengisian akan aktif sesuai jadwal pelaksanaan Asesmen Nasional di setiap jenjang pendidikan.</li>\n	<li>Jika Anda bertugas di lebih dari satu satuan pendidikan, pastikan Anda mengisi survei lingkungan belajar untuk setiap tempat penugasan</li>\n	<li>Kendala selama proses pengisian dapat disampaikan ke tim helpdesk Asesmen Nasional melalui proktor/operator satuan pendidikan</li>\n</ul>\n', 0, 'SMKN 1 Purwosari', 3, 9, 222, NULL, 1, NULL, '2024-03-01 03:57:33', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (428, 'Juara 3 Lomba Video Pendek dengan Tema Nilai-nilai Kepahlawanan di Masa Pandemi Covid 19', '', '1632225732.JPG', '', NULL, NULL, '<p style=\"text-align:justify\">Selamat dan terima kasih kepada Ananda Nehemia Tito Dwi Nugraha atas prestasinya mengharumkan nama SMKN 1 Purwosari, Kab. Pasuruan meraih Juara 3 Lomba Video Pendek dengan Tema Nilai-nilai Kepahlawanan di Masa Pandemi Covid-19 dalam rangka HUT RI ke-76 yang di selenggarakan Pemerintah Kabupaten Pasuruan<br />\n<br />\nTerus berkarya untuk menjadi yang terbaik</p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 347, NULL, 1, NULL, '2024-03-04 00:13:26', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (429, 'Sosialisasi dan Koordinasi Pengembangan SMK Pusat Keunggulan', '', '1632368368.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\">Program SMK Pusat Keunggulan bertujuan untuk menghasilkan lulusan yang terserap di dunia kerja atau menjadi wirausaha melalui keselarasan pendidikan vokasi yang mendalam dan menyeluruh dengan dunia kerja serta diharapkan menjadi pusat peningkatan kualitas dan rujukan bagi SMK lainnya</p>\n\n<p style=\"text-align:justify\">program pengembangan SMK dengan kompetensi keahlian tertentu dalam peningkatan kualitas dan kinerja, yang diperkuat melalui kemitraan dan penyelarasan dengan dunia usaha, dunia industri, dunia kerja, yang akhirnya menjadi SMK rujukan yang dapat bertujuan untuk menghasilkan lulusan yang terserap di dunia kerja atau menjadi wirausaha melalui keselarasan pendidikan vokasi yang mendalam dan menyeluruh dengan dunia kerja serta diharapkan menjadi pusat peningkatan kualitas dan rujukan bagi SMK lainnya</p>\n\n<p style=\"text-align:justify\">Sosialisasi dan Koordinasi Pengembangan SMK Pusat Keunggulan dilaksanakan pada hari Kamis 23 September 2021 di Gedung Aula SMKN 1 Purwosari</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 312, NULL, 1, NULL, '2024-03-02 09:12:33', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (430, 'Budaya dan Tata Tertib Masuk Sekolah', '', '1632608166.jpg', '', NULL, NULL, '<p><iframe height=\"480\" src=\"https://drive.google.com/file/d/1p4O_ETnNnpb0TJFiDYujZDcckFuGmtXS/preview\" width=\"640\"></iframe></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 526, NULL, 1, NULL, '2024-03-04 09:36:26', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (431, 'Program Jatim Cerdas bersama Dr. Ir. Wahid Wahyudi MT di SMKN 1 Purwosari', '', '1633437026.jpeg', '', NULL, NULL, '<p>Program Jatim Cerdas yang merupakan salah satu program prioritas dari Nawa Bhakti Satya Pemerintah Provinsi Jawa Timur. Program ini telah menjadi pemicu inovasi dan prestasi pendidikan di Jawa Timur.</p>\n\n<p>Kerjasama yang yang telah dilakukan dengan&nbsp;perusahaan dalam&nbsp;negeri&nbsp;memberikan peluang luas bagi para lulusan siswa SMKN 1 Purwosari&nbsp;di Pasuruan untuk lebih cepat bekerja. Oleh karena itu&nbsp; lulusan&nbsp;lebih bisa langsung diterima kerja di perusahaan-perusahaan tersebut.</p>\n\n<p>Program Jatim Cerdas Pemprov Jatim tidak hanya melahirkan alumni&nbsp;yang siap kerja, tapi juga andal dalam berwirausaha. SMKN 1 Purwosari juga menyiapkan siswa siswinya untuk menjadi start up dan wirausahawan muda.</p>\n\n<p>Program Jatim Cerdas bersama Dr. Ir. Wahid Wahyudi MT di SMKN 1 Purwosari</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 486, NULL, 1, NULL, '2024-03-01 03:55:58', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (432, 'KT&G SCHOLARSHIP FOUNDATION BERIKAN BEASISWA KEPADA 80 SISWA BERPRESTASI', '', '1633437621.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:#f4f4f4\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Selasa, 5 Oktober 2021 SMKN 1 Purwosari&nbsp; menerima delegasi dari KT&amp;G Korea Selatan untuk penandatanganan MoU pemberian beasiswa kepada 80 Siswa bertempat di Ruang sidang 1 SMKN 1 Purwosari. Hadir dalam pertemuan itu antara lain : Mr. Hong Won Tack (President Director), Mrs. Riwayanti Triswanti (General Manager), Mr. Bagus Sugiarto Dian Fajar (HRD-L) dan Mrs. Wahyuni Dyah Prihatini (HR. Staff)dari KT&amp;G TSPM. Sedangkan dari SMKN 1 Purwosari dihadiri oleh Bpk. Drs. Syaefudin, M.Pd (Kepala Sekolah), Ibu Andri Puspitarini, S.Pd (Waka Humas), Bpk. Eko Subagyo S.Pd (Waka HKI), Bpk. Muhadhi, S.Pd, M.K.Pd (Waka Kesiswaan) serta Nadzir Tri Armono dan Rizky Fadillah sebagai perwakilan siswa penerima beasiswa.</span></span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:#f4f4f4\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Beasiswa itu akan diberikan kepada 80 orang siswa berprestasi, baik akademik maupun non akademik dan siswa dari keluarga kurang mampu. Dengan diberikannya beasiswa itu diharapkan akan lebih meningkatkan semangat para siswa untuk terus maju dan berprestasi.</span></span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:#f4f4f4\"><span style=\"font-family:&quot;Arial&quot;,sans-serif\"><span style=\"color:#333333\">Di akhir pertemuan, Mr. Hong mengatakan : &ldquo;<em>The school is clean, We hope in the future We will have a sustainable cooperation.&rdquo; </em></span></span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 312, NULL, 1, NULL, '2024-03-01 04:00:26', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (433, 'Memperingati Maulid Nabi SAW 12 Rabiul awal 1443 H / Oktober 2021', '', '1635932212.jpeg', '', NULL, NULL, '<p>Memperingati Maulid Nabi SAW 12 Rabiul awal 1443 H / Oktober 2021</p>\n\n<p>Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021&nbsp;M di Lapangan SMKN 1 Purwosari&nbsp;dilaksanakan diikuti oleh Seluruh Guru, Karyawan dan Siswa, dengan menerapkan Protokol Kesehatan acara maulid nabi dapat berjalan dengan baik</p>\n\n<p>Acara diawali dengan Shalawat yang dibawakan oleh Tim Hadrah dari siswa SMKN 1 Purwosari dan dilanjutkan dengan pembacaan Ayat Suci Alquran oleh Qiro&#39;ah, Ceramah Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021&nbsp;M yang disampaikan oleh Habib Zein Abdulloh Ba&rsquo;abud Dari Lawang, Kab. Malang</p>\n\n<p>Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021&nbsp;M dengan tema &ldquo;Mari Kita Tingkatkan Akhlaqul Karimah&rdquo;</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 141, NULL, 1, NULL, '2024-03-04 10:06:21', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (434, 'Memperingati Maulid Nabi SAW 12 Rabiul awal 1443H - Oktober 2021', '', '1635932583.jpeg', '', NULL, NULL, '<p>Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021M di SMKN 1 Purwosari&nbsp;dilaksanakan di Lapangan SMKN 1 Purwosari yang diikuti oleh seluruh guru dan Karyawan Serta Siswa, dengan menerapkan protokol kesehatan acara dapat berjalan dengan baik, Rabu (03/11).</p>\n\n<p>Acara diawali dengan pembacaan Sholawat&nbsp;dilanjutkan dengan pembacaan Ayat Suci Alquran dan Qoriah. Ceramah Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021&nbsp;M yang disampaikan oleh Habib Zein Abdulloh Ba&rsquo;abud Dari Lawang, Kab. Malang</p>\n\n<p>Peringatan Maulid Nabi Muhammad SAW Tahun 1443&nbsp;H/2021M dengan tema &ldquo;Mari Kita Tingkatkan Akhlaqul Karimah&rdquo;</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 222, NULL, 1, NULL, '2024-03-03 00:05:42', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (435, 'TAX Goes To School', '', '1637316750.jpeg', '', NULL, NULL, '<p>Tax Goes To School adalah kegiatan pengenalan perpajakan bagi Siswa/I SMA/K Sederajat yang ada di wilayah Kabupaten Pasuruan. salah satunya diselenggarakan di SMKN 1 Purwosari. Untuk saat ini masih banyak sekali masyarakat Indonesia yang masih belum mengenal dan mengerti akan perngertian pajak, fungsi pajak serta manfaat dari pajak.</p>\n\n<p>Hal ini sangat disayangkan karena pajak merupakan salah satu komponen terpenting dalam perekonomian Indonesia dimana sekitar 80-85% pendapatan negara diperoleh dari pajak. Dengan terlaksananya kegiatan ini diharapkan mampu memberikan pengetahuan dan kesadaran pajak sejak dini kepada para Siswa/I SMA/SMK sederajat. Sehingga kedepannya masyarakat Indonesia bisa lebih memahami dan juga taat dalam melaksanakan kewajiban dibidang perpajakan.</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 288, NULL, 1, NULL, '2024-03-02 19:55:34', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (436, 'Daihatsu Skill Contest Regional Jatim Bali', '', '1639691890.jpeg', '', NULL, NULL, '<p>Siswa dan guru&nbsp;jurusan Teknik Kendaraan Ringan (TKR)&nbsp;unjuk kemampuan troubleshooting mesin mobil Astra Daihatsu di Teaching Factory di SMKN 1 Purwosari, Sabtu (11/12). Dalam kontes Pintar Bersama Daihatsu ini, peserta bersaing menunjukkan kemampuan terbaiknya memecahkan soal tentang servis dan perawatan. Para peserta ini dari beberapa SMK binaan Astra Daihatsu Motor (ADM) wilayah&nbsp;yang mencakup Jawa Timur dan Bali. &ldquo;Penilaiannya pada skill contest ini adalah, troubleshooting bagi guru. Dan, peserta siswa mencakup teknik perawatan berkala mesin mobil dengan benar, untuk jarak tempuh 10 kilometer,&rdquo; demikian ketua tim juri, Aji Prima Barus Nurcahya, Agar kemampuannya lebih meningkat, lanjutnya, maka harus ditambah lagi detil dan kecepatannya dalam menyelesaikan. Terlebih, untuk project skill contest tingkat nasional, maka tentunya soal lebih sulit dan harus bersaing dengan teknisi terbaik.</p>\n\n<p>&nbsp;</p>\n\n<p>&ldquo;Targetnya, waktu penyelesaian untuk guru 30 menit, dan siswa 75 menit.&nbsp;Dikatakan, untuk menjadi teknisi senior mobil standar Astra Daihatsu sendiri harus menguasi beberapa tingkatan keahlian. Yakni, mampu melakukan service hingga jarak tempuh mesin 120 kilometer/jam.</p>\n\n<p>Dari hasil Daihatsu SMK Skill Contest, <strong>SMKN 1 Purwosari</strong> mendapatkan<strong>&nbsp;Juara 3 Bapak Amin Selaku </strong>Guru dan Siswa Binaannya<strong> Farhan Fairus Ashary kelas X TO 1</strong></p>\n', 0, 'SMKN 1 Purwosari', 3, 10, 240, NULL, 1, NULL, '2024-03-01 03:37:40', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (437, 'Bantuan Program LINK dan Match SMKN Binaan PT Gudang Garam Tbk', '', '1639692659.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\">BANTUAN PROGRAM LINK AND MATCH SMKN BINAAN PT. GUDANG GARAM Tbk.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:whitesmoke\">Perusahaan rokok Gudang Garam adalah salah satu industri rokok terkemuka di tanah air yang telah berdiri sejak tahun 1958 di kota Kediri, Jawa Timur. Hingga kini, Gudang Garam sudah terkenal &nbsp;luas baik di dalam negeri maupun mancanegara sebagai penghasil rokok kretek berkualitas tinggi.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:whitesmoke\">Gudang Garam tumbuh berdasarkan falsafah pendiri perusahaan yang kemudian dikembangkan menjadi dasar tata kelola perusahaan yang baik. Nilai-nilai tersebut seterusnya dijadikan panduan untuk senantiasa memenuhi tanggung jawab kepada karyawan dan masyarakat sekitar. Gudang Garam&nbsp; memiliki komitmen untuk menunaikan tanggung jawab sosial serta terus berkontribusi pada pembangunan masyarakat sebagai&nbsp; perwujudan tanggung jawab sosial pada masyarakat merupakan sebuah investasi bagi masa depan sekaligus kesempatan untuk memastikan agar perusahaan dan masyarakat dapat tumbuh bersama dan saling mendukung.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"background-color:whitesmoke\">Pada hari Senin, 6 Desember 2021 PT. Gudang Garam Tbk. Menyerahkan bantuan program Link and Match kepada SMKN 1 Purwosari sebagai salah satu sekolah binaannya yang berupa :</span></span></span></p>\n\n<ol>\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sistem Elektronika Dasar Training Kit</span></span></li>\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sistem Elektronika Digital Training Kit</span></span></li>\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Sistem Elektronika dengan vriable Drives Training Kit</span></span></li>\n	<li><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pneumatic Controller Training Kit</span></span></li>\n</ol>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bantuan peralatan itu diserahkan ke SMKN 1 Purwosari untuk digunakan pada proses pembelajaran di Program Keahlian Mekatronika. Dengan diberikannya bantuan itu diharapkan kompetensi yang dimiliki oleh siswa di program keahlian Mekatronika semakin meningkat.</span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 269, NULL, 1, NULL, '2024-03-03 22:34:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (438, 'SMKN 1 Purwosari Peduli Bencana Gunung Semeru', '', '1639693210.jpeg', '', NULL, NULL, '<p>Musibah bencana alam yang melanda warga di sekitar Gunung Semeru menimbulkan korban jiwa, kerugian materi, maupun non materi. Dikutip dari BNPB, total 13 warga meninggal dunia dan 41 warga lainnya mengalami luka-luka (khususnya luka bakar). Per Minggu, 5 Desember 2021 pukul 09.20 WIB, ada 902 warga yang mengungsi sebagai dampak dari sebaran awan panas dan juga abu vulkanik. Beberapa rumah warga dan jembatan penghubung Lumajang-Malang juga tertutup material vulkanik. Pantauan dari kondisi udara menunjukkan tingkat polusi yang tinggi sehingga meningkatkan risiko dari kelompok rentan seperti lansia, ibu hamil, dengan disabilitas, dan anak-anak.</p>\n\n<p>SMKN 1 Purwosari mengajak Anda berpastisipasi untuk ikut membantu saudara-saudara kita yang membutuhkan bantuan di sekitar Gunung Semeru. Bantuan dapat disalurkan melalui rekening virtual account UGM BANTUAN BENCANA di rekening berikut:</p>\n\n<p>Bank Jatim : 1092043726 an. Sofyan Hanif Abdillah, S.ST.</p>\n\n<p>Contact Person :&nbsp;</p>\n\n<p>- Muhadi, S.Pd., M.Kpd&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 085850630157</p>\n\n<p>- Sofyan Hanif Abdillah, S.ST&nbsp; &nbsp; &nbsp;: 081334509001</p>\n\n<p>- Miftachul Huda, S.Pd&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : 083147218334</p>\n\n<p>&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 401, NULL, 1, NULL, '2024-03-03 18:32:37', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (439, 'Komitmen DIDUKA untuk Rekruitmen dan Penyaluran Lulusan', '', '1640297837.jpeg', '', NULL, NULL, '<p>SMKN 1 Purwosari merupakan SMK Pusat Keunggulan yang memiliki tujuan menghasilkan lulusan yang terserap di dunia kerja atau menjadi wirausaha melalui keselarasan pendidikan vokasi yang mendalam dan menyeluruh dengan dunia kerja. Dengan harapan yang besar dalam mewujudkan cita-cita besar itu, &nbsp;SMKN 1 Purwosari&nbsp;harus mampu mengembangkan pendidikan kejuruan yang relevan dengan tuntutan kebutuhan masyarakat dan perkembangan dunia kerja berbasis kearifan lokal pada sektor pembangunan padat karya dan&nbsp;mampu mendukung proses pembelajaran secara teratur dan berkelanjutan.</p>\n\n<p>SMKN 1 Purwosari&nbsp;berkolaborasi dengan dunia kerja, bermitra dalam menyelaraskan konsep kurikulum yang adaptif dan implementatif. Kurikulum Sekolah serta bimbingan dan arahan dari dunia industri baik dalam segi penguasaan&nbsp;<em>soft skill</em>&nbsp;maupun penguasaan&nbsp;<em>hard skill</em>&nbsp;sehingga dapat menghasilkan output yang sesuai dengan harapan industri.</p>\n\n<p>Salah bentuk komitmen Kemitraan SMKN 1 Purwosari dengan dunia kerja adalah membangun komunikasi intens.&nbsp;&nbsp;Kegiatan ini dilaksanakan pada tanggal&nbsp; 21-22 Desember 2021 di Jambuluwuk Batu,&nbsp;dengan dihadiri dan penandatanganan MoU dari PT. Satoria Agro Industri &amp; PT. Satoria&nbsp;Aneka&nbsp;Industri, PT. Panverta Cakrakencana, PT. Tirta Fresindo Jaya.</p>\n', 1, 'Jambuluwuk Batu', 3, 11, 679, NULL, 1, NULL, '2024-03-01 03:59:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (440, 'Lomba Kompetensi Siswa Tahun 2022', '', '1642944221.jpeg', '', NULL, NULL, '<p>Lomba Kompetensi Siswa (<strong>LKS</strong>) Sekolah Menegah Kejuruan (SMK) tingkat Wilker 2&nbsp;dimulai 24 Januari&nbsp;2022.&nbsp;Lomba Kompetensi Siswa ini dilaksanakan secara&nbsp;tahunan antar siswa pada jenjang SMK sesuai bidang keahlian yang diajarkan pada SMK. SMKN 1 Purwosari mengirimkan 11 Peserta lomba diantaranya bidang : Food Technology, Landscaping and Gardening, IT Network System Administration, CNC Mailing, CNC Turning, Welding, Mechanical Engineering CAD, Industrial Control, Web Technology, IT Software Solutions For Business</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 378, NULL, 1, NULL, '2024-03-04 07:05:55', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (441, 'Juara 2 lomba Prusiking ekstrakurikuler Pecinta Alam tingkat Kabupaten Pasuruan', '', '1643147448.jpeg', '', NULL, NULL, '<p>Pecinta Alam adalah Istilah yang dipergunakan untuk kelompok-kelompok yang bergerak di alam bebas, pada bidang petualangan, lingkungan hidup dan konservasi alam, pendidikan maupun kemanusiaan.&nbsp;<strong>TUJUAN&nbsp;</strong>Untuk menumbuhkan,memupuk,membina dan mengembangkan kecintaan terhadap&nbsp;<strong>alam</strong>&nbsp;beserta segenap isinya sebagai pernyataan rasa cinta terhadap tuhan sang pencipta. Untuk mengembangkan dan membina pribadi yang luhur,ketahanan jasmani dan rohani serta ilmu pengetahuan demi kemanusiaan.</p>\n\n<p>Lomba Repling dan Prusiking yang diadakan oleh UKM MAPALA PANORAMA ITSNU-STAIS Pasuruan pada tanggal 25 Januari 2022.&nbsp;Perwakilan dari Pecinta alam SMKN 1 Purwosari, sebagai salah satu peserta dalam kegiatan ekstrakurikuler&nbsp;mendapatkan juara 2 lomba prusiking (memanjat dengan menggunakan tali prusik) tingkat kabupaten.</p>\n\n<p>&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 10, 269, NULL, 1, NULL, '2024-03-01 03:57:30', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (442, 'SMKN 1 Purwosari Rebut 6 Juara di Bidang Lomba LKS Wilker 2 Tahun 2022', '', '1643410852.jpeg', '', NULL, NULL, '<p>Lomba Kompetensi Siswa (LKS)&nbsp;diadakan setiap tahunnya. Kegiatan ini yaitu bertujuan untuk&nbsp;menyeleksi siswa-siswi terbaik dari seluruh Wilayah Kerja 2&nbsp;yang akan dibimbing lebih lanjut oleh tim bidang kompetisi masing-masing dan akan diikutsertakan pada kompetisi keahlian tingkat Provinsi.&nbsp;</p>\n\n<p>Pelajar SMKN 1 Purwosari&nbsp;sukses mengukir prestasi membanggakan pada ajang Lomba Kompetensi Siswa (LKS) SMK tingkat Wilker 2 Tahun&nbsp;2022. Tujuh siswa terbaik berhasil meraih juara. Yakni, juara 1 bidang lomba Landscape and Gardening, Juara 2&nbsp;bidang lomba Welding, Juara 3&nbsp;bidang lomba Industrial Control, Juara 2&nbsp;bidang lomba CNC Turning, Juara 3&nbsp;bidang lomba CNC Milling, dan Juara 4&nbsp; bidang lomba IT Software Solutions for Business.</p>\n\n<p>&lsquo;&rsquo;Prestasi yang telah diraih&nbsp;ini merupakan hasil kerja keras bersama. Ini sekaligus membuktikan bahwa SMKN 1 Purwosari mampu bersaing di tingkat Wilayah Kerja 2&quot;</p>\n\n<p>&nbsp;</p>\n', 0, 'Wilayah Kerja 2', 3, 10, 287, NULL, 1, NULL, '2024-03-04 04:17:42', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (443, 'FIFGROUP HIJAUKAN BUMI', '', '1645620192.jpeg', '', NULL, NULL, '<p>Dalam rangka memperingati ulang tahun FIF Group ke-33, SMKN 1 Purwosari terpilih menjadi salah satu sekolah pertanian yang dipercaya untuk berperan dalam Program Penghijauan. &nbsp;Program ini sepenuhnya didanai oleh FIF Group untuk memperbanyak tanaman baik perintis, pelindung, penghijauan dan produktif. &nbsp;Sebanyak1.000 tanaman yang akan dibibitkan di SMKN 1 Purwosari yang bertujuan sebagai tanaman penghijauan produktif diantaranya Jeruk dan Trembesi. &nbsp;Total tanaman yang akan ditanam seluruh Indonesia sebanyak 33.000 tanaman dengan jenis tanaman yang beragam</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 147, NULL, 1, NULL, '2024-03-01 04:03:35', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (444, 'Peringatan IsroÃ¢â‚¬â„¢ MiÃ¢â‚¬â„¢roj Nabi Besar Muhammad SAW 1443H Dan Istighosah Akbar', '', '1647236099.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">PERINGATAN ISRO&rsquo; MI&rsquo;ROJ NABI BESAR MUHAMMAD SAW 1443H</span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">DAN ISTIGHOSAH AKBAR</span></span></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pada hari Jumat tanggal 11 Maret 2022, SMKN 1 Purwosari mengadakan peringatan isro mi&rsquo;roj nabi Muhammad SAW 1443H dan sekaligus istighosah rutin. Kegiatan tersebut di ikuti oleh siswa kelas XII, sehingga pada kegiatan kali ini istighosahnya mengambil tema &ldquo;ISTIGHOSAH AKBAR DALAM MENGANTAR KESUKSESAN KELAS XII DALAM MENGHADAPI UJIAN AKHIR SEKOLAH&rdquo;.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bertindak sebagai Imam istighosah adalah KH. Sya&rsquo;roni selaku ketua komite SMKN 1 Purwosari dan sebagai pembicara dalam peringatan ini yang sekaligus sebagai motivator bagi kelas XII dalam menghadapi ujian sekolah adalah Drs. H. Nur Kholis, M.Pd (guru di SMKN 1 Purwosari). </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pada materi ceramahnya pembicara menyampaikan sekilas tentang perjalann isro&rsquo; mi&rsquo;raj Nabi Besar Muhammad SAW, 1443H. yang sebenarnya &nbsp;mengandung banyak pelajaran dari siapa-siapa saja para nabi dan rosul yang ditemui oleh Nabi Muhammad SAW. Dan keutamaan yang luar biasa adalah perintah sholat 5 waktu, dimana saat ini dihimbau kepada para siswa untuk senantiasa tetap bisa istiqomah menjalankan sholat 5 waktu secara waktu dan jangan sampai meninggalkannya. </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kemudian juga bagi siswa kelas XII yang akan menyongsong ujian akhir diharapkan tetap semangat belajar dan senantiasa berdoa untuk kesuksesan serta kelancarannya dalam menyelesaikan ujian akhir sekolah, baik UKK (Uji Kompetensi Keahlian) maupun USPBK (Ujian Satuan Pendidikan Berbasis Komputer). Para siswa dihimbau juga untuk tetap memanfaatkan waktu sebaik-baiknya menjelang ujian sekolah dengan tetap belajar dan melaksanakan tugas-tugas yang diberikan guru melalui materi ataupun latihan soal yang diberikan</span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 147, NULL, 1, NULL, '2024-03-04 10:24:36', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (445, 'Eksebisi Pamer Karya Branding dan Packaging Design serta TVC UKK Multimedia', '', '1647986991.jpeg', '', NULL, NULL, '<p>Hari Senin 21 Maret 22 giliran Semangat Hebat bersama Bapak/Ibu Guru dan Siswa/wi SMK Negeri 1 Purwosari dalam kegiatan UKK (Ujian Kompetensi Keahlian) Multimedia bersama Maspion IT + AMIGROUP. Konsep Eksebisi Pamer Karya Branding + Packaging Design serta TVC (iklan komersial) pantas ditiru keren.<br />\nSukses buat Anda Semua, Tetap dan Teruslah Membawa Manfaat bagi Semua.<br />\nSMK tidak hanya Bisa tapi Hebat Luar Biasa seperti SMK Negeri 1 Purwosari ini.</p>\n\n<p>Terima Kasih<br />\nIr. Okky Tri Hutomo, M.IT<br />\nMaspion IT<br />\nIcon IT Indonesia Ã°Å¸â€¡Â®Ã°Å¸â€¡Â©</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 238, NULL, 1, NULL, '2024-03-01 03:57:26', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (446, 'Penyuluhan Hukum Kejaksaan Negeri Kabupaten Pasuruan', '', '1648767127.jpeg', '', NULL, NULL, '<p>Menyambut datangnya bulan suci Ramadhan 2022. Terkait hal itu Kejaksaan Negeri Kabupaten Pasuruan menggelar program Penyuluhan Hukum Kejaksaan Negeri Kabupaten Pasuruan. Penyuluhan ini&nbsp;menjelaskan&nbsp;tentang&nbsp;program kegiatan Kejaksaan Negeri Kabupaten Pasuruan dalam rangka mengajak generasi muda khususnya di kalangan pelajar&nbsp;terhindar dari permasalahan tindak pidana yang sewaktu-waktu bisa terjadi.</p>\n\n<p>Berbicara di hadapan perwakilan siswa&nbsp;12&nbsp;mendapat perhatian yang luar biasa dari peserta yang ingin tahu lebih banyak tentang hukum terutama menyangkut UU ITE. UU ITE menjadi pengawas dalam bermedia sosial, sebuah kegiatan yang hampir seluruh siswa melakukan. WhatsApp, IG, FB, Twitter, dan lain sebagainya yang akrab dengan mereka sehingga&nbsp; memungkinkan potensi singgungan dengan seseorang saat menggunakan medsos. Oleh karena itu&nbsp;seseorang harus bijak dalam menggunakan media sosial, bukan saja agar tak terjerat ITE, tetapi&nbsp;juga agar tidak menjadi korban media sosial.</p>\n\n<p>Selain itu, dalam penyuluhan ini juga memperkenalkan bagian-bagian dan tugas maupun fungsi dari pejabat kejaksaan. Beberapa program yang digagas kejaksaan dan berjalan, termasuk mengajak lulusan&nbsp;agar bisa menjadi jaksa, di kemudian hari. &quot;Harapannya nanti ada adik-adik lulusan&nbsp;bisa ikut tes di kejaksaan negeri,&quot; harapnya saat memberikan sosialisasi.</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 244, NULL, 1, NULL, '2024-03-01 04:06:35', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (447, 'Sosialisasi Beasiswa China dan Taiwan', '', '1648768032.jpeg', '', NULL, NULL, '<p>Siswa SMKN 1 Purwosari mendapatkan kesempatan dalam sosialisasi beasiswa studi lanjut di China dan Taiwan<em>,</em>&nbsp;acara ini dihadiri oleh Koko Sigit Harianto, selaku pemateri dalam sosialisasi beasiswa&nbsp;di China dan Taiwan. Acara yang&nbsp;dilaksanakan pada tanggal 31 Maret 2022 di Ruang sidang 2 SMKN 1 Purwosari</p>\n\n<p>Koko Sigit memaparkan terkait pendidikan di Taiwan, jenis beasiswa, hidup di Taiwan, fakta-fakta tentang Taiwan dan strategi selama studi di China dan Taiwan.</p>\n\n<p>China dan Taiwan memiliki 48 universitas negeri dan 105 universitas swasta dengan 15 kampus terbaik, diantaranya National Taiwan University,&nbsp;National Yang Ming University, National Cheng Kung University, National Chiao Tung University, National Tsing Hua University Taiwan, National Central University, National Sun Yat Sen University, National Taiwan Normal University, National Taiwan University of Science &amp; Technology, Tamkang University, Taipei Medical University, Chang Gung University, National Chung Cheng University, National Chengchi University dan China Medical University Taiwan.</p>\n\n<p>Ada sejumlah beasiswa yang bisa dikejar oleh calon mahasiswa yang berminat kuliah di Taiwan, ITC-CENTER PROGRAM memaparkan beberapa beasiswa yang bisa dikejar&nbsp;yaitu diantaranya Beasiswa China Program, Beasiswa Taiwan Program, Beasiswa MBBS Program, Triple One Program dan Two + Two Program</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 200, NULL, 1, NULL, '2024-03-01 03:57:50', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (448, 'Bimbingan Karir Bagi Siswa SMKN 1 Purwosari Oleh PT Tirta Fresindo Jaya (Mayora Group)', '', '1652431151.jpeg', '', NULL, NULL, '<p>Bimbingan Karir Bagi Siswa SMKN 1 Purwosari Oleh PT Tirta Fresindo Jaya (Mayora Group)</p>\n\n<p>Hari ini, Jum&#39;at 13 Mei 2022 dilaksanakan Bimbingan Karir bagi Siswa SMK Negeri 1 Purwosari oleh PT. Tirta Fresindo Jaya Group<br />\n.<br />\nBimbingan karir adalah layanan bantuan untuk memperoleh penyesuaian diri, pemahaman tentang dunia kerja dan pada akhirnya mampu menentukan pilihan kerja dan menyusun perencanaan karir.<br />\nPeserta didik dapat mempersiapkan masuk dunia kerja, persiapan administrasi<br />\nMengenal budaya kerja agar menjadi pribadi yang tangguh, mempunyai kemampuan bekerjasama, bertanggung jawab<br />\n.<br />\nMENJEMPUT SUKSES BERSAMA Ã°Å¸â€Â¥Ã°Å¸â€Â¥Ã°Å¸â€Â¥<br />\n&nbsp;</p>\n\n<p><iframe height=\"360\" src=\"https://drive.google.com/file/d/100dHJGgP6z2I82VCdAkHdpU_D7gyNplt/preview\" width=\"480\">\n</iframe><iframe height=\"360\" src=\"https://drive.google.com/file/d/1SgzVG1s3fjr8A8kT0E6RKe9-zzn7G_57/preview\" width=\"480\">\n</iframe><iframe height=\"300\" src=\"https://drive.google.com/file/d/1E8cs17JOfcvCTkt1DwaL2_NmRK02BKH8/preview\" width=\"480\">\n</iframe><iframe height=\"360\" src=\"https://drive.google.com/file/d/1trea926byGSG-pWVctVHBCEwxBj8pDem/preview\" width=\"480\">\n</iframe></p>\n\n<p>&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 287, NULL, 1, NULL, '2024-03-01 03:55:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (449, 'PERTANDINGAN KARATE PROPDISDIK', '', '1653780140.jpeg', '', NULL, NULL, '<p>PERTANDINGAN KARATE PROPDISDIK( Piala Kapolres Dinas Pendidikan &amp; Kebudayaan Koni Jombang ) SMA SMK se-Jatim&nbsp;</p>\n\n<p>Tanggal Pertandingan 27, 28 &amp; 29 MEI 2022</p>\n\n<p>- M. Rendra Sefrizal ( X AT 1 ) &nbsp;juara 2 KATA perorangan putra<br />\n- Tsaniyah Mukamillah ( X PPLG) &nbsp;juara &nbsp;1 KATA perorangan &nbsp;dan beregu putri&nbsp;<br />\n- &nbsp;Vanesya Ayu Wilujeng ( X TE ) &nbsp;juara 1 KATA beregu putri<br />\n- Zuliza Khulut ( XI ATPH 2 )<br />\n- M. Mahbub Shiddiqi ( XII MKT 2 ) official</p>\n', 0, 'SMKN 1 Purwosari', 3, 10, 265, NULL, 1, NULL, '2024-03-01 03:55:06', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (450, 'Kelulusan Peserta Didik dari Satuan Pendidikan Tahun Pelajaran 2021-2022', '', '1654246604.png', '', NULL, NULL, '<p>Kelulusan Peserta Didik dari Satuan Pendidikan Tahun Pelajaran 2021-2022</p>\n\n<p><iframe height=\"480\" src=\"https://drive.google.com/file/d/1lyHNgKFxx7XT_KtWuEtVmqvms3PKWvdE/preview\" width=\"640\"></iframe></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 736, NULL, 1, NULL, '2024-03-03 00:47:31', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (451, 'Upacara Bendera HUT ke-77 Republik Indonesia ', '', '1660737159.jpg', '', NULL, NULL, '<p>Pelaksanaan Upacara Bendera Tk. Kec. Purwosari dalam Rangka HUT ke-77 Republik Indonesia di SMK Negeri 1 Purwosari, Pasuruan</p>\n\n<p>Upacara setiap tanggal 17 Agustus dilakukan untuk memperingati Hari Kemerdekaan Indonesia. Bertindak&nbsp;sebagai Penjabat&nbsp;Upacara</p>\n\n<p>1.&nbsp;&nbsp; &nbsp;Inspektur Upacara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;SUGENG HARIYADI, SE. M.M ( Camat Purwosari)&nbsp;<br />\n2.&nbsp;&nbsp; &nbsp;Cadangan Irup&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;KAPTEN ARH INDAR SUMARSONO ( Dan Ramil Pwsr ) &nbsp;<br />\n3.&nbsp;&nbsp; &nbsp;Komandan Upacara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;PELTU BUKHORI&nbsp;<br />\n4.&nbsp;&nbsp; &nbsp;Cadangan dan Up&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;PELDA PUJI&nbsp;<br />\n5.&nbsp;&nbsp; &nbsp;Perwira Upacara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;SERMA SUMANTRI&nbsp;<br />\n6.&nbsp;&nbsp; &nbsp;Cad Perwira Upacara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;SERKA ARI&nbsp;<br />\n7.&nbsp;&nbsp; &nbsp;Pembawa Upacara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;BUYUT ZAINI / Petugas SMKN 1 Purwosari&nbsp;<br />\n8.&nbsp;&nbsp; &nbsp;Cad.Pembawa Acara&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;NEVI FADILAH / Petugas SMKN 1 Purwosari&nbsp;<br />\n9.&nbsp;&nbsp; &nbsp;Pembaca Teks Proklamasi&nbsp; : Kepala Sekolah SMKN 1 Purwosari&nbsp;<br />\n10.&nbsp;&nbsp; &nbsp;Perwira Keamanan&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : &nbsp;SERKA JUWANTO<br />\n&nbsp;</p>\n\n<p>Kesatuan Upacara &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; :&nbsp;<br />\nPasukan Upacara &nbsp;&nbsp;&nbsp; &nbsp; &nbsp; :</p>\n\n<p>a. Kompi Upacara I&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: REGU TNI dan POLRI ( @10 ORANG)&nbsp;<br />\nb.&nbsp;&nbsp; &nbsp;Kompi Upacara II&nbsp; &nbsp; &nbsp;: REGU KORPRI Staf Kecamatan ( 10 ORANG)&nbsp;<br />\nc.&nbsp;&nbsp; &nbsp;Kompi Upacara III&nbsp; &nbsp; : REGU KORPRI Sekdes dan Sekkel ( 15 ORANG)&nbsp;<br />\nd.&nbsp;&nbsp; &nbsp;Kompi Upacara IV&nbsp; &nbsp; : REGU BANSER + PEMUDA PANCASILA ( @10 ORANG)&nbsp;<br />\ne.&nbsp;&nbsp; &nbsp;1 SSR Siswa / Siswi SMKN 1 Purwosari &nbsp;<br />\n&nbsp;<br />\nIV.&nbsp;&nbsp; &nbsp;Personil Upacara Lainnya :&nbsp;<br />\n1.&nbsp;&nbsp; &nbsp;Pengibar Bendera Merah Putih&nbsp; &nbsp;: 3 orang SMKN 1 Purwosari&nbsp;<br />\n2.&nbsp;&nbsp; &nbsp;Pembaca Teks Proklamasi&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : Kepala Sekolah SMKN 1 Purwosari&nbsp;<br />\n3.&nbsp;&nbsp; &nbsp;Pembaca Doa&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: Kepala K.U.A Kecamatan Purwosari&nbsp;<br />\n4.&nbsp;&nbsp; &nbsp;Tenaga Kesehatan&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; : Puskesmas Purwosari</p>\n\n<p>Adapun Susunan Acara Sebagai Berikut :</p>\n\n<p>1.&nbsp;&nbsp; &nbsp;Acara Persiapan :&nbsp;<br />\na.&nbsp;&nbsp; &nbsp;Persiapan Pasukan&nbsp;<br />\nb.&nbsp;&nbsp; &nbsp;Komandan Upacara memasuki lapangan upacara&nbsp;<br />\nc.&nbsp;&nbsp; &nbsp;Komandan Upacara mengambil alih Komando&nbsp;<br />\nd.&nbsp;&nbsp; &nbsp;Latihan seperlunya.&nbsp;</p>\n\n<p>2.&nbsp;&nbsp; &nbsp;Acara Pendahuluan :&nbsp;<br />\na.&nbsp;&nbsp; &nbsp;Laporan Perwira Upacara kepada Inspektur Upacara&nbsp;<br />\nb.&nbsp;&nbsp; &nbsp;Inspektur Upacara tiba di lapangan upacara&nbsp;</p>\n\n<p>3. Acara Pokok :&nbsp;<br />\na.&nbsp;&nbsp; &nbsp;Penghormatan pasukan kepada Inspektur Upacara&nbsp;<br />\nb.&nbsp;&nbsp; &nbsp;Laporan Komandan Upacara kepada Inspektur Upacara&nbsp;<br />\nc.&nbsp;&nbsp; &nbsp;Pembacaan Teks Proklamasi&nbsp;<br />\nd.&nbsp;&nbsp; &nbsp;Mengheningkan cipta dipimpin oleh Inspektur Upacara&nbsp;<br />\ne.&nbsp;&nbsp; &nbsp;Pembacaan Doa&nbsp;<br />\nf.&nbsp;&nbsp; &nbsp;Persiapan Pengibaran Bendera Merah Putih &nbsp;<br />\ng.&nbsp;&nbsp; &nbsp;Tamu Undangan dimohon berdiri&nbsp;<br />\nh.&nbsp;&nbsp; &nbsp;Penghormatan kepada Bendera Merah Putih dipimpin oleh Komandan Upacara&nbsp;<br />\ni.&nbsp;&nbsp; &nbsp;Tamu Undangan disilahkan duduk kembali&nbsp;<br />\nj.&nbsp;&nbsp; &nbsp;Andhika Bhayangkari / Paduan Suara SMKN 1 Purwosari&nbsp;<br />\nk.&nbsp;&nbsp; &nbsp;Laporan Komandan Upacara kepada Inspektur Upacara&nbsp;<br />\nl.&nbsp;&nbsp; &nbsp;Penghormatan Pasukan kepada Inspektur Upacara&nbsp;</p>\n\n<p>4. Acara Penutup&nbsp;<br />\na.&nbsp;&nbsp; &nbsp;Inspektur upacara berkenan meninggalkan tempat&nbsp;<br />\nb.&nbsp;&nbsp; &nbsp;Perwira Upacara laporan kepada Inspektur Upacara&nbsp;<br />\nc.&nbsp;&nbsp; &nbsp;Pasukan dapat diistirahatkan&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 167, NULL, 1, NULL, '2024-03-02 03:59:03', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (452, 'Pemilihan Ketua dan Wakil Ketua OSIS Periode 2022-2023', '', '1662849343.PNG', '', NULL, NULL, '<p>Pemilihan Ketua dan Wakil Ketua OSIS Periode 2022-2023 dilaksanan pada hari Jum&#39;at, 9&nbsp;September 2022. Pada kesempatan ini pemilihan ketua OSIS SMKN 1 Purwosari&nbsp;dimulai dari pemilihan calon ketua OSIS yang dipilih dari peserta didik kelas X,XI,XII dan XIII, yang mempunyai kecakapan dan jiwa kepemimpinan. Para calon ketua OSIS menyampaikan visi dan misi untuk menjelaskan program kerja yang akan dilakukan ketika terpilih menjadi ketua OSIS nantinya. Berikut adalah&nbsp;calon ketua OSIS SMKN 1 Purwosari :</p>\n\n<p><strong>Pasangan Urut Nomer 1</strong></p>\n\n<p>Charlie Liemanjaya Nugroho - XI MKT<br />\nFajrina Aisyah Putri - XI APHP 2</p>\n\n<p><strong>Pasangan Urut Nomer 2</strong></p>\n\n<p>M. Aries Satriansyah - XI TPBO<br />\nTasya Dhafina Ramadhani - XI ATPH 2</p>\n\n<p>Diharapkan seluruh Siswa dan Guru SMKN 1 Purwosari menggunakan hak pilihnya dengan baik, untuk meminimalisir tindakan&nbsp;kecurangan. Seluruh Siswa dan Guru dapat melakukan pemilihan dengan mengakses link yang sudah diberikan dan perhitungan suara dilaksanakan secara realtime Quick Count sehingga hasil dapat langsung diketahui. Pembina OSIS dan panitia pelaksanaan pemilihan ketua OSIS memastikan proses demokrasi di sekolah melalui pemilu ketua OSIS dilakukan secara Luber Jurdil (Langsung, Bersih, Rahasia, Jujur dan Adil).<br />\nKegiatan pemilihan Ketua OSIS diharapkan bisa membekali siswa berupa karakter dan kecakapan untuk menjadi warga negara yang baik. Siswa menjadi tahu bagaimana prosedur pemilihan umum yang benar. Pemilihan ketua OSIS dilaksanakan dengan sebaik-baiknya, sportif dan bertanggung jawab serta tidak menimbulkan konflik setelah pelaksanaan pemilihan ketua OSIS.</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 192, NULL, 1, NULL, '2024-03-01 03:53:43', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (453, 'Maulid Nabi Muhammad SAW 2022 ', '', '1665322092.JPG', '', NULL, NULL, '<p>Hari ini, Jumat (7/10/2022), SMKN 1 Purwosari&nbsp;memperingati Maulid Nabi atau hari kelahiran Nabi Muhammad Shalallaahu Alaihi Wassalaam (SAW). Nabi Muhammad SAW lahir pada bulan Rabiul Awal yang mana sekaligus menjadi penanda waktu ia melakukan hijrah dan wafat. Nabi Muhammad SAW lahir pada Senin, 12 Rabiul Awal pada tahun Gajah atau tepatnya pada 571 Masehi.</p>\n\n<p>Selesai Sholat Dhuha,&nbsp;Ghoib dan membaca Manakib Nabi Muhammad, Siswa mengambil&nbsp;makanan yang telah dikoordinir oleh OSIS dan dibagikan bersama-sama. Siswa SMKN 1 Purwosari&nbsp;tidak hanya bergembira merayakan kelahiran Nabi, tetapi juga bersyukur atas teladan, jalan hidup, dan tuntunan yang dibawa oleh Nabi.</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 143, NULL, 1, NULL, '2024-03-01 03:53:56', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (454, 'Peringatan Hari Sumpah Pemuda Tahun 2022', '', '1667445625.JPG', '', NULL, NULL, '<h4>UPACARA PERINGATAN HARI SUMPAH PEMUDA 28 OKTOBER 2022 DI SMKN 1 PURWOSARI</h4>\n\n<h4>Upacara hari Jumat tanggal 28 oktober 2022&nbsp;di SMKN 1 Purwosari&nbsp;yang diikuti oleh seluruh guru, staf dan&nbsp;kepala sekolah.&nbsp;Sumpah pemuda pada dasarnya mengandung isi Bersatu bangun bangsa. Secara menyeluruh membangun bangsa untuk masa kini hingga masa depan,&nbsp;karena pembangunan suatu bangsa adalah suatu tekad yang penting dalam suatu bangsa dan negara. Bangsa Indonesia adalah bangsa yang ber-Bhineka tunggal Ika. Berbeda-beda, tetapi tetap bersatu.</h4>\n\n<p>Di samping itu, kegunaan dari sumpah pemuda bagi warga SMKN 1 Purwosari&nbsp;khususnya dan umumnya masyarakat indonesia adalah menumbuhkan rasa sekaligus sikap, kebiasaan kerja keras serta rajin bekerja, semangat dan sikap gotong royong dalam membangun suatu kebersamaan sebagai bangsa seperti istilah pepatah berat sama dipikul ringan sama dijinjing. Kita semua sadar bahwa punya karakter persatuan yang diturunkan oleh nenek moyang sebagai karakter yang percaya diri untuk bangsa indonesia yang sesungguhnya, yang tidak akan dipengaruhi tentang ideologi, politik, budaya serta ekonomi asing yang tidak kita harapkan.</p>\n\n<p>Diharapkan kita semua bisa saling bersatu padu membangun komunikasi dan kolaborasi demi kemajuan bangsa dan teruntuk SMKN 1 Purwosari</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 194, NULL, 1, NULL, '2024-03-04 04:48:59', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (455, 'Memperingati Hari Pahlawan 10 November 2022', '', '1668207671.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">PAHLAWANKU, TELADANKU</span></span></span></span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Setiap 10 November bangsa Indonesia memperingati hari Pahlawan. Peringatan hari Pahlawan ini tidak lepas dari pertempuran</span></span></span></span><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:#2a2a2a\"> besar yang terjadi di Surabaya antara tentara Indonesia dan pasukan Inggris pada tanggal 10 November 1945.</span></span></span></span> <span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Peringatan hari Pahlawan ini sebagai bentuk penghargaan atas jasa dan pengorbanan terhadap pahlawan yang telah gugur di medan perang. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Tahun ini, peringatan hari Pahlawan bertemakan <strong>&ldquo;Pahlawanku, Teladanku&rdquo;</strong>. Tema ini bertujuan <span style=\"background-color:white\"><span style=\"color:black\">untuk meneladani dan mengimplementasikan nilai-nilai luhur pahlawan dalam kehidupan sehari-hari. &ldquo;Bentuk meneladani perjuangan para pahlawan yang telah mendahuli kita bagi pelajar adalah cinta tanah air dengan rajin belajar dan pantang menyerah&rdquo; kata Bapak Aditya Novan, S.Pd. selaku pembina upacara di peringatan hari sumpah pemuda 10 November 2022 di SMKN 1 Purwosari.</span></span></span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Peringatan hari pahlawan di SMKN 1 Purwosari dilaksanakan di lapangan <em>indoor</em> diikuti kepala sekolah, para guru dan para karyawan. Selain pembinaan yang disampaikan Bapak Novianto selaku pembina upacara, juga ada pembacaan pesan para pahlawan bagi para generasi bangsa yang dibacakan M. Lefi Rachmad dari kelas XRPL, Utsa Abi H. dari kelas XMKT2 dan Muhammad Azril dari kelas XATPH1. Untuk pembacaan puisi Karawang Bekasi dibacakan Echa dari kelas XIIMKT. Upacara semakin khidmat diakhiri dengan doa.</span></span></span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Selain upacara bendera, peringatan hari Pahlawan di SMKN 1 Purwosari juga diadakan beberapa lomba. Lomba-lomba tersebut meliputi lomba pidato dan lomba <em>creative fotografi</em>. Lomba-lomba tersebut dimulai 10 November 2022 sampai 16 November 2022. Pemenang lomba akan diumumkan pada upacara hari guru 25 November 2022.</span></span></span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Semoga dengan peringatan hari Pahlawan ini kita tidak melupakan jasa-jasa para pahlawan yang telah berjuang melawan penjajah. Kita hargai jerih payah mereka dengan meneruskan perjuangannya. Pahlawankku, teladanku. &nbsp;&nbsp;</span></span></span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 106, NULL, 1, NULL, '2024-03-03 02:37:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (456, 'Tes PT. Astra Daihatsu Motor', '', '1668479193.jpeg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\">Pada hari Senin, 14 November 2022. Sebanyak 39 siswa alumni mengikuti tes fisik yang diadakan oleh PT. Astra Daihatsu, dengan merekomendasikan untuk menjadi penguji pada tes fisik kali ini adalah pihak dari SMKN 1 Purwosari yang dilaksanakan di Lapangan SMKN 1 Purwosari. Tes ini merupakan rangkaian seleksi yang nantinya akan merekrut siswa-siswa lulusan SMK yang berminat bergabung. Setelah tes fisik, peserta tes akan mengikuti tes psikologi sebagai rangkaian tes berikutnya. </span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">PT. Astra Daihatsu Motor ini salah satu tujuan utama para lulusan SMK dalam jurusan otomotif. Selain otomotif, beberapa jurusan juga dapat melamar di PT. ADM, tidak hanya untuk jurusan otomotif saja.</span></span></span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">Selain Ijazah dan Nilai yang menjadi syarat utamanya, kita juga wajib lolos dalam semua tes saat proses rekrutmen PT. ADM berlangsung, supaya bisa diterima di perusahaan tersebut. Karena Astra Daihatsu Motor merupakan salah satu perusahaan yang Bonafit, maka para&nbsp;jobseeker&nbsp;yang melamar pun juga banyak.</span></span></span></span></span></span></p>\n\n<p><br />\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Tes Fisik PT. ADM (Astra Daihatsu Motor)</span></span></span></strong></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Tes Fisik Lari</span></span></span></strong></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">Yang pertama yaitu tes lari. Lari memang salah satu tes yang paling mudah untuk mengetahui daya tahan seseorang. Untuk jaraknya bermacam macam. Siswa melakukan lari dengan jarak kurang lebih 400 meter dengan batas waktu kurang lebih 4 menit.</span></span></span></span></span></span><br />\n<br />\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Tes Fisik Push Up</span></span></span></strong></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">Berikutnya yaitu&nbsp;</span></span></span><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Push Up</span></span></span></em><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">. Intinya, semakin banyak akan semakin bagus. Namun terkadang siswa akan diberi waktu, seperti dalam 1 menit wajib menyelesaikan 30-40 push up, atau juga bisa lebih</span></span></span></span></span></span><br />\n<br />\n<span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Tes Fisik Shit Up</span></span></span></strong></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">Untuk tes&nbsp;</span></span></span><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Shit Up</span></span></span></em><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">&nbsp;ini tergolong lebih mudah dari pada lari dan push up. Seperti push up, siswa akan diberi waktu 1 menit untuk menyelesaikan 30-40 shit up. </span></span></span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;inherit&quot;,serif\"><span style=\"color:black\">Tes Fisik Pull Up</span></span></span></strong></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:Roboto\"><span style=\"color:black\">Untuk tes&nbsp;Pull up ini yang paling susah, jadi siswa diusahakan bisa dulu melakukan pull up, waktu yang diberikan dalam Pull Up yaitu 1 Menit.</span></span></span></span></span></span><br />\n&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 232, NULL, 1, NULL, '2024-03-04 09:43:40', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (457, 'FIF GROUP HIJAUKAN BUMI', '', '1669498887.JPG', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:12pt\"><strong><span style=\"color:black\">FIF GROUP HIJAUKAN BUMI</span></strong></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><strong><span style=\"color:black\">Tanam 33.000 Pohon Sepanjang 2022 Se- Indonesia</span></strong></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:12pt\"><strong><span style=\"color:black\">FIF Group Pasuruan 2</span></strong></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"background-color:white\"><span style=\"color:black\">&quot;Dia menanam pohon, maka dia jugalah yang menanam harapan.&rdquo; Sebait kata-kata mutiara dari seorang guru dan penulis buku asal Amerika Lucy Larcom sungguh indah dan juga menjadi pengingat bagi kita untuk menjaga kelestarian lingkungan. Pohon adalah unsur yang sangat penting dalam kehidupan manusia karena mampu menyediakan apa yang dibutuhkan seperti tempat tinggal, perabotan, mencegah banjir, dan yang paling penting yaitu memproduksi oksigen serta membersihkan udara.&nbsp;</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Rata-rata satu pohon dengan crown diameter atau diameter tajuk (lingkar terluar daun) 3-10 meter mampu menghasilkan 1,2 kilogram (kg) oksigen per hari. Sementara setiap orang perlu 0,5 kg oksigen per hari. Jadi, bisa dikatakan satu pohon mampu menunjang kehidupan dua orang atau lebih. Itulah pentingnya mengapa kegiatan tanam pohon patut untuk dijadikan kebiasaan tanpa akhir.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Seperti yang akan dilakukan oleh PT Federal International Finance (FIFGROUP) salah satu anak perusahaan PT Astra International Tbk yang juga bagian dari Astra Financial, mengadakan kegiatan Hijaukan Bumi &ldquo;Tanam 33.000 Pohon Sepanjang 2022 se-Indonesia&rdquo; sebagai rangkaian peringatan Hari Ulang Tahun FIFGROUP ke-33 secara serentak dengan 10 titik di wilayah Indonesia.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dalam rangka memperingati ulang tahun FIF Group ke-33, SMKN 1 Purwosari terpilih menjadi salah satu sekolah pertanian yang dipercaya untuk berperan dalam Program Penghijauan. &nbsp;Program ini sepenuhnya didanai oleh FIF Group untuk memperbanyak tanaman baik perintis, pelindung, penghijauan dan produktif.&nbsp; Tanaman yang ditanam di lahan SMKN 1 Purwosari adalah Jeruk</span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 89, NULL, 1, NULL, '2024-03-01 15:21:16', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (458, 'Hari Guru Nasional 2022 dan HUT PGRI ke-77', '', '1669502821.JPG', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hari Guru Nasional 2022 jatuh pada hari ini, Jumat (25/11/2022). Hari Guru Nasional diperingati pada 25 November setiap tahunnya. Tema Hari Guru Nasional 2022 kali ini yakni &quot;Serentak Berinovasi, Wujudkan Merdeka Belajar&quot;. Penetapan Hari Guru Nasional berkaitan dengan riwayat berdirinya Persatuan Guru Republik Indonesia (PGRI).</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Persiapan upacara ini yakni diikuti oleh seluruh guru yang bertugas sebagai pelaksana petugas upacara. Jelang 25 November 2022, Sekolah tengah mempersiapkan moment spesial Hari Guru Nasional yang diawali dengan upacara bendera di pagi hari dan dilanjutkan acara Tasyakuran. Pelaksanaannya berbeda dengan upacara pengibaran bendera hari Senin, karena terdapat beberapa rangkaian acara yang harus dikerjakan pada saat upacara berlangsung, seperti amanat dan pidato.&nbsp;</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; Tujuannya untuk memeriahkan dan mengingat jasa-jasa para guru. Karena Berkat jasa para guru juga, kita bisa mengenal jendela dunia melalui ilmu yang mereka ajarkan. Tidak heran, jika seorang guru sangat dimuliakan oleh para muridnya. Tidak hanya ilmu pengetahuan, terkadang guru juga dengan penuh rasa ikhlas memberikan kita pelajaran seputar kehidupan melalui petuah bijaknya.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp; &quot;Harapan kedepannya semoga kegiatan ini bisa meriah, bisa terlaksana lebih baik lagi dari pada yang sekarang, dan juga bisa memberikan motivasi untuk siswa-siswi yang ada di sekolah ini bahwa jasa guru itu pahlawan tanpa tanda jasa yang dimana guru memberikan ilmu kepada muridnya benar-benar ikhlas.</span></span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 268, NULL, 1, NULL, '2024-03-03 01:16:34', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (459, 'Coaching Clinic Volly Ball Tournament 2022', '', '1670017229.jpeg', '', NULL, NULL, '<p>Juara Livoli Divisi Utama Indomaret Sidoarjo bagi-bagi ilmu ke siswa-siswi di Kabupaten Pasuruan. Tim ini mengunjungi sejumlah sekolah, mengadakan coaching clinic. Mengajak pemain, pelatih, dan manajemen untuk memperkenalkan bola voli ke sekolah di Pasuruan. Sekaligus menyosialisasikan event Indomaret Poinku Volley Ball Tournament 2022. Yang berlangsung pada 17-24 Desember 2022 mendatang.</p>\n\n<p>Coaching clinic di SMKN 1 Purwosari dilaksanakan hari Kamis tgl 1 November 2022. Salah satu pemain tim putra Indomaret, Paskasius Roby, hadir ke SMKN 1 Purwosari. Roby tidak sendirian. Ia datang bersama manajer sekaligus mantan pemain Indomaret, Sutono. Kedatangan mereka disambut hangat oleh siswa dan jajaran guru. Mereka tampak antusias mengikuti coaching clinic yang diberikan Roby dan Sutono.<br />\nKedatangan Roby dan Sutono disambut meriah. Roby berhasil mencuri perhatian para siswa. Dengan tinggi 205 cm, pemain yang berposisi sebagai opposite hitter ini memang sangat mencolok. Para siswa SMKN 1 Purwosari juga antusias menerima materi yang disampaikan Roby dan Sutono.<br />\nBapak Ely Wahyudi selaku pembimbing kegiatan ekstra kurikuler bola voli di SMKN 1 Purwosari, dalam sambutannya menyampaikan terima kasih atas terlaksananya kegiatan coaching clinic ini, dan berharap semoga semua ini bisa menjadi motivasi bagi siswa siswi untuk lebih berprestasi dalam bidang bola voli.</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 235, NULL, 1, NULL, '2024-03-04 11:04:47', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (460, 'Sosialisasi Bahaya Gadged Bagi Remaja', '', '1670018345.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sosialisasi Bahaya Penggunaan <em>Gadget</em> Secara Berlebihan di SMKN 1 Purwosari</span></span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kehadiran <em>gadget</em> terutama dalam bentuk <em>smartphone</em> telah banyak memberikan kontribusi dalam kehidupan sehari-hari, <em>gadget</em> sebagai media pencarian informasi, melakukan interaksi, mendapatkan hiburan, bahkan hingga untuk keperluan kegiatan berbisnis secara <em>online</em>.&nbsp; Sebuah teknologi yang pada hakikatnya diciptakan untuk membuat hidup manusia menjadi semakin mudah dan nyaman. Akan tetapi kemajuan teknologi yang semakin pesat saat ini membuat hampir tidak ada bidang kehidupan manusia yang bebas dari penggunaannya, baik secara langsung maupun tidak langsung. Seiring arus globalisasi dengan tuntutan kebutuhan pertukaran informasi yang cepat, peranan teknologi komunikasi menjadi sangat penting. Sehingga membuat para pengguna tidak terkendali dalam hal pemakaian suatu teknologi tersebut. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Guna memberikan wawasan kepada seluruh siswa SMKN 1 Purwosari melalui ekstrakurikuler PMR p</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ada Jumat, 2 Desember 2022 mengadakan sosialisasi bahaya penggunaan gadget secara berlebihan</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">.</span></span> <span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sosialisasi ini</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> disampaikan oleh Psikiater Khusus Remaja </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">d</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">r. Nur Aida dari Rumah Sakit Jiwa Lawang</span></span> <span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">di Lapangan </span></span><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">i</span></span></em><em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">ndoor</span></span></em><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> SMKN 1 Purwosari</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">. </span></span>&nbsp;<span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">S</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">eluruh siswa kelas X</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">, XI dan</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> XII</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> wajib mengikuti sosialisasi ini.</span></span> <span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dalam penjelasannya, dr. Nur Aida mengatakan bahwa penggunaan <em>gadget</em> maksimal 3 jam dalam sehari. Dan jika kita merasa kepikiran, sedih dan cemas ketika tidak memegang <em>gadget</em>, maka dikatakan kecanduan. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kegiatan sosialisasi ini </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">dilanjutkan dengan </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">sesi tanya jawab. &quot;Bagaimana pendapat </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">dokter</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> terhadap setiap permasalahan yang dipubli</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">kasikan</span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> melalui sosial media?&quot; tanya salah satu siswa kepada </span></span><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">dr. Nur Aida. &quot;Mengenai kebiasaan buruk yang selalu mempublikasikan hal-hal yang sensitif dan termasuk pribadi ke media sosial yang banyak dilakukan oleh anak remaja saat ini bisa jadi memiliki tujuan untuk menarik simpati terhadap pembaca&quot; jawab beliau. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&ldquo;Jika profesi seseorang yang selalu memakai <em>gadget</em> seperti <em>programer</em>, desain grafis dan sebagainya, apakah termasuk juga dikatakan kecanduan <em>gadget</em>?&rdquo; pertanyaan dari perwakilan jurusan Mekatronika. &ldquo;Hal itu tidak termasuk kecanduan tapi itu termasuk obsesi&rdquo; jawab dr. Nur Aida.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Semoga dengan diadakannya sosialisasi penggunaan <em>gadget</em> secara berlebihan ini, siswa-siswi di SMKN 1 Purwosari dapat mengurangi waktu penggunaan <em>gadget</em>. Waktu yang ada digunakan untuk belajar dan melakukan kegiatan yang positif seperti membaca buku, olahraga dan mengikuti secara aktif ekstrakurikuler di sekolah.&nbsp; </span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 129, NULL, 1, NULL, '2024-03-01 03:58:30', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (461, 'Owner Soe Meals Alumni SMK Negeri 1 Purwosari Dapat menjual 100Kg Perbulan', '', '1670319767.jpeg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Assalamualaikum.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Halo&hellip; Perkenalkan saya Divanda Rifka Ramadhan Fauzi, alumni SMKNUSA alias SMK Negeri 1 Purwosari jurusan ATPH atau Agribisnis Tanaman Pangan dan Hortikultura angkatan 2014. Saya adalah founder sekaligus owner dari Soe Meals.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Bagi yang belum tahu, Soe Meals adalah merk dari olahan berbagai inovasi kue kering yang berdiri pada bulan Oktober tahun 2020.&nbsp;</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kue-kue kering tersebut diantaranya adalah soes kering, red velvet, lidah kucing, cornflakes, dan chocolate cookies.&nbsp;</span></span></span><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dari berbagai inovasi kue kering tersebut&nbsp; fokus kami lebih kepada jenis soes kering dengan sistem PO setiap bulannya, sedangkan jenis kue kering yang lainnya hanya terdapat di high season saja (lebaran).</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Alhamdulilah terhitung 2 tahun berjalan, soes kering soe meals terjual rata-rata&nbsp;100kg perbulannya.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Jurusan saya sewaktu bersekolah di SMKNUSA memanglah pertanian, kalau dilihat memang sangat tidak linier dengan pekerjaan yang sedang saya tekuni saat ini. Namun perlu diingat teman-teman, apapun yang berpeluang untuk bisa menciptakan inovasi dan dikembangkan maka lakukanlah. </span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Bukan berarti karena saya bergelut di bidang FnB maka ilmu pertaniannya terlupakan, tidak dong. Sampai saat ini saya masih terus belajar di bidang pertanian karena berpotensi untuk dapat ditekuni dalam jangka panjang.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Jadi pesan saya untuk adi-adik SMK Negeri 1 Purwosari semuanya, semangat dan terapkan semua ilmu yang diperoleh semasa sekolah dan perbanyaklah teman, ikuti ekstrakulikuler agar bisa berkembang.</span></span></span></p>\n\n<p><strong>Owner&nbsp;Soe Meals</strong></p>\n\n<p><iframe height=\"670\" src=\"https://drive.google.com/file/d/1Rxzq5nxh7LvB9wmjUaJruNL7oaW0r7EV/preview\" width=\"480\"></iframe></p>\n\n<p><strong>Testimoni Soe Meals Oleh Bapak Kepala Sekolah dan&nbsp;Guru Bahasa Inggris</strong></p>\n\n<p><iframe height=\"670\" src=\"https://drive.google.com/file/d/1eOUw3Lkp6LQMFjpfthtw5DQWSUVhsR3B/preview\" width=\"480\"></iframe></p>\n\n<p><strong>Testimoni Soe Meals Oleh&nbsp;Guru jurusan ATPH</strong></p>\n\n<p><iframe height=\"670\" src=\"https://drive.google.com/file/d/1l0sLzWz9UDEWD9intD9bCK4SoD6OVGcB/preview\" width=\"480\"></iframe></p>\n', 0, 'SMK Negeri 1 Purwosari', 3, 9, 172, NULL, 1, NULL, '2024-03-01 03:59:35', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (462, 'Campus Expo, Media Pengenalan Dunia Perkuliahan bagi Siswa SMKN 1 Purwosari oleh Alumni', '', '1673929612.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp;Kegiatan expo kampus di SMKN&nbsp;1 Purwosari&nbsp;merupakan ajang promosi kampus dari para alumni 2022. Kegiatan ini bertujuan mengenalkan Perguruan Tinggi yang dapat menjadi pilihan adik kelas untuk melanjutkan sekolahnya. Exco kampus dihadiri&nbsp;dari Perguruan Tinggi Negeri dan&nbsp;Perguruan Tinggi Swasta.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&nbsp; &nbsp;Kegiatan ini diawali dengan briefing fasilitator yang akan menjelaskan, jalur-jalur masuk PTN/PTS. Setelah itu dilanjutkan dengan sosialisasi pengenalan kampus dan pengalaman-pengalaman alumni selama menjadi mahasiswa. Bagi siswa yang sudah menemukan kampus yang ingin dituju bisa ditanyakan langsung saat proses diskusi dan bisa menghubungi kakak tingkat yang sudah masuk di PTN Negri maupun Swasta.</span></span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SMK NEGERI 1 PURWOSARI</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SMK.... BISA</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SMK..... HEBAT</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SMK..... BISA HEBAT</span></span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 123, NULL, 1, NULL, '2024-03-01 05:27:03', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (463, 'Pembelajaran Jarak Jauh 2022 dengan PENS Surabaya', '', '1673930510.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Penerimaan Mahasiswa Baru (PMB)</span></strong></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Program Pendidikan Jarak Jauh (PJJ) PENS</span></strong></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Prodi D3 PJJ Teknik Informatika dan Prodi Sarjana Terapan PJJ Teknik Telekomunikasi</span></strong></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Politeknik Elektronika Negeri Surabaya (PENS)</span></strong></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Tahun Ajaran 2023/2024</span></strong></span></span></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp; &nbsp; Politeknik Elektronika Negeri Surabaya (PENS) membuka pendaftaran mahasiswa Program Studi Pendidikan Jarak Jauh (PJJ) PENS, untuk Program Studi: D3 PJJ Teknik Informatika dan D4 PJJ Teknik Telekomunikasi. Program Studi PJJ Teknik Informatika PENS telah mendapatkan SK perijinan penyelenggaraan program PJJ pada lingkup Program Studi D3 PJJ Teknik Informatika dengan SK Nomor 62/M/KPT/2017 (Terakreditasi B), dan Program Studi D4 PJJ Teknik Telekomunikasi dengan SK Nomor 184/M/KPT/2017 (Terakreditasi B).</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">D3 PJJ Teknik Informatika PENS:</span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp; &nbsp; Membekali mahasiswa dengan kemampuan mengembangkan dan memelihara sistem informasi berbasis komputer pada lingkungan bisnis dan manufaktur. Kompetensi prodi ini meliputi: Basis data, Sistem Informasi, Rekayasa Perangkat Lunak, jaringan komputer dan multimedia.</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><strong>D4 PJJ Teknik Telekomunikasi PENS:</strong></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp; &nbsp; &nbsp;Membekali mahasiswa dengan pengetahuan dan kemampuan di bidang instalasi dan pemrograman telepon analog dan jaringan, pembuatan aplikasi jaringan berbasis pemrograman shell, sistem komunikasi nirkabel, serta pengolahan sinyal.</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Sistem Pembelajaran:</span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Prodi PJJ PENS menganut Sistem Hybrid yaitu:</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp; &nbsp; &nbsp;Dapat mengikuti Kuliah pada Pagi Hari sesuai jadwal jam reguler atau Sore Hari (mengikuti materi yang di sampaikan pada pagi hari dengan bentuk tapping / rekaman yang akan di jelaskan ulang oleh Tutor / Dosen Pendamping di PBJJ </span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">&nbsp; &nbsp; &nbsp;Mata kuliah teori dan praktik dilaksanakan 16X (enam belas kali) termediasi online di Aplikasi Ethol atau di ethol.pens.ac.id, bersama Dosen PENS&nbsp; atau Mata kuliah praktikum dilaksanakan 16X (enam belas kali) tatap muka langsung dengan Tutor/Pendamping di PBJJ. Untuk mahasiswa PJJ Teknik Informatika yang berdomisili di kota yang tidak ada PBJJ, mahasiswa bisa melakukan kuliah teori/praktikum online secara sinkron maupun asinkron darimana saja selama koneksi internet stabil dan dapat mengikuti pembelajaran sampai selesai.</span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 120, NULL, 1, NULL, '2024-03-01 03:59:59', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (464, 'Sosial Emotional Learning Untuk Siswa SMKN 1 Purwosari', '', '1673930377.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Pentingnya Pembelajaran Social Emotional Learning (SEL) bagi Anak</span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Social Emotional Learning &ndash; Salah satu yang pembelajaran yang dapat mempengaruhi proses belajar anak adalah emosi. Social Emotional Learning (SEL) merupakan pembelajaran yang akan memberi dampak positif pada anak yakni perilaku anak ke diri sendiri, orang lain serta lingkungan. Lalu, bagaimana pentingnya pembelajaran Social Emotional Learning (SEL) bagi anak? Untuk itu, maka simak informasi lebih lengkapnya berikut.</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Social Emotional Learning atau SEL adalah pembelajaran yang di dalamnya terdapat proses pembentukan diri yang berkaitan dengan diri sendiri, mengontrol diri, dan kemampuan menjalin hubungan dengan yang lain. Apakah pembelajaran SEL itu penting? Tentu saja sangat penting, karena proses dalam pembelajaran ini akan membantu anak memiliki kehidupan yang baik, ketika berada di sekolah, lingkungan kerja atau masyarakat. Seseorang yang memiliki sosial emosional akan memiliki kelebihan seperti:</span></span></span></p>\n\n<ul>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mudah menerima tantangan</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mudah belajar</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Memiliki sikap professional</span></span></span></li>\n</ul>\n\n<p style=\"margin-left:48px; text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mudah bersosialisasi</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Sehingga, pembelajaran SEL tidak hanya fokus pada kemampuan anak dalam jangka waktu pendek, namun juga untuk jangka panjang. Untuk itu banyak pihak sekolah yang menerapkan SEL dalam pembelajaran anak. Namun, penerapannya butuh perhatian khusus, yakni harus sesuai dengan SAFE, yakni:</span></span></span></p>\n\n<ul>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Sequenced, pembelajaran saling berkaitan dan terkoordinasi guna mendorong keterampilan anak.</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Active, yaitu pembelajaran harus aktif agar anak mampu menguasai keterampilan yang baru.</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Focused, pembelajaran menenkankan pada kemampuan anak secara individu maupun sosial.</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Explicit, hendaknya pembelajaran memiliki target dalam pembentukan keterampilan sosial dan emosional yang lebih spesifik.</span></span></span></li>\n</ul>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong><span style=\"font-size:12.0pt\">Kompetensi Pada Social Emotional Learning</span></strong></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Daniel Goleman merupakan pelopor dari pembelajaran sosial dan emosional ini. Menurut Daniel, pembelajaran sosial emosional penting diberikan agar anak mendapatkan kesuksesan dan guna mengembangkan pendidikan. menurut CASEL (Collaborative for Academic, Social and Emotional Learning) ada 5 kunci dalam pengembangan SEL pada anak, di antaranya:</span></span></span></p>\n\n<ul>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Kesadaran Diri (Self Awareness)</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Memanajemen Diri (Self Management)</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Memiliki Kesadaran Sosial (Social Awareness)</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Mampu Membuat Relasi (Relation Skill)</span></span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Membuat Keputusan Bertanggungjawab (Responsible Decision Making)</span></span></span></li>\n</ul>\n', 1, 'SMKN 1 Purwosari', 3, 11, 2437, NULL, 1, NULL, '2024-03-04 11:16:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (465, 'Kunjungan ke Sekolah T-TEP & Sub T-TEP TOYOTA ASTRA MOTOR', '', '1674697477.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><strong>Kunjungan ke Sekolah T-TEP &amp; Sub T-TEP TOYOTA ASTRA MOTOR&nbsp; &nbsp;</strong></p>\n\n<p>Pelaksanaan T-TEP (Toyota - Technical Education Program)&nbsp;dilaksanakan oleh TOYOTA ASTRA MOTOR di SMKN 1 Purwosari&nbsp;pada Hari Rabu 25 Januari 2023. Team T-TEP Company Project TAM bermaksud mengadakan kunjungan ke sekolah binaan T-TEP &amp; Sub T-TEP (General Repair % Body Paint), Dalam program ini Toyota memberikan beberapa fasilitas bagi Sekolah Menengah Kejuruan terpilih yang menjadi institusi T-TEP, seperti training manual, pengembangan kurikulum terbaru, pelatihan untuk guru, buku panduan technical Toyota.</p>\n\n<p>Kunjungan ini dihadiri oleh :&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Suryo Kepala Diklat PT. Toyota Astra Motor</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Ibu Yoke Direktur Auto 2000 Wilayah Jatim Bali</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Setiawan Kepala Auto 2000 Learning Center Jatim Bali</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Budi Kepala Bengkel Auto 2000 Singosari Malang</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Rajendra Kepala Bengkel Auto 2000 Pasuruan</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Dharma Kepala Bengkel Auto 2000 Malang Sutoyo</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Bpk. Deni Kepala Bengkel Toyota Kartikasari Malang</span></span></p>\n\n<p>Kehadiran ini bertujuan&nbsp;untuk&nbsp;<span style=\"font-size:11.0pt\">Visitasi SMK yg tergabung dalam Program Kelas Sub T-TEP,&nbsp;Pihak PT. TOYOTA ASTRA MOTOR melalui Kepala Diklat PT. TOYOTA ASTRA MOTOR melakukan visitasi ke SMK guna mengetahui sejauh mana perkembangan SMK binaannya,&nbsp;Pemenuhan kebutuhan peralatan di SMK binaannya yg kurang dan&nbsp;akan dipenuhi secara bertahap serta Membuka peluang bekerja untuk alumni SMK Binaan di PT. Tayota Astra Motor melalui Auto 2000</span>.</p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 332, NULL, 1, NULL, '2024-03-04 07:58:07', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (466, 'Pembelajaran Entrepreneur dari PT. Indobismar', '', '1674946366.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pada hari Rabu tanggal 25 Januari 2023, siswa kelas XII SMKN 1 Purwosari mendapatkan kesempatan untuk mengikuti pembelajaran entrepreneur dari PT. INDO BISMAR. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">PT. INDO BISMAR merupakan Perusahaan swasta yang bergerak dalam bidang telekomunikasi, yang menyediakan Handphone, Laptop, PC, dll., selain itu juga mempunya divisi lain seperti, Rental Mobil, percetakan, Restoran dan tempat rekreasi. Hadir dalam kegiatan ini, Bpk. DR. Siswanto, S.Sos, MM selaku CEO PT. INDO BISMAR. Beliau yang juga merupakan alumni SMK, tepatnya dari SMKN 1 Glagah, dengan sangat bersemangat memberikan motivasi kepada siswa SMKN 1 Purwosari untuk menjadi wirausahawan tangguh, pantang menyerah dan sukses luar biasa.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dalam kesempatan ini, juga dilaksanakan penandatanganan MoU antara SMKN 1 Purwosari dengan PT. INDO BISMAR. </span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bpk. Rudi Trisantoso, S.Pd, M.Pd selaku Kepala SMKN 1 Purwosari dalam sambutannya menyampaikan ucapan terima kasih kepada PT. INDO BISMAR untuk pembelajaran entrepreneur yang telah diberikan kepada siswa SMKN 1 Purwosari serta berharap agar kerja sama yang telah terjalin semakin erat dan memberikan kontribusi yang positif bagi kedua belah pihak.</span></span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 230, NULL, 1, NULL, '2024-03-04 03:06:33', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (467, 'Kerjasama SMKN 1 Purwosari dengan PT Hummatech Digital Indonesia', '', '1675149439.jpeg', '', NULL, NULL, '<p>Senin, 30 Januari 2023 bertempat di aula SMKN 1 Purwosari dilaksanakan Penandatanganan perjanjian kerjasama antara SMKN 1 Purwosari dengan PT Hummatech Digital Indonesia yang salah anak perusahaannya adalah Hummasoft.</p>\n\n<p>Hummasoft merupakan sebuah perusahaan yang bergerak dibidang IT (Information Technology) yang memiliki beberapa divisi layanan, diantaranya: Software Development (Website Application, Desktop Application, and Mobile Application), IT Course, IT Training, IT Research, and IT Services.</p>\n\n<p>Bpk. Afrizal Himawan, S.Kom selaku Direktur PT. Hummatech Digital Indonesia dalam sambutannya menyampaikan, agar siswa SMK ketika lulus sudah mempunyai bisnis, terutama menjadi programer-programer muda untuk alumni Konsentrasi Keahlian Rekayasa Perangkat Lunak. &nbsp;80% tim di Hunmasof adalah lulusan SMK.&nbsp;</p>\n\n<p>Dalam kesempatan ini, Selain penandatanganan perjanjian kerja sama juga dilaksanakan pembelajaran dari DUDIKA (Guru Tamu)dengan materi &quot;Menghadapi Industri 4.0 dalam dunia pemrograman dan prospek bisnis IT&quot;.</p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 173, NULL, 1, NULL, '2024-03-01 03:38:09', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (468, 'Peringatan IsroÃ¢â‚¬â„¢ MiÃ¢â‚¬â„¢roj Nabi Besar Muhammad SAW 1443H Dan Istighosah', '', '1676798027.JPG', '', NULL, NULL, '<p style=\"text-align:center\">PERINGATAN ISRO&rsquo; MI&rsquo;ROJ NABI BESAR MUHAMMAD SAW 1444H</p>\n\n<p style=\"text-align:center\">DAN ISTIGHOSAH</p>\n\n<p>&nbsp;</p>\n\n<p>Pada hari Jumat tanggal 17&nbsp;Februari&nbsp;2023, SMKN 1 Purwosari mengadakan peringatan isro mi&rsquo;roj nabi Muhammad SAW 1443H dan sekaligus istighosah bersama. Kegiatan tersebut di ikuti oleh seluruh siswa, sehingga pada kegiatan kali ini istighosahnya mengambil tema &ldquo;TEBARKAN MANFAAT, WUJUDKAN GENERASI MUSLIM TAAT IBADAH&rdquo;.</p>\n\n<p>Bertindak sebagai penceramah&nbsp;istighosah adalah Ustadz H. Mukhlis Imron, S.Pd, M.Pd&nbsp;dalam peringatan ini yang sekaligus sebagai motivator bagi seluruh siswa&nbsp;dalam menghadapi era milenial untuk terus taat dalam beribadah dan selalu berpegang teguh kepada agama.</p>\n\n<p>Pada materi ceramahnya pembicara menyampaikan sekilas tentang perjalann isro&rsquo; mi&rsquo;raj Nabi Besar Muhammad SAW, 1444H. yang sebenarnya &nbsp;mengandung banyak pelajaran dari siapa-siapa saja para nabi dan rosul yang ditemui oleh Nabi Muhammad SAW. Dan keutamaan yang luar biasa adalah perintah sholat 5 waktu, dimana saat ini dihimbau kepada para siswa untuk senantiasa tetap bisa istiqomah menjalankan sholat 5 waktu secara waktu dan jangan sampai meninggalkannya.</p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 87, NULL, 1, NULL, '2024-03-01 03:56:31', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (469, 'Kunjungan dari PT. TOYOTA ASTRA MOTOR Jakarta', '', '1677142424.jpeg', '', NULL, NULL, '<p>Implementasi Kurikulum Merdeka berupaya untuk memulihkan pembelajaran demi mewujudkan transformasi pendidikan di Indonesia ke arah yang lebih baik. Pada Kurikulum Merdeka, guru dapat mengenali potensi murid lebih dalam guna menciptakan pembelajaran yang relevan.&nbsp;Kurikulum Merdeka juga memungkinkan guru untuk menerapkan pembelajaran yang menyenangkan karena bisa dilakukan melalui pembelajaran berbasis projek</p>\n\n<p>Kunjungan dari PT. TOYOTA ASTRA MOTOR Jakarta yang dilaksanakan pada hari Selasa 21 Februari 2023, dalam rangka persiapan training guru terkait implementasi kurikulum GR baru (TEAM GP). Kunjungan tersebut meliputi pengecekan kondisi sarana dan prasarana yang ada di bengkel teknik kendaraan ringan otomotif dan teknik bodi otomotif.</p>\n\n<p>Hadir dalam kunjungan tersebut Bapak Andre dan Bapak Ade dari PT, TOYOTA ASTRA MOTOR Jakarta.&nbsp;</p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 106, NULL, 1, NULL, '2024-03-01 03:59:38', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (470, 'Penyuluhan dan Bimbingan Jabatan (PBJ) oleh Dinas Tenaga Kerja Kab. Pasuruan', '', '1677142848.jpeg', '', NULL, NULL, '<p>Penyuluhan dan Bimbingan Jabatan merupakan suatu proses untuk membantu seseorang dalam memecahkan permasalahan yang berhubungan dengan pemilihan pekerjaan atau&nbsp;jabatan, dengan tujuan untuk mempertemukan antara karakteristik individu dengan peluang / kesempatan kerja yang ada&nbsp;sehingga seseorang (Pencari Kerja) menyadari realitas potensi dirinya dan dapat memiliki gambaran obyektif tentang peluang kesempatan kerja dan pada akhirnya mampu menentukan pilihan karirnya secara tepat.</p>\n\n<p>Sasaran program PBJ ditujukan bagi para pencari kerja, siswa sekolah, mahasiswa, masyarakat dan pihak-pihak yang terkait dengan pelayanan penempatan tenaga kerja dalam rangka membantu mempersiapkan calon tenaga kerja yang kompeten dan memenuhi persyaratan kompetensi kerja (job requirement) yang dibutuhkan di pasar kerja. Untuk dapat memenuhi kesenjangan antara persyaratan jabatan dengan kompetensi yang dimiliki seseorang, antara lain dapat diarahkan melalui program-program pelatihan kerja atau program lain yang dapat meningkatkan kompetensinya sehingga persyaratan jabatan dapat dipenuhi.</p>\n\n<p>Rabu, 22 Februari 2023 Penyuluhan dan Bimbingan Jabatan (PBJ) oleh Dinas Tenaga Kerja Kab. Pasuruan Hadir dalam acara tersebut Bapak Kepala Sekolah, perwakilan dari Dinas Tenaga Kerja Kab. Pasuruan, Bpk. Anggara dari PT. Trisakti Purwosari Makmur, dan Bpk Andri Wahyudi Wakil Ketua 1 DPRD Kab. Pasuruan. Kegiatan tersebut bertujuan untuk meningkatkan kesiapan diri memasuki dunia kerja bagi siswa, sehingga akan tercapai pelayanan penempatan tenaga kerja secara optimal dengan prinsip The Right Man On The Right Job.</p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 136, NULL, 1, NULL, '2024-03-04 07:37:43', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (471, 'Sosialisasi dan penerangan hukum oleh Kejaksaan Negeri Kab. Pasuruan', '', '1677143299.JPG', '', NULL, NULL, '<p>Tepatnya pada hari Kamis, 23&nbsp;Februari&nbsp;2023&nbsp;Tim dari Kejaksaan Negeri Kab. Pasuruan&nbsp;yang dihadiri oleh Bapak Bapak Denata Suryaningrat, Kasi Barang Bukti dan Barang Rampasan dan Bapak Hendro Nugroho berkunjung ke SMKN 1 Purwosari. Kunjungan tersebut dalam rangka Sosialisasi dan penerangan hukum oleh Kejaksaan Negeri Kab. Pasuruan</p>\n\n<p>Sosialisasi tentang Restorative Justice yang diikuti oleh siswa, guru BK, wakil kepala sekolah dan kepala sekolah SMKN 1 Purwosari, SMKN 1 Sukorejo, SMKN 2 Sukorejo dan SMKN Tutur.&nbsp;</p>\n\n<p>Restorative justice&nbsp;adalah penyelesaian tindak pidana dengan melibatkan pelaku, korban, keluarga pelaku, keluarga korban, tokoh masyarakat, tokoh agama, tokoh adat, atau pemangku&nbsp;kepentingan&nbsp;untuk bersama-sama mencari penyelesaian yang adil melalui perdamaian dengan menekankan pemilihan kembali pada keadaan semula&nbsp; Penghentian penuntutan dengan berdasarkan syarat formil dan materiil.&nbsp;</p>\n\n<p>Syarat formil :&nbsp;<br />\n- Belum pernah dihukum, ancaman hukuman maksimal 5 tahun, kerugian tidak lebih 3,5 jt.<br />\nSyarat materiil:<br />\n- Ada kesepakatan damai, pemenuhan hak dan tanggung jawab.&nbsp;</p>\n\n<p>Perkara yang biasa ditangani di Restorative Justice :<br />\n- Perilaku berlalulintas, perilaku bermedsos/ UU ITE, perkelahian/masalah di circle pertemanan</p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 112, NULL, 1, NULL, '2024-02-07 09:18:45', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (472, 'TIM Futsal SMKN 1 Purwosari Memperoleh Juara 2 Tingkat Pelajar SLTA se-Kab/Kota Pasuruan', '', '1677935415.jpeg', '', NULL, NULL, '<p>Tim futsal putra SMKN 1 Purwosari berhasil mendapatkan juara 2 di&nbsp;turnamen futsal yang diadakan oleh HIMABIS CUP Universitas Yudharta Pasuruan. Pasukan asuhan Bapak Miftachul Huda, pelatih futsal SMKN 1 Purwosari ini tampil dominan dengan mengukir beberapa kemenangan dan kalah di partai final melawan SMK Nesaba dengan skor 3-4, Sabtu&nbsp;(25 /02/2023) yang lalu.</p>\n\n<p>Menurut Bapak Miftachul Huda, tim futsal SMKN 1 Purwosari&nbsp;membuka keunggulan pada babak pertama saat waktu menunjukkan 5&nbsp;menit melalui gol Andrian Dwi F, dengan skor 1-0. Setelah itu terdapat aksi balasan dari tim musuh dan babak kedua ditutup dengan keunggulan musuh dengan skor 3-4</p>\n\n<p>Mendapatkan juara itu tidak semudah membalikkan telapak tangan tapi melalui proses panjang, tim futsal kita mampu menunjukkan itu. Alhamdulillah tim futsal sudah ada regenerasi dari kelas berikutnya, lebih ditingkatkan lagi untuk tahun tahun kedepannya agar tim futsal SMKN 1 Purwosari menjadi Jawara</p>\n\n<p>Berikut nama-nama tim futsal SMKN 1 Purwosari saat bertanding :</p>\n\n<p>1. Sheftian Tri Bambang ( XII TL2)<br />\n2. Novirza Ramadhan (XI TKRO 1)<br />\n3. Dicky Eza (XII TPM 1)&nbsp;<br />\n4.Wafi Aqim (XII TPM 2)<br />\n5.Moch Emir Hakim (X TPM 1)<br />\n6. Risqianto mubarok (XII TPM 1)<br />\n7. Thomas Agung Sadewa P.(XII RPL)<br />\n8. M. Zidan Afrisandi (XII TKJ 1)<br />\n9. Sandi Avansya S. (XII MM 2)<br />\n10.Adrian Dwi F.(XI TKRO 1)</p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 100, NULL, 1, NULL, '2024-03-01 11:12:53', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (473, 'SMKN 1 Purwosari Tampil Sebagai Jawara di acara Daihatsu Vocational Day 2023', '', '1677937372.jpeg', '', NULL, NULL, '<p><span style=\"font-size:12pt\"><strong>&nbsp;</strong><span style=\"color:black\">Pada Sabtu (4/3/2023),&nbsp;<a href=\"https://www.suara.com/tag/pt-astra-daihatsu-motor\" style=\"color:blue; text-decoration:underline\"><span style=\"color:black\">PT Astra Daihatsu Motor</span></a>&nbsp;(ADM) menggelar&nbsp;<a href=\"https://www.suara.com/tag/daihatsu-vocational-day\" style=\"color:blue; text-decoration:underline\"><span style=\"color:black\">Daihatsu Vocational Day</span></a>&nbsp;2023 di lokasi fasilitasnya, Sunter Assembly Plant, Jakarta. Sebuah ajang yang menjadi bukti komitmen perusahaan otomotif ini dalam mendukung&nbsp;<a href=\"https://www.suara.com/tag/pendidikan-vokasi\" style=\"color:blue; text-decoration:underline\"><span style=\"color:black\">pendidikan vokasi</span></a>&nbsp;di Tanah Air. Yaitu lewat program&nbsp;<em>Pintar Bersama Daihatsu.</em></span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"color:black\">dalam kesempatan Daihatsu Vocational Day 2023, PT ADM memberikan apresiasi kepada mitra pendukung program pembinaan SMK, yaitu&nbsp;<a href=\"https://www.suara.com/tag/pt-astra-international-daihatsu-sales-operation\" style=\"color:blue; text-decoration:underline\"><span style=\"color:black\">PT Astra International Daihatsu Sales Operation</span></a>, dan Politeknik Astra. Ada 66 SMK menerima apresiasi atas pencapaian mereka dalam implementasi beberapa program unggulan seperti Dojo Center, Bengkel Sahabat Daihatsu, dan Teaching Factory di sekolah.</span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"color:black\">Dalam kesempatan yang sama, Daihatsu juga menggelar Final Daihatsu&nbsp;<a href=\"https://www.suara.com/tag/smk-skill-contest\" style=\"color:blue; text-decoration:underline\"><span style=\"color:black\">SMK Skill Contest</span></a>&nbsp;ketujuh, bertempat di Daihatsu Training Center. Sebuah kegiatan tahunan bagi para siswa dan guru SMK untuk berkompetisi menguji kemampuan teknikal mereka dalam hal pengecekan kendaraan berkala, dan trouble shooting.</span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"color:black\">Dari 317 SMK binaan Daihatsu yang menghasilkan 30.000 lebih pendaftar, terpilih delapan finalis hasil seleksi. SMKN 1 Purwosari Mendapatkan Juara 1 dalam acara Daihatsu Vocational Day 2023 yang di wakili oleh Bapak Andri Purwantono dan Siswa a.n. Misbakhul Munir</span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"color:black\">Selain itu, ADM juga turut mendukung program Pusat Keunggulan (PK) Pemadanan yang dicetuskan Kementerian Pendidikan, Kebudayaan, Riset dan Teknologi Republik Indonesia, di mana industri diharapkan dapat bersinergi dengan SMK untuk menciptakan link and match.</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 199, NULL, 1, NULL, '2024-03-04 00:20:11', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (474, 'BUPATI PASURUAN HADIRI PELETAKAN BATU PERTAMA PEMBANGUNAN MASJID DI SMKN 1 PURWOSARI', '', '1678327226.JPG', '', NULL, NULL, '<p style=\"text-align:center\"><strong>BUPATI PASURUAN HADIRI PELETAKAN BATU PERTAMA PEMBANGUNAN MASJID DI SMKN 1 PURWOSARI</strong></p>\n\n<p>&nbsp; &nbsp;Rabu, 8 Maret 2023 Bupati Pasuruan Dr. H. M. Irsyad Yusuf, SE, MMA berkenan datang ke SMKN 1 Purwosari untuk melakukan peletakan batu pertama pembangunan masjid Sabilul Khoirot SMKN 1 Purwosari.<br />\n&nbsp; &nbsp; Selain Bupati Pasuruan hadir pula Plt. Kepala Cabang Dinas Pendidikan Wilayah Pasuruan Bpk. Dr. Mustakim, S.S, M.Si, Forkomcam antara lain Camat Purwosari (Bpk. Sugeng Haryadi, SE. MM), Danramil (Kapten Czi Mukrab) dan Kapolsek Purwosari beserta jajarannya.<br />\nDalam kesempatan ini Bpk. Rudi Trisantoso, M.Pd menyampaikan selamat datang dan ucapan terima kasih atas kehadiran Bapak Bupati Pasuruan dan segenap undangan juga perkenannya untuk melakukan peletakan batu pertama dalam pembangunan masjid Sabilul Khoirot SMKN 1 Purwosari. Selain itu Beliau juga menyampaikan prestasi-prestasi yang diperoleh siswa siswi SMKN 1 Purwosari dalam berbagai lomba baik akademis maupun non akademis.<br />\n&nbsp; &nbsp; Bapak Dr. Mustakim, S.S, M.Si (Plt. Kacabdin Pendidikan Wilayah Pasuruan) dalam sambutannya menyampaikan apresiasi kepada Bpk. Rudi Trisantoso, M.Pd selaku Kepala SMKN 1 Purwosari atas banyaknya prestasi yang telah diraih oleh SMKN 1 Purwosari. Salah satunya adalah Juara 1 Tingkat Nasional dalam Daihatsu SMK Skill Contest yang diselenggarakan di Jakarta pada tgl 3 - 4 Maret yang lalu.<br />\nSelain melakukan peletakan batu pertama dalam pembangunan masjid, Bapak Bupati Pasuruan juga menyampaikan motivasi yang membangkitkan semangat kepada siswa siswa SMKN 1 Purwosari untuk menjalani masa depan yang penuh dengan tantangan. Beliau juga berharap semoga pembagunan masjid dapat berjalan dengan lancar sesuai dengan rencana, sehingga dapat bermanfaat untuk banyak orang.</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 168, NULL, 1, NULL, '2024-03-04 06:13:54', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (475, 'Hijaukan Bumi Tanam 34.000 Pohon Sepanjang 2023 se-Indonesia', '', '1678327969.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><strong>Hijaukan Bumi Tanam 34.000pohon Sepanjang 2023 se-Indonesia</strong></p>\n\n<p>Rabu 8 Maret 2023, PT Federal International Finance (FIF GROUP)&nbsp;salah satu anak perusahaan PT Astra International Tbk yang juga bagian dari Astra Financial, mengadakan kegiatan Hijaukan Bumi &ldquo;Tanam 34.000 Pohon Sepanjang 2023&nbsp;se-Indonesia&rdquo;, berkunjung ke SMKN 1 Purwosari</p>\n\n<p>Kegiatan ini sebagai rangkaian peringatan Hari Ulang Tahun FIF Group ke-34. Untuk tahap pertama penanaman 3.400 pohon atau 10% dari rencana program digelar secara serentak dengan 10 titik di wilayah Indonesia.&nbsp;</p>\n\n<p>Untuk setiap tahap selanjutnya, FIF Group berkolaborasi dengan seluruh titik yang berada pada jangkauan jaringan perusahaan di 235 cabang seluruh Indonesia untuk melaksanakan penanaman pohon sebanyak 34.000 secara bertahap di sepanjang tahun 2023&nbsp;dengan masing-masing 10 titik.</p>\n\n<p>Aksi penanaman pohon ini, diharapkan dapat melindungi dan memulihkan penggunaan berkelanjutan ekosistem darat, mengelola hutan secara berkelanjutan, mengembalikan degradasi tanah serta menghambat hilangnya keanekaragaman hayati</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 177, NULL, 1, NULL, '2024-03-04 02:56:55', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (476, 'Penerimaan Peserta Didik Baru (PPDB) 2023', '', '1681721290.jpeg', '', NULL, NULL, '', 1, 'SMKN 1 Purwosari', 3, 12, 545, NULL, 1, NULL, '2024-03-01 03:46:18', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (477, 'Halal BiHalal Keluarga Besar SMKN 1 Purwosari', '', '1682747210.jpeg', '', NULL, NULL, '', 1, 'SMKN 1 Purwosari', 3, 12, 125, NULL, 1, NULL, '2024-03-01 03:58:34', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (478, 'Apresiasi dari Gubernur Jatim untuk SMK Negeri 1 Purwosari', '', '1683162666.JPG', '', NULL, NULL, '<p style=\"text-align:center\"><strong><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">GUBERNUR JAWA TIMUR BERIKAN PENGHARGAAN UNTUK INSAN PENDIDIKAN</span></span></span></span></strong></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Selasa, 2 Mei 2023 dalam puncak acara Peringatan Hari Pendidikan Nasional tahun 2023 tingkat Provinsi Jawa Timur&nbsp; bertempat di Halaman Gedung Negara Grahadi, Jl. Gubernur Suryo no. 7 Surabaya, Gubernur Jawa Timur Ibu Khofifah Indar Parawansa memberikan penghargaan-penghargaan kepada insan Pendidikan Provinsi Jawa Timur dan menyerahkan penghargaan kepada SMA/ SMK di Jawa Timur atas prestasinya di tingkat regional, nasional dan internasional.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Bapak Andri Purwantono, S.Pd , guru sekaligus Ketua Kompetensi Keahlian Teknik Kendaraan Ringan Otomotif termasuk salah satu yang mendapatkan penghargaan dari Gubernur Jawa Timur atas prestasinya sebagai juara 1 Nasional dalam ajang Daihatsu SMK Skill Contest 2023 yang diikuti Bersama siswa SMKN 1 Purwosari atas nama Misbakhul Munir (XII TKRO) pada bulan Maret 2023 yang lalu.</span></span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Semoga prestasi yang telah diraih ini dapat menginspirasi insan Pendidikan yang lain untuk terus bergerak maju, berinovasi dan berprestasi. </span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">&nbsp;</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 129, NULL, 1, NULL, '2024-03-01 03:53:52', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (479, 'Gelar Karya Projek Penguatan Profil Pelajar Pancasila (P5) dan Keunggulan SMK', '', '1684310257.jpg', '', NULL, NULL, '', 1, 'SMKN 1 Purwosari', 3, 11, 226, NULL, 1, NULL, '2024-03-04 11:17:45', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (480, 'Siswa SMKN 1 Purwosari Rebut Juara 3 Duta Bahasa Se Jawa Timur', '', '1684673226.jpeg', '', NULL, NULL, '<p>Sebagai bahasa yang berkembang, bahasa akan terus mengalami perubahan sejalan dengan perkembangan masyarakat pemakainya. Luasnya wilayah pemakaian bahasa Indonesia, keanekaragaman penuturnya serta cepatnya perkembangan teknologi informasi merupakan kondisi yang menimbulkan permasalahan cukup kompleks dalam bidang kebahasaan.&nbsp;Idealnya, kecintaan terhadap bahasa harus terus dikembangkan demi menjaga martabat bahasa dan Tanah Air khususnya Jawa Timur. Pemartabatan bahasa tersebut akan bermuara pada pembentukan karakter dan penumbuhan rasa nasionalisme.</p>\n\n<p>Duta bahasa&nbsp;mengemban amanah untuk mengawal penggunaan dan pengutamaan&nbsp;bahasa di kalangan anak muda. Peran mulia sebagai abdi bahasa, jaga bahasa, dan niaga bahasa<br />\nArya Pakshi Airlangga tidak menyangka selempang Duta Bahasa Timur 2023&nbsp;itu akhirnya disampirkan di pundaknya. Arya meraih juara 3&nbsp;dalam pemilihan Duta Bahasa Timur Tahun&nbsp;2023.</p>\n\n<p>Finalis Cak Yuk Pasuruan 2023&nbsp;itu mengungkapkan, saat seleksi, dirinya menjalani serangkaian tes. Mulai tes kepribadian, unjuk minat dan bakat, penilaian etika dan penampilan, hingga kompetensi berbahasa yang diujikan melalui UKBI (Uji Kemahiran Berbahasa Indonesia). Tak tertinggal bahasa Inggris.</p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 159, NULL, 1, NULL, '2024-03-03 14:10:24', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (481, 'Pelatihan Guru SMK T-TEP & Sub T-TEP Kurikulum Baru Toyota Team-GP Area Jawa Timur Tahun 2023', '', '1685140629.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><strong>Pelatihan Guru SMK T-TEP &amp; Sub T-TEP Kurikulum Baru Toyota Team-GP Area Jawa Timur Tahun 2023</strong></p>\n\n<p>T-TEP (Toyota &ndash; Technical Education Program) merupakan program kerjasama antara Toyota Motor Corporation (TMC), PT. Toyota Astra Motor, dealer resmi Toyota dan pemerintah, dalam mengembangkan dan mempersiapkan SDM Sekolah Menengah Kejuruan (SMK) yang lebih siap memasuki industri otomotif.</p>\n\n<p>Dalam program ini, Toyota memberikan beberapa fasilitas bagi SMK terpilih yang menjadi SMK TTEP maupun Sub TTEP. Untuk SMK Sub TTEP, Toyota menyediakan training manual, pengembangan kurikulum yang terbaru, pelatihan untuk guru, buku panduan technical Toyota, dan kesempatan untuk melakukan latihan kerja di Toyota. Sementara untuk SMK TTEP, Toyota menyediakan keempat hal tersebut dan ditambah dengan fasilitas alat peraga.</p>\n\n<p>dalam kegiatan pelatihan yang diadakan oleh Toyoya di Learning Center Ngoro, Guru SMKN 1 Purwosari yaitu Bapak Andri Purwantono, S.Pd dan Bapak Amin meraih Gelar 1 &amp; 3 Peserta Pelatihan terbaik. Selamat Untuk Pak Andri dan Pak Amin</p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 124, NULL, 1, NULL, '2024-03-04 01:10:11', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (482, 'SMKN 1 Purwosari Raih Juara Lomba Kompetensi Siswa Tingkat Provinsi 2023', '', '1685140682.jpeg', '', NULL, NULL, '<p style=\"text-align:center\"><strong>SMKN 1 Purwosari Raih Juara Lomba Kompetensi Siswa Tingkat Provinsi 2023</strong></p>\n\n<p>SMKN 1 Purwosari merupakan salah satu peserta Lomba Kompetensi Siswa pada bidang lomba CNC Turning yang diwakili oleh Muhammad Rizky Arbi Rabbani dari kelas XII TPM 2 dan dibimbing oleh Bp. Julianto S.Pd</p>\n\n<p>Pada pelaksanaan lomba CNC Turning, peserta diberikan Job Gambar yang akhirnya akan di proses dalam mesin CNC sehingga menjadi benda yang sesuai dengan Job Gambar yang telah diberikan.</p>\n\n<p>Dari Lomba Kompetensi Siswa tingkat Provinsi ini siswa dari SMKN 1 Purwosari mampu bersaing dengan Sekolah Lain yang berada di Jawa Timur dari segi Mental, Materi, dan Skill dalam mengoperasikan Mesin Industri. SMKN 1 Purwosari tidak kalah hebat dari SMK lain di daerah Jawa Timur dan Akan berjuang kembali di Tingkat Nasional Untuk membawa nama baik Sekolah dan Provinsi Jawa Timur.</p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 168, NULL, 1, NULL, '2024-03-01 04:09:22', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (483, 'PURNAWIYATA SMKN 1 PURWOSARI ANGKATAN XXI', '', '1686468452.jpg', '', NULL, NULL, '', 1, 'SMKN 1 Purwosari', 3, 11, 351, NULL, 1, NULL, '2024-03-04 06:33:22', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (484, 'Asistensi Mengajar Universitas Negeri Malang di SMKN 1 Purwosari Kabupaten Pasuruan', '', '1686715372.JPG', '', NULL, NULL, '', 1, 'SMKN 1 Purwosari', 3, 11, 157, NULL, 1, NULL, '2024-03-03 20:14:48', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (485, 'Innalillahi Wainna Ilaihi Rojiun Turut Berdukacita Atas Berpulangnya Bpk Tarmuji S.Pd ke Rahmatullah', '', '1688167988.jpeg', '', NULL, NULL, '<p>Inna lillahi wa inna ilaihi raji&#39;un. Turut berduka atas meninggalnya Bapak Tarmuji S.Pd. Semoga Allah SWT mengampuni segala dosanya dalam keadaan husnul khotimah dan memberikan Jannatul Firdaus. Amin ya Rabbal allamin.&nbsp;Sesungguhnya Allah memiliki hak untuk mengambil serta memberi sesuatu dari hamba-hamba-Nya, sebab segala sesuatu tersebut hanyalah titipan saja. Semoga keluarga dari bapak Tarmuji diberikan kesabaran serta ketabahan atas kepergian sosok yang&nbsp;disayangi<br />\n&nbsp;</p>\n', 1, 'SMKN 1 Purwosari', 3, 11, 162, NULL, 1, NULL, '2024-03-03 05:36:58', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (486, 'Pembukaan Pekan MPLS (Masa Pengenalan Lingkungan Sekolah) Tahun Ajaran 2023-2024 di SMKN 1 Purwosari', '', '1689646066.jpg', '', NULL, NULL, '<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">MPLS (Masa Pengenalan Lingkungan Sekolah) <span style=\"background-color:white\">merupakan sebuah kegiatan yang umum dilaksanakan di sekolah setiap awal tahun ajaran guna menyambut kedatangan para peserta didik baru.</span> MPLS dijadikan sebagai ajang untuk melatih ketahanan metal, disiplin, dan mempererat tali persaudaraan. MPLS juga sering dipakai sebagai sarana perkenalan siswa terhadap lingkungan baru di sekolah tersebut. Baik itu perkenalan dengan sesama siswa baru, senior, guru, hingga karyawan lainnya di sekolah itu. Tak terkecuali pengenalan berbagai macam kegiatan yang ada dan rutin dilaksanakan di lingkungan sekolah.</span></span></p>\n\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">MPLS di SMKN 1 Purwosari dimulai pada tanggal 17 Juli 2023 yang diawali dengan upacara pembukaan MPLS. Kemudian dilanjutkan dengan perkenalan wali MPLS serta pemberian materi MPLS. Materi MPLS pada hari pertama ini mencakup materi Wawasan Wiyata Mandala, materi Budi Pekerti dan Tata Krama, serta materi Sosialisasi Kurikulum Merdeka.</span></span></span></p>\n\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Pada tanggal 18 Juli 2023 MPLS hari kedua di awali dengan apel pagi yang dilanjutkan dengan pemberian materi. Materi pada hari kedua ini mencakup materi Tata Tertib Sekolah, materi Penanggulangan Penyalahgunaan Narkoba, serta Kegiatan Pengenalan Fasilitas Sarana dan Prasarana Sekolah.</span></span></span></p>\n\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Pada tanggal 20 Juli 2023 MPLS hari ketiga seperti pada hari sebelumnya selalu di awali dengan apel pagi yang dilanjutkan dengan pemberian materi. Materi pada hari ketiga ini mencakup materi Sosial Emosional Learning (green mindset, GEDSI, SEL) dan materi Wawasan Kebangsaan. Kemudian dilanjutkan dengan penutupan MPLS oleh bapak Kepala Sekolah.</span></span></span></p>\n\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Pada tanggal 21 Juli 2023 para peserta didik baru melakukan kegiatan Senam Kesegaran Jasmani bersama-sama yang kemudian dilanjutkan Demo Ekstrakurikuler oleh OSIS dan PKS. Di akhir kegiatan pada tanggal 22-23 Juli 2023 dilaksanakan kegiatan CARAKA oleh peserta didik baru di SMKN 1 Purwosari.</span></span></span></p>\n\n<p><span style=\"font-size:14px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"background-color:white\">Selalu semangat untuk para peserta didik baru yang sudah diterima di SMKN 1 Purwosari, semoga kalian bisa dengan cepat beradaptasi di&nbsp;lingkungan baru dengan baik dan menjadi pelajar yang bisa dibanggakan untuk masa depan bangsa dan negara.</span></span></span></p>\n\n<p>&nbsp;</p>\n', 1, 'SMKN 1 Purwosari', 3, 13, 148, NULL, 1, NULL, '2024-03-03 17:17:01', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (487, 'MPLS Hari Pertama Tahun Ajaran 2023-2024', '', '1689662290.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Kegiatan pekan MPLS SMKN 1 Purwosari tahun ajaran 2023-2024 sudah resmi di buka yang diawali dengan upacara pembukaan MPLS yang diikuti oleh seluruh warga SMKN 1 Purwosari. Pada upacara pembukaan ini, dipimpin langsung oleh Bapak Kepala SMKN 1 Purwosari yaitu Bapak Rudi Trisantoso serta di lakukan penerimaan peserta didik baru secara simbolis.</span></span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Setelah upacara pembukaan selesai, maka dilanjutkan dengan kegiatan perkenalan Wali MPLS serta pemberian materi MPLS. Materi MPLS pada hari pertama ini mencakup materi Wawasan Wiyata Mandala yang disampaikan oleh Bapak Nur Cholis. Wawasan Wiyata Mandala adalah sikap menghargai dan bertanggung jawab terhadap lingkungan sekolah sebagai tempat menuntut ilmu pengetahuan.</span></span></span>&nbsp;<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:#000000\">Tujuan Wawasan Wiyata Mandala adalah diharapkan agar seluruh siswa dapat berperan aktif dalam meningkatkan fungsi sekolah sebagai lingkungan pendidikan</span>.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Materi selanjutnya yaitu Budi Pekerti dan Tata Krama yang disampaikan oleh Bapak Ainur Rofiq. Budi pekerti bisa diartikan dengan nilai yang ditanamkan kepada anak-anak sejak dalam asuhan orang tua dan dilanjutkan oleh guru di sekolah serta masyarakat dalam kehidupan sehari-hari. Sedangkan tata krama adalah aturan sopan santun dalam pergaulan.</span></span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">Materi yang terakhir yaitu Sosialisasi Kurikulum Merdeka yang disampaikan oleh Bapak M. Haryanto. Kurikulum Merdeka adalah kurikulum dengan pembelajaran intrakurikuler yang beragam di mana konten akan lebih optimal agar peserta didik memiliki cukup waktu untuk mendalami konsep dan menguatkan kompetensi. Guru memiliki keleluasaan untuk memilih berbagai perangkat ajar sehingga pembelajaran dapat disesuaikan dengan kebutuhan belajar dan minat peserta didik.</span></span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 13, 92, NULL, 1, NULL, '2024-03-01 03:58:02', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (488, 'SMKN 1 Purwosari Mengikuti Pembukaan MPLS Secara Daring Bersama Gubernur JATIM 2023', '', '1689666198.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Pada pembukaan MPLS 2023 di SMKN 1 Purwosari, selain melakukan secara <em>offline </em>juga melakukan secara daring<em> </em>bersama Ibu Gubernur Jawa Timur Khofifah Indar Parawansa. </span></span></span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">MPLS daring yang diadakan oleh Dinas Pendidikan Provinsi Jatim melibatkan ribuan siswa dan siswi dari berbagai sekolah di Jawa Timur salah satunya yaitu SMKN 1 Purwosari. Melalui platform Zoom, para siswa dan siswi dapat mengikuti serangkaian kegiatan yang dirancang untuk memberikan pemahaman tentang lingkungan sekolah, peraturan, dan kegiatan-kegiatan yang akan dijalankan selama tahun ajaran baru.</span></span></span></span></p>\n\n<p style=\"text-align:start\"><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Masa pengenalan lingkungan sekolah adalah momen penting bagi kalian untuk memahami nilai-nilai pendidikan dan tata tertib sekolah. Meskipun melalui platform daring, saya berharap kalian tetap bisa merasakan semangat kebersamaan dan kolaborasi dalam proses pembelajaran. Mari wujudkan sekolah ramah anak tanpa perundungan dan tanpa kekerasan untuk mencetak generasi cerdas dan berakhlak mulia, kata Gubernur Jatim Khofifah Indar Parawansa dalam sambutannya.</span></span></span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Dalam kesempatan tersebut, Gubernur Khofifah benar-benar memberikan atensi agar kejadian perundungan yang sebelumnya pernah terjadi, jangan sampai terulang. Begitupun untuk tahun-tahun yang akan datang. Seluruh siswa baru harus dibuat nyaman dengan kegiatan MPLS.</span></span></span></p>\n\n<p><span style=\"font-size:12pt\"><span style=\"background-color:white\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Dinas Pendidikan Provinsi Jatim dan Ibu Gubernur Jawa Timur berharap bahwa MPLS ini dapat menjadi awal yang baik bagi siswa dan siswi di Jawa Timur untuk menghadapi tantangan dan meraih prestasi di tahun ajaran baru. Dengan kolaborasi antara pemerintah daerah, sekolah,</span></span> <span style=\"font-size:14.0pt\"><span style=\"color:black\">dan orang tua, diharapkan pendidikan di Jawa Timur dapat terus berjalan dengan baik.</span></span></span></span></p>\n', 0, 'Aula SMKN 1 Purwosari', 3, 13, 66, NULL, 1, NULL, '2024-03-01 12:47:46', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (489, 'MPLS Hari Kedua Tahun Ajaran 2023-2024', '', '1689836878.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Kegiatan pekan MPLS SMKN 1 Purwosari tahun ajaran 2023-2024 telah memasuki hari kedua, seperti biasa sebelum serangkaian acara di mulai selalu di awali dengan Apel pagi. Apel pagi hari ini, dipimpin oleh Bapak Widayat.</span></span></span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Setelah apel pagi selesai, maka dilanjutkan dengan penyampaian materi tentang Tata Tertib Sekolah yang disampaikan oleh Bapak Achmad Taufiq. Tata tertib sekolah adalah sekumpulan peraturan sekolah yang harus dipatuhi oleh seluruh siswa demi kelancaran proses belajar mengajar. Tidak hanya itu, dengan adanya tata tertib diharapkan seluruh siswa dapat hidup disiplin di sekolah.</span></span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"> <span style=\"background-color:white\">Tata tertib sekolah memuat perintah dan larangan yang harus ditaati di lingkungan sekolah.</span></span></span></span></span></span></p>\n\n<p><br />\n<span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\"><span style=\"background-color:white\">Setelah penyampaian materi selesai, maka peserta didik baru yang didampingi oleh Wali MPLS dan OSIS diajak berkeliling untuk mengenal fasilitas sarana dan prasarana yang ada di SMKN 1 Purwosari. </span></span></span></span></span></span></p>\n\n<p>&nbsp;</p>\n', 0, 'SMKN 1 Purwosari', 3, 13, 58, NULL, 1, NULL, '2024-03-01 03:59:18', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (490, 'MPLS Hari Ketiga Tahun Ajaran 2023-2024', '', '1689906138.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Kegiatan pekan MPLS SMKN 1 Purwosari tahun ajaran 2023-2024 telah memasuki hari ketiga, seperti biasa sebelum serangkaian acara di mulai selalu di awali dengan Apel pagi. Apel pagi hari ini, dipimpin oleh Bapak Muhadi.</span></span></span></span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Setelah apel pagi selesai, maka dilanjutkan dengan pemnyampaian materi tentang <em>social emotional learning (green mindset, GEDSI, SEL)</em> yang disampaikan oleh Ibu Sri Mulyati. Pembelajaran sosial emosional atau dikenal juga dengan nama <em>social emotional learning (SEL)</em> adalah sebuah metode yang membantu siswa untuk mengembangkan pengetahuan, sikap, dan keterampilannya dalam mengelola emosi, membangun hubungan yang sehat, menetapkan tujuan, dan mengambil keputusan dalam hidupnya. Pembelajaran ini sangat penting diajarkan pada siswa sejak dini karena dapat mempengaruhi kesuksesannya di sekolah, pekerjaan, dan kehidupan sosial.</span></span></span></span></span></span><br />\n&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Setelah penyampaian materi selesai, agar tidak jenuh maka peserta didik baru dibentuk kelompok yang didampingi oleh anggota OSIS untuk membuat yel-yel. Kemudian yel-yel tersebut akan ditampilkan secara bergantian di lapangan indoor SMKN 1 Purwosari. Penampilan yel-yel ini membuat peserta didik baru sangat bersemangat dan <em>happy</em> sekali. </span></span></span></span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Setelah serangkaian acara selesai, maka dilanjutkan dengan acara penutupan MPLS yang di lakukan secara simbolis oleh Bapak Kadi selaku Wakil Kepala Sekolah Bidang Kesiswaan. </span></span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 13, 82, NULL, 1, NULL, '2024-03-01 03:59:22', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (491, 'Kegiatan Latihan Baris Berbaris (LBB) oleh Peserta Didik Baru Tahun Ajaran 2023-2024 ', '', '1690271076.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Pada tanggal 24-25 Juli 2023 telah dilaksanakan Latihan Baris Berbaris di SMKN 1 Purwosari yang dipandu oleh Polsek dan Koramil Purwosari.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Baris berbaris merupakan bentuk kedisiplinan dan juga merupakan latihan gerak dasar yang diwujudkan dalam rangka menanamkan nilai para peserta didik baru agar dapat menumbuhkan sikap :</span></span></span></span></span></p>\n\n<ul>\n	<li style=\"list-style-type:none\">\n	<ul style=\"list-style-type:disc\">\n		<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Disiplin pribadi maupun disiplin kelompok;</span></span></span></span></span></li>\n		<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Rasa tanggungjawab, kesatuan, dan persatuan;</span></span></span></span></span></li>\n		<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Kompak;</span></span></span></span></span></li>\n		<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Kebersamaan;</span></span></span></span></span></li>\n		<li><span style=\"font-size:11pt\"><span style=\"background-color:white\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"color:black\">Penampilan pribadi yang baik secara perorangan maupun kelompok.</span></span></span></span></span></li>\n	</ul>\n	</li>\n</ul>\n', 0, 'SMKN 1 Purwosari', 3, 13, 119, NULL, 1, NULL, '2024-03-04 00:16:59', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (492, 'Kegiatan Sosialisasi dari Wimus (Wirausaha Muda) di SMKN 1 Purwosari', '', '1690441923.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Wirausaha muda adalah mereka yang terjun melakukan usaha di usia yang terbilang cukup muda. Kebanyakan mereka berasal dari kategori anak muda/milenial. </span></span></span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Faktor munculnya wirausaha muda yaitu generasi milenial tidak gagap teknologi. Sebagaimana kita ketahui ketika membuka bisnis, agar bisnis tersebut bisa dijangkau secara cepat dan luas oleh berbagai kalanagan, maka kecakapan dalam menggunakan teknologi menjadi salah satu faktor keunggulan produk. Teknologi yang sangat dekat dengan milineal menjadikan hal tersebut bukan suatu tantangan, melaikan peluang nyata.</span></span></span></span></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"><span style=\"color:black\">Memanfaatkan sosial media sebagai sarana promosi telah menjadi salah satu langkah tepat yang digunakan generasi milenial dalam memanfaatkan teknologi. Teknologi tersebut mampu memotong biaya <em>advertising </em>(periklanan) yang harus dikeluarkan pelaku usaha. Wirausaha muda juga paham akan pasar anak muda. Jarak antara keduanya tidak terlalu jauh sehingga paham betul produk yang diinginkan generasi seumuran mereka serta cara memperkenalkan produknya. Generasi milenial cenderung tertarik kepada produk yang instant dan tidak suka produk yang dianggap ribet. &nbsp;</span></span></span></span></span></p>\n', 0, 'Aula SMKN 1 Purwosari', 3, 9, 95, NULL, 1, NULL, '2024-03-01 03:58:18', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (493, 'Kegiatan Istighosah dan Santunan Anak Yatim untuk Memperingati 10 Muharram 1445H di SMKN 1 Purwosari', '', '1690550549.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pada hari Jum&rsquo;at tanggal 28 Juli 2023 bertempat di lapangan indoor SMKN 1 Purwosari telah dilaksanakan kegiatan Istighosah dan Santunan Anak Yatim dalam rangka memperingati 10 Muharram 1445H. Kegiatan tersebut diikuti oleh seluruh warga sekolah SMKN 1 Purwosari.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Muharram adalah bulan pertama dalam penanggalan kalender Hijriyah atau kalender Islam.</span></span></span><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\"> <span style=\"background-color:white\">Bulan ini memiliki arti penting dalam Islam dan dihormati oleh umat muslim. Muharram adalah bulan yang dianggap suci dan dihormati. Selama bulan ini, umat muslim dianjurkan untuk melakukan amalan-amalan baik, memperbanyak ibadah, dan memperdalam nilai-nilai agama.</span></span></span><br />\n<br />\n<span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Oleh karena itu, hampir setiap tahun di SMKN 1 Purwosari selalu diadakan kegiatan Santunan Anak Yatim yang diberikan terhadap beberapa anak yatim di sekitar lingkungan sekolah. Santunan ini diperoleh dari sumbangan seluruh warga sekolah SMKN 1 Purwosari. </span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Alhamdulillah kami ucapkan terima kasih yang sebesar-besarnya, jazakumullah khairan katsir kepada seluruh warga sekolah SMKN 1 Purwosari. Semoga Allah SWT membalas amal jariyah dengan pahala serta keberkahan kepada semua. Teriring do&rsquo;a semoga kita semua senantiasa sehat, panjang umur, serta dikabulkan hajat hidupnya atas wasilah sedekah yang telah diberikan, Aamiin.</span></span></span></span></p>\n', 0, 'Lapangan Indoor SMKN 1 Purwosari', 3, 11, 61, NULL, 1, NULL, '2024-03-03 07:02:50', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (494, 'Kegiatan BIMTEK New Kurikulum Daihatsu Fase F di SMKN 1 Purwosari', '', '1690766393.jpg', '', NULL, NULL, '<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kegiatan Bimtek Pintar Bersama Daihatsu ini meliputi wilayah Jawa Timur, Bali dan NTB yang di selenggarakan selama 3 hari mulai tanggal 28-30 Juli 2023 di SMKN 1 Purwosari. Bimtek ini bertujuan untuk menstandarisasi implementasi kurikulum merdeka SMK binaan sesuai dengan kebutuhan industri otomotif.</span></span></span></p>\n\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Salah satu kegiatan <em>Coporate Social Responsibility</em> (CSR) atau Tanggung Jawab Sosial Perusahaan yang gencar dijalankan oleh PT Astra Daihatsu Motor (ADM) adalah &ldquo;Pintar Bersama Daihatsu&rdquo;. Aktivitas ini dijalankan untuk membantu dunia pendidikan Indonesia, khususnya SMK Jurusan Otomotif untuk meningkatkan mutu guru dan lulusannya.&nbsp;</span></span></span></p>\n\n<p><span style=\"color:#000000\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Acara ini diselenggarakan sebagai apresiasi dan tukar pendapat tentang kurikulum otomotif yang diprakarsai oleh Daihatsu dan telah diterapkan di beberapa SMK. Kurikulum tersebut bertujuan untuk meningkatkan kualitas lulusan SMK sehingga makin mudah memasuki dunia kerja. Begitu mereka masuk ke dunia kerja, tidak lagi terjadi kesenjangan yang besar. Pasalnya, mereka telah memperoleh pelajaran tentang dunia otomotif sesuai dengan standar Daihatsu.</span></span></span><br />\n&nbsp;</p>\n', 1, 'Aula SMKN 1 Purwosari', 3, 9, 102, NULL, 1, NULL, '2024-03-03 23:36:21', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (495, 'Program Upskilling dan Reskilling Guru Kejuruan Berstandar Industri Pola SMK PK Ã¢â‚¬â€œ Magang Indu', '', '1691394557.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Dalam rangka meningkatkan kompetensi guru Sekolah Menengah Kejuruan sehingga dapat mengikuti perkembangan teknologi dan pemanfaatannya di dunia kerja, maka Balai Besar Pengembangan Penjaminan Mutu Pendidikan Vokasi Bidang Otomotif dan Elektronika (BBPPMPV BOE) melaksanakan Program Upskilling dan Reskilling Guru Kejuruan Berstandar Industri pola SMK Pusat keunggulan - Magang Industri. </span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">SMKN 1 Purwosari sebagai salah satu SMK Pusat Keunngulan diberi kepercayaan sebagai tempat pelatihan Upskilling dan Reskilling secara Luring yang dilaksanakan pada tanggal 07-12 Agustus 2023. Sedangkan untuk magang industri yang dilaksanakan pada tanggal 14-19 Agustus 2023 dan sertifikasi/uji industri dilaksankan pada tanggal 21-23 Agustus 2023 di industri yang merupakan salah satu mitra SMKN 1 Purwosari yaitu PT. Hummatech Digital Indonesia .</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:12.0pt\">Adapun judul pelatihan Upskilling dan Reskilling ini adalah Pemograman Mobile APP dengan menggunakan Flutter. Pemograman mobile merupakan suatu proses pembuatan aplikasi pada perangkat mobile. Sedangkan flutter adalah sebuah framework open source yang dikembangkan oleh google untuk membuat aplikasi mobile android dan IOS dengan satu basis code. Flutter dirancang untuk memberi kemudahan kepada para programmer mobile dalam membangun aplikasi mobile multi platform. Maka dengan menggunakan flutter kita cukup koding satu kali sudah dapat membuat aplikasi android dan IOS sekaligus. Flutter hadir dengan memiliki beberapa keunggulan yaitu performa yang seperti aplikasi native, material design yang sangat fleksibel dan menarik, dan proses development yang cepat dengan fitur <em>hot reload </em>(menampilkan hasil koding dengan kilat). </span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 161, NULL, 1, NULL, '2024-03-03 01:47:52', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (496, 'Kegiatan Recruitment Alumni SMKN 1 Purwosari oleh PT. Mayora Indah Tbk.', '', '1692062687.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">PT. Mayora Indah tbk didirikan pada tahun 1977 dengan lokasi pabrik pertama berada di Tangerang. Seiring berjalannya waktu, perusahaan ini semakin berkembang cukup pesat. PT. Mayora Indah tbk ini menghasilkan dua produk utama berupa makanan dan minuman olahan. Namun dalam pelaksanaan kegiatan produksinya, PT. Mayora Indah tbk membagi produk-produk yang dihasilkannya dalam 6 divisi yakni divisi biskuit, divisi permen, divisi wafer, divisi coklat, divisi kopi, dan divisi minuman sehat.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Dengan semakin pesatnya perkembangan PT. Mayora Indah tbk ini, maka perusahaan ini mendirikan salah satu pabrik baru yang berlokasi di Purwosari Pasuruan. Sehingga PT. Mayora Indah tbk mengadakan recruitment untuk mengisi pabrik baru ini, salah satu yang dipercaya untuk mengisi adalah para Alumni SMKN 1 Purwosari. Kepercayaan ini diberikan karena sudah terjalinnya kerja sama yang baik dengan PT. Mayora Indah tbk dengan SMKN 1 Purwosari.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pelaksanaan kegiatan recruitment batch 5 ini sendiri dilaksanakan di aula SMKN 1 Purwosari di mulai pada tanggal 14 sampai 16 Agustus 2023. Recruitment ini diikuti para Alumni SMKN 1 Purwosari sebanyak 170 peserta serta dihadiri oleh Bapak Antony selaku perwakilan dari PT. Mayora Indah Tbk. Semoga kerja sama ini selalu terjalin dengan baik dan sukses selalu untuk para Alumni SMKN 1 Purwosari.</span></span></span></span></p>\n', 1, 'Aula SMKN 1 Purwosari', 3, 9, 146, NULL, 1, NULL, '2024-03-03 03:18:20', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (497, 'Kegiatan Upacara Bendera 17 Agustus 2023 di SMKN 1 Purwosari', '', '1692326868.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Kamis, 17 Agustus 2023 tepatnya di lapangan indoor SMKN 1 Purwosari telah dilaksanakan upacara bendera dalam rangka memperingati HUT RI ke-78. Kegiatan ini merupakan salah satu agenda tahunan yang dilaksanakan tepat pada tanggal 17 Agustus yang diikuti oleh seluruh warga SMKN 1 Purwosari. Upacara bendera berjalan dengan lancar dan khitmad.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Proklamasi kemerdekaan Republik Indonesia yang diperingati setiap tanggal 17 Agustus merupakan tonggak sejarah berdirinya negara kita tercinta. Oleh karena itu, setiap siswa hendaknya tahu apa makna proklamasi bagi pelajar agar semakin tumbuh rasa cinta terhadap tanah air. Tanpa adanya proklamasi tidak mungkin siswa bisa bebas belajar dan mewujudkan cita-citanya seperti saat ini.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:16.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Sebagai siswa yang nantinya akan menjadi generasi penerus bangsa, mengetahui pentingnya arti serta makna proklamasi kemerdekaan akan menambah semangat untuk belajar. Siswa akan memahami bahwa belajar adalah bentuk perjuangan di masa sekarang ini agar bisa menjadi pemimpin yang tangguh di masa depan.</span></span></span></span></p>\n', 0, 'Lapangan Indoor SMKN 1 Purwosari', 3, 9, 93, NULL, 1, NULL, '2024-03-03 19:46:05', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (498, 'Siswa SMKN 1 Purwosari Menjadi Juara 1 Lomba Pemilihan Duta Baca Kabupaten Pasuruan Tahun 2023', '', '1692838061.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Dinas Perpustakaan dan Kearsipan Kabupaten Pasuruan berkolaborasi dengan Dinas Pendidikan dan Kebudayaan mengadakan lomba bergengsi yang bertujuan meningkatkan pemahaman literasi serta membumikan kegemaran membaca khususnya di kalangan remaja dan masyarakat di Kabupaten Pasuruan secara umum.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Peran duta baca dalam peningkatan Literasi, kreativitas, dan Inovasi dalam mewujudkan generasi muda berintegritas menuju Kabupaten Pasuruan yang Maslahat sangatlah penting. Literasi digital sangat relevan di zaman sekarang, kolaborasi antara teknologi dan literasi sangat memungkinkan untuk mewujudkan peningkatan tersebut.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Diikuti oleh sekitar 23 SMA/SMK/MA/sederajat di Kabupaten Pasuruan, SMKN 1 Purwosari turut serta berpartisipasi dalam lomba tersebut. Bertempat di Kompleks Perkantoran Pemkab Pasuruan. Rabu, 23 Agustus 2023 lagi-lagi SMKN 1 Purwosari menorehkan prestasi. Pembawaan dengan penuh rasa percaya diri, penguasaan materi yang sangat matang serta inovasi yang disajikan mengatarkan Dharmazka Arsyadani siswa kelas XI RPL SMKN 1 Purwosari yang merupakan satu-satunya peserta laki-laki berhasil meraih juara 1 Lomba Pemilihan Duta Baca Kabupaten Pasuruan Tahun 2023, dengan judul artikel &ldquo;PLATFORM APLIKASI DIGITAL HADIRKAN KEMASLAHATAN&rdquo;.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Rasa bangga yang teramat luar biasa dari kami keluarga besar SMKN 1 Purwosari semoga bisa menjadi motivasi bagi siswa-siswa yang lain.</span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 166, NULL, 1, NULL, '2024-03-03 06:13:07', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (499, 'Pelaksanaan ANBK Tahun Ajaran 2023-2024 di SMKN 1 Purwosari', '', '1693448861.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:#fefefe\"><span style=\"color:black\">Ujian Nasional Berbasis Komputer (UNBK) telah digantikan oleh Asesmen Nasional Berbasis Komputer (ANBK</span></span><span style=\"color:black\"><span style=\"background-color:#fefefe\">). Kerap disebut Asesmen Nasional, ANBK adalah program evaluasi yang diselenggarakan oleh Kementerian Pendidikan, Kebudayaan, Riset, dan Teknologi (Kemendikbudristek). ANBK bertujuan untuk meningkatkan mutu pendidikan dengan memotret input, proses, dan output pembelajaran di seluruh satuan pendidikan. Asesmen Nasional dilaksanakan dengan tiga instrumen, yaitu Asesmen Kompetensi Minimum (AKM) Literasi dan Numerasi, Survei Karakter, dan Survei Lingkungan Belajar.</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:#fefefe\"><span style=\"color:black\">AKM berfungsi untuk mengukur literasi membaca dan literasi matematika atau numerasi peserta didik. Sedangkan, Survei Karakter mengukur sikap, nilai, keyakinan, dan kebiasaan yang mencerminkan karakter murid. Survei Lingkungan Belajar mengukur kualitas berbagai aspek input dan proses belajar-mengajar di kelas maupun di tingkat satuan pendidikan.</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:#fefefe\"><span style=\"color:black\">Asesmen Nasional tidak menentukan kelulusan seorang peserta didik, yang tetap menjadi kewenangan pendidik dan satuan pendidikan. Asesmen juga tidak digunakan untuk menilai peserta didik yang menjadi peserta asesmen. Hasil ANBK tidak akan memuat skor atau nilai peserta didik secara individual. Melainkan, hasil diharapkan menjadi dasar dilakukannya perbaikan pembelajaran. Program evaluasi ini diikuti oleh seluruh satuan pendidikan tingkat dasar dan menengah di Indonesia, serta program kesetaraan yang dikelola oleh Pusat Kegiatan Belajar Masyarakat (PKBM).&nbsp;</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:#fefefe\"><span style=\"color:black\">SMKN 1 Purwosari sendiri telah melaksanakan ANBK pada tanggal 28-29 Agustus 2023 yang diikuti oleh kurang lebih 90 peserta didik yang dipilih secara sampling dan dibagi menjadi 3 sesi. Dalam pelaksanaanya ANBK di SMKN 1 Purwosari berjalan dengan lancar dan dihadiri oleh pengawas dari sekolah lain.</span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 110, NULL, 1, NULL, '2024-03-01 03:59:07', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (500, 'Pemilihan Ketua dan Wakil Ketua OSIS Periode 2023-2024', '', '1693530778.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#212529\">Pemilihan Ketua dan Wakil ketua OSIS ini adalah wujud implementasi pembelajaran demokrasi di sekolah. Pada tanggal 29 Agustus 2023 bertempat di lapangan indoor SMKN 1 Purwosari telah menyelenggarakan pemilihan Ketua dan Wakil Ketua OSIS SMKN 1 Purwosari Periode 2023-2024. Sebelum proses pemilihan berlangsung, kepada masing-masing kandidat diberikan kesempatan untuk menyampaikan Visi dan Misi serta Program Kerjanya melalui kampanye terbuka. Selain itu, juga terdapat sesi tanya jawab yang dilakukan oleh juri serta para siswa kepada para kandidat. Adapun kandidat yang mencalonkan yaitu.</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#212529\">1. Pasangan kandidat pertama atas nama Utsha Abi Hurairo kelas (XI MKT 2) dan M. Ridho Pangestu kelas (X APHP 1)</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#212529\">2. Pasangan kandidat kedua atas nama Dharmazka Arsyadani kelas (XI RPL) dan Rengga Saputra (X TKR 2)</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:#212529\">Dalam melakukan pemilihan Ketua dan Wakil Ketua OSIS ini sudah memanfaat teknologi yang ada yaitu dengan menggunakan google forms sehingga untuk merekap hasil pemungutan suara tidak perlu dihitung secara manual dan bisa diketahui secara cepat hasilnya. Berdasarkan hasilnya terdapat 1686 suara yang masuk dengan rincian pasangan kandidat nomor satu memperoleh 935 suara dan pasangan kandidat nomor dua memperoleh 751 suara. Dengan demikian terpilihlah pasangan kandidat nomor satu atas nama Utsha Abi Hurairo kelas (XI MKT 2) sebagai Ketua OSIS dan M. Ridho Pangestu kelas (X APHP 1) sebagai Wakil Ketua OSIS SMKN 1 Purwosari periode 2023-2024. </span></span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 177, NULL, 1, NULL, '2024-03-04 00:38:26', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (501, 'Pelaksanaan Survei Lingkungan Belajar Tahun 2023', '', '1694574532.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Survei Lingkungan Belajar adalah alat ukur yang digunakan untuk mengevaluasi dan memetakan aspek pendukung kualitas pembelajaran di lingkungan satuan pendidikan.&nbsp;Hasil dari Informasi yang diperoleh pada survei lingkungan belajar adalah tentang faktor-faktor dari aspek input dan proses pembelajaran yang berpotensi mempengaruhi hasil belajar murid.&nbsp;</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Satuan Pendidikan bisa dikatakan baik, jika satuan pendidikan tersebut mampu memfasilitasi belajar murid melalui beberapa hal berikut:&nbsp;</span></span></span></span></p>\n\n<ol>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">proses pembelajaran yang berkualitas;&nbsp;</span></span></span></span></li>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">guru-guru yang secara konsisten melakukan refleksi dan memperbaiki praktik pengajarannya;&nbsp;</span></span></span></span></li>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">kepala satuan pendidikan yang menerapkan visi, kebijakan, dan program yang berfokus pada kualitas pembelajaran, dan&nbsp;</span></span></span></span></li>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">iklim satuan pendidikan yang aman, menghargai keragaman dan inklusif.&nbsp;</span></span></span></span></li>\n</ol>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dalam Survei Lingkungan Belajar mencakup sembilan dimensi yang diasumsikan mempengaruhi hasil belajar murid:</span></span></span></span></p>\n\n<ol>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:black\">Latar belakang sosial-ekonomi murid</span></em></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Murid dengan kondisi sosial-ekonomi yang berbeda memiliki hak yang sama dalam mengakses dan memperoleh layanan pendidikan yang berkualitas, seperti tingkat pendidikan orang tua dan fasilitas belajar yang tersedia di rumah.</span></span></span></span></p>\n\n<ol start=\"2\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><em><span style=\"color:black\">Kualitas pembelajaran di kelas</span></em></span></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Seluruh kegiatan belajar mengajar di kelas, mencakup indikator manajemen kelas, dukungan afektif, pembelajaran interaktif dan penyesuaian cara mengajar dengan tingkat kemampuan murid.</span></span></span></span></p>\n\n<ol start=\"3\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><em><span style=\"color:black\">Refleksi dan perbaikan pembelajaran oleh guru</span></em></span></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Kemampuan pengembangan guru untuk terus meningkatkan kompetensi melalui belajar mandiri dengan merefleksi praktik pengajaran yang telah diterapkan dan juga belajar dari rekan guru.</span></span></span></span></p>\n\n<ol start=\"4\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><em><span style=\"color:black\">Kepemimpinan instruksional</span></em></span></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Kemampuan kepala satuan pendidikan dalam menyusun dan mengkomunikasikan visi, misi, program, dan kebijakan yang mendukung guru dalam meningkatkan mutu pembelajaran di satuan pendidikan.</span></span></span></span></p>\n\n<ol start=\"5\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><em><span style=\"color:black\">Iklim keamanan di satuan pendidikan</span></em></span></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">satuan pendidikan yang memiliki kebijakan, pemahaman, dan program terkait perundungan, hukuman fisik, kekerasan seksual dan narkotika sehingga memberikan perlindungan dan rasa aman bagi warga satuan pendidikan, baik secara fisik maupun psikologis.</span></span></span></span></p>\n\n<ol start=\"6\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:black\">Iklim kebinekaan di satuan pendidikan</span></em></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">lingkungan satuan pendidikan yang menghargai keragaman agama maupun sosial-budaya dan dukungan kesetaraan hak.</span></span></span></span></p>\n\n<ol start=\"7\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:black\">Iklim kesetaraan gender</span></em></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Bagaimana lingkungan satuan pendidikan berperilaku adil, memberikan kesempatan yang sama bagi warga satuan pendidikan, baik laki-laki maupun perempuan dalam menjalankan peran publik.seperti dukungan kepala satuan pendidikan dan guru atas kesetaraan gender.</span></span></span></span></p>\n\n<ol start=\"8\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:#222222\">Iklim inklusivitas</span></em></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Pengetahuan, penerimaan dan dukungan guru terhadap murid dengan disabilitas serta murid cerdas istimewa dan murid bakat istimewa.</span></span></span></span></p>\n\n<ol start=\"9\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em><span style=\"color:black\">Dukungan orangtua dan murid terhadap program satuan pendidikan</span></em></span></span></li>\n</ol>\n\n<p style=\"margin-left:36px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">partisipasi orangtua dalam kegiatan satuan pendidikan, dan partisipasi murid dalam penyusunan program satuan pendidikan.</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Manfaat dari hasil survei lingkungan belajar :</span></span></p>\n\n<ol>\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Bagi Pemerintah Daerah/Dinas Pendidikan/Kantor Kemenag</span></span></span></li>\n</ol>\n\n<p style=\"margin-left:48px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dapat memperoleh potret mutu satuan pendidikan di wilayahnya yang kemudian digunakan sebagai bahan kebijakan dalam mengevaluasi sistem pendidikan.</span></span></span></span></p>\n\n<ol start=\"2\">\n	<li style=\"text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Bagi Kepala Satuan Pendidikan</span></span></span></li>\n</ol>\n\n<p style=\"margin-left:48px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dapat memperoleh potret mutu satuan pendidikan secara utuh dari input, proses dan hasil, guna peningkatan hasil mutu pendidikan.</span></span></span></span></p>\n\n<ol start=\"3\">\n	<li><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"color:black\">Bagi Guru</span></span></span></li>\n</ol>\n\n<p style=\"margin-left:48px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dapat mengetahui berbagai aspek pendukung suasana lingkungan belajar yang lebih komprehensif.</span></span></span></span></p>\n\n<ol start=\"4\">\n	<li style=\"text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Bagi Murid</span></span></span></span></li>\n</ol>\n\n<p style=\"margin-left:48px; text-align:start\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dapat memperoleh informasi rapor dan profil satuan pendidikan.</span></span></span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"background-color:white\"><span style=\"color:black\">Dari seluruh manfaat yang dirasakan oleh kepala satuan pendidikan, guru maupun peserta didik pada dasarnya diharapkan dapat menciptakan suasana lingkungan belajar mengajar yang aman, nyaman dan menyenangkan.</span></span></span></span></p>\n\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:11pt\"><strong><span style=\"font-size:12.0pt\"><span style=\"background-color:white\"><span style=\"color:black\">(Sumber : Survei Lingkungan Belajar. (03 April 2022). Diakses pada 13 September 2023 dari <em>https://pusmendik.kemdikbud.go.id/an/page/news_detail/survei-lingkungan-belajar)</em></span></span></span></strong></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 119, NULL, 1, NULL, '2024-03-01 03:59:43', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (502, 'Tim Futsal SMKN 1 Purwosari Meraih Juara 1 dalam Kompetisi Se Kabupaten Pasuruan dan Mojokerto', '', '1695006948.jpeg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Mojokerto, 17 September 2023 - Pekan Olahraga Kabupaten Mojokerto tahun ini menyuguhkan persaingan sengit dalam Kompetisi Futsal Tingkat Pelajar SMA/SMK/MA se-Kabupaten Mojokerto dan Pasuruan. Acara ini, yang diadakan oleh Pekan Olahraga Kabupaten Mojokerto, berlangsung mulai tanggal 10-19 September 2023. Kompetisi ini melibatkan total 23 sekolah, yang masing-masing mengirimkan tim terbaiknya.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Salah satu yang mencuri perhatian adalah keberhasilan SMK Negeri 1 Purwosari yang meraih prestasi gemilang dalam kompetisi ini. Tim futsal dari sekolah ini tampil luar biasa, dan keberhasilan mereka tidak terlepas dari kerja keras, disiplin, dan semangat juang yang tinggi. Tim ini dikenal memiliki kekompakan yang luar biasa, dan itulah salah satu kunci kesuksesan mereka.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dalam daftar pemain mereka, terdapat sepuluh pemain berbakat yang telah menunjukkan performa luar biasa sepanjang turnamen. Berikut adalah daftar para pemain tersebut beserta kelas mereka:</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">1. Ferry Ade Yusuf (12 TPM 2)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. Zahriel Arbian E (12 TPM 2)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3. Adrian Dwi Firmansyah (12 TKR 1)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">4. Krisna Aditya P (12 TPM 2)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">5. Novirza Ramadhan (12 TKR 1)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6. Agung Kuncoro (12 MKT)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">7. Bian Adi Putra (10 TKR)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">8. A. Daniel Baihaky (11 TKR)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">9. M. Dhikri M (12 MKT)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">10. Moch. Emir Hakim Izzuddin (11 TPM 1)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pak Huda, pembina tim dari SMK Negeri 1 Purwosari, mengatakan, &ldquo;Yang jelas, dengan usaha gigih dan terus berlatih serta tim yang solid, mampu menghasilkan permainan yang luar biasa.&rdquo;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kemenangan mereka bukan hanya tentang keterampilan dalam bermain futsal, tetapi juga tentang kerja tim yang kuat dan dedikasi yang tak tergoyahkan. Tim SMK Negeri 1 Purwosari berhasil mengalahkan lawan-lawannya dengan permainan yang brilian, menciptakan momen-momen magis dan menegangkan di lapangan.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">kemenangan gemilang ini, SMK Negeri 1 Purwosari membuktikan bahwa dengan kerja keras, konsistensi, dedikasi, dan semangat juang, impian besar dapat tercapai. Ini adalah prestasi yang luar biasa, dan kami berharap mereka terus menginspirasi para Siswa lainnya untuk terus mencetak prestasi dan semakin mengharumkan nama sekolah. Selamat kepada SMK Negeri 1 Purwosari atas prestasi gemilang ini!</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 356, NULL, 1, NULL, '2024-03-01 04:00:06', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (503, 'Kegiatan Guru Tamu Oleh Biznet Network Pasuruan yang Diikuti Siswa Siswi XII TKJ SMKN 1 Purwosari', '', '1695352520.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pada hari Selasa, 19 September 2023 bertempat di aula SMK Negeri 1 Purwosari telah dilaksanakan kegiatan Guru Tamu dari Biznet Network Pasuruan. Kegiatan ini diikuti oleh siswa siswi kelas XII Kompetensi Keahlian Teknik Komputer Jaringan dengan materi Pengenalan dan Pembelajaran Fiber Optik.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pembelajaran fiber optik adalah pembelajaran yang mempelajari tentang teknologi transmisi data menggunakan cahaya melalui kabel serat optik. Fiber optik memiliki keunggulan seperti kecepatan, kapasitas, dan keamanan yang tinggi. Fiber optik digunakan dalam berbagai aplikasi seperti telekomunikasi, internet, televisi, dan jaringan komputer.untuk belajar fiber optik, kita perlu mengetahui komponen-komponen fiber optik, cara penyambungan atau terminasi konektor , dan alat-alat yang dibutuhkan.</span></span></p>\n', 0, 'Aula SMKN 1 Purwosari', 3, 9, 100, NULL, 1, 1, '2024-03-04 03:37:31', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (504, 'Assement SMK Bisa oleh Assesor dari Astra Internasional (Kamis, 19 Oktober 2023)', '', '1697716663.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Apresiasi SMK BISA 2023, diselenggarakan PT. ASTRA Internasional melalui program Rejuvinasi Kriteria SMK BISA Astra. Bekerja sama dengan Kementerian Pendidikan dan Kebudayaan&nbsp; serta Kementerian Perindustrian Republik Indonesia,&nbsp; event ini diikuti oleh 20 SMK binaan PT. ASTRA melalui anak perusahaannya di seluruh Indonesia. Keikutsertaan SMK Negeri 1 Purwosari dalam apresiasi ini selaku SMK binaan dari PT. Astra Daihatsu Motor melalui Program Pintar Bersama Daihatsu. </span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Hadir dalam Cross Assement SMK Bisa, Bapak Djunaedi Syarif dari PT Astra Honda Motor selaku assesor, Bapak Lukman Hakim selaku Ketua Korwil PBD(Pintar Bersama Daihatsu) wilayah Jatim Bali dan Nusa Tenggara Barat, dan Bapak Ahmad Shofi selaku perwakilan dari PT. Jolo Daihatsu Malang.&nbsp; Asesmen meliputi status sekolah, Budaya Industri, Link and Match serta Beyond Compliance.</span></span></span></span></p>\n', 0, 'SMKN 1 Purwosari', 3, 9, 52, NULL, 1, NULL, '2024-03-01 03:56:06', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (505, 'Juara 3 LKS Nasional XXXI Bidang Lomba CNC Turning', '', '1698444127.jpeg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Dunia pendidikan mengadakan lomba bergengsi yaitu Lomba Kompetensi Siswa Sekolah Menengah Kejuruan (LKS &ndash; SMK) Tingkat Nasional. Pada tahun ini, Surabaya menjadi tempat dilaksanakannya LKS Nasional Tahun 2023 yang dimulai tanggal 23 - 28 Oktober 2023. Seluruh peserta terbaik dari masing-masing daerah unjuk keahlian dan keterampilan dalam berbagai bidang lomba. Penyelenggaraan LKS Nasional ini mencakup 37 cabang lomba, dengan 6 area kategori salah satunya adalah Kelompok Teknologi Manufaktur dan Rekayasa. M. Rizky Arbi siswa SMK Negeri 1 Purwosari dari jurusan Teknik Permesinan (TPM) menjadi salah satu peserta pada Kelompok Teknologi Manufaktur dan Rekayasa dalam cabang lomba CNC Turning.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pencapaian yang sangat luar biasa tentunya dicapai dengan proses yang luar biasa pula. Dalam cabang lomba CNC Turning, ada beberapa hal yang menjadi penilaian. Pertama, kemampuan peserta dalam membaca dan memahami gambar kerja. Kedua, kemampuan dalam membuat dan menyusun program pada mesin CNC sesuai dengan produk atau benda yang dijadikan. Ketiga, kemampuan peserta dalam mengoperasikan mesin CNC Turning. Keempat, kemampuan peserta dalam melakukan <em>quality control</em> terhadap benda yang dihasilkan atau diproduksi.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Selama lomba berlangsung, kendala yang ditemui adalah <em>software </em>yang digunakan, peralatan serta mesin yang baru membutuhkan familiarisasi dan adaptasi yang cepat. Tapi hal itu tidak menyurutkan semangat Arbi untuk menyerah. &ldquo;Selalu giat berlatih dan terus berlatih, banyak melakukan uji coba dan menanamkan untuk selalu cermat dan teliti dalam bekerja mengoperasikan mesin sesuai SOP. Itu&nbsp; yang selalu saya sampaikan&nbsp; ke Arbi agar ia mampu mencapai materi lomba&rdquo;, ujar Pak Julianto selaku pembimbing.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Setiap peserta ingin yang terbaik dengan membawa nama daerah dan instansi sekolahnya masing-masing. &ldquo;Perasaan saat lomba biasa saja, ya grogi itu wajar tapi masih tergolong biasa tidak yang berlebihan yang terpenting pekerjaan selesai sesuai waktu dan target&rdquo;, ucap Arbi,&nbsp; menjelaskan perasaan saat lomba. Hal tersebut sudah terlihat sejak awal bahwa Arbi memang siap dan layak menjadi yang terbaik dalam bidangnya. &ldquo;Potensi Arbi sangat besar hal ini sudah tampak sejak dilakukan seleksi di tingkah sekolah, tingkat kabupaten dan hingga saat ini potensi itu masih bisa untuk terus dikembangkan&rdquo;, ujar Pak Julianto.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Pak Rudi Trisantoso, selaku Kepala SMK Negeri 1 Purwosari juga tak henti-hentinya bangga atas apa yang telah diraih oleh anak didiknya. &ldquo;Tentunya selaku Kepala Sekolah sangat bangga dengan pencapaian yang sudah diraih oleh Arbi. Mudah-mudahan bisa menginspirasi adik-adik kelasnya pada LKS berikutnya. Prestasi yang diraih Arbi juga bisa menjadi jembatan untuk meraih masa depannya, karena juara 1, 2, 3 pada LKS Nasional akan banyak mendapat&nbsp; penawaran beasiswa maupun kerja&rdquo;, ujar Pak Rudi.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Hal serupa juga disampaikan oleh Pak Julianto selaku pembimbing bahwa setelah mendapat juara 3 dan LKS Nasional ini, Arbi tetap semangat dan giat belajar di manapun dan kapanpun. Tetap rendah hati dan harus terus berkembang. Maksimalkan potensi yang ada pada diri, berproseslah terus dan selalu berbagi kebaikan.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Keberhasilan Arbi dalam LKS Nasional Tahun 2023 ini menambah harapan dan motivasi yang banyak terkhusus untuk Arbi dan adik kelasnya. &ldquo;Harapan saya semoga apa yang saya dapat ini bisa membantu saya ke depannya dan juga menginspirasi teman-teman yang maasih sekolah untuk lebih serius dan giat dalam mengejar prestasi. Untuk adik kelas,&nbsp; semoga kalian bisa melampaui prestasi kakak kelasnya dalam Lomba Kompetensi Siswa&nbsp; di tahun berikutnya&rdquo; ucap Arbi.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kerjasama &nbsp;antara peserta, pembimbing, kepala sekolah dan dukungan dari seluruh pihak akan menambah keyakinan atas apa yang akan dilalui. Kebanggaan dan prestasi yang terus ditorehkan peserta didik SMK Negeri 1 Purwosari membuktikan bahwa begitu banyak bibit-bibit unggul yang dilahirkan dan dibesarkan melalui sekolah tercinta ini.</span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>Selamat untuk M. Rizky Arbi Rabbani atas diraihkan Juara 3 LKS Nasional dan Pak Julianto, S.Pd selaku pembimbing.</strong></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>Kerja keras dan kegigihan akan membuahkan hasil yang luar biasa. Kami keluarga besar SMK Negeri 1 Purwosari bangga!</strong></span></span></p>\n', 1, 'SMKN 5 Surabaya', 3, 10, 131, NULL, 1, NULL, '2024-03-04 06:12:52', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (506, 'TUNJUKKAN SEMANGAT PEMUDA BANGSA,  KEBERAGAMAN BUDAYA BERBAUR DALAM PERINGATAN SUMPAH PEMUDA', '', '1698757976.jpeg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Purwosari&nbsp; &ndash; Sumpah Pemuda adalah satu tonggak utama dalam sejarah pergerakan kemerdekaan Indonesia. Makna sumpah pemuda itu sendiri mempunyai inti persatuan yang sesuai dengan semboyan bangsa dan sejarah sumpah pemuda itu sendiri yang terdiri dari pemuda berbagai daerah. Senin (30/10), peringatan Sumpah Pemuda di SMKN 1 Purwosari memiliki banyak kegiatan, di antaranya lomba <em>fashion show </em>dan cerdas cermat. Selain itu, bebarengan dengan peringatan Bulan Bahasa dengan mengadakan lomba poster, pidato bahasa Jawa, musikalisasi puisi, dan <em>news reading</em> sebagai satu rangkaian peringatan hari besar.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Upacara peringatan Sumpah Pemuda ke-95 berjalan secara khidmat. Seluruh peserta dengan pakaian adat yang digunakan menambah anggun dan menakjubkan akan persatuan yang ada di SMKN 1 Purwosari sebagai wujud keberagaman di Indonesia. Semangat juang dan toleransi tergambar jelas dalam kegiatan peringatan Sumpah Pemuda. Penghargaan atas pakaian adat terbaik juga tidak luput dari perhatian seluruh warga sekolah.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><em>Fashion show </em>atau peragaan busana adat adalah kegiatan yang paling ditunggu-tunggu. Wadah bagi peserta didik untuk dapat mengasah kemampuan dan bakat yang dimiliki. Penampilan pakaian adat dari masing-masing kelas terasa begitu kompetitif. Masing-masing kelas mengirim perwakilan terbaik dengan menampilkan pakaian adat yang terbaik pula. Sepanjang peragaan, kita disuguhkan oleh semangat para pemuda dengan pesona budaya yang ada di Indonesia. Bersyukur karena keberagaman ini masih dapat dinikmati dan dilestarikan oleh pemuda bangsa yang ada di SMKN 1 Purwosari.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Selain itu, kegiatan lomba cerdas cermat juga tak kalah mendebarkan dan penuh persaingan. Lomba cerdas cermat pada dasarnya adalah sebuah kompetisi yangmenguji pengetahuan dan kemampuan berpikir bagi peserta yang turut berpartisipasi. Lomba Cerdas Cermat terdiri dari babak penyisihan, semifinal, dan final. Babak penyisihan dilaksanakan Senin (30/10), sedangkan pada babak semifinal dan final dilaksanakan pada tanggal 31 Oktober 2023 dengan tempat yang sama yaitu Aula SMKN 1 Purwosari.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Diadakannya lomba cerdas cermat banyak memberi manfaat dan kesan bagi peserta. &quot;Ini salah satu jembatan bagus<em> sih</em> bagi kita pemuda agar semangat lagi belajar tentang wawasan kebangsaan. Adanya lomba cerdas cermat ini bisa menguji dan meningkatkan kemampuan berpikir kritis dan analisis siswa/ siswi secara cepat dan tepat&rdquo;, ujar salah satu peserta cerdas cermat dari kelas XII DKV 2.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\">Kemeriahan peringatan Hari Sumpah Pemuda ke-95 di SMKN 1 Purwosari membentuk barisan manusia yang menyimbolkan kolaborasi dan warna, menunjukkan keanekaragaman suku, bahasa dan budaya heterogenitas sebagai sumber memajukan Indonesia.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&ldquo;BERSAMA MAJUKAN INDONESIA&rdquo;</strong></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 117, NULL, 1, NULL, '2024-03-03 06:54:30', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (507, 'Pengoptimalan Gerakan Literasi Sekolah dengan Program \"Pustaka Keliling\" di SMK Negeri 1 Purwosari', '', '1699018245.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">SMKN 1 Purwosari - Hari ini (3/11), SMK Negeri 1 Purwosari melaksanakan Program &quot;Pustaka Keliling&quot; untuk pertama kalinya dengan tujuan utama meningkatkan minat baca peserta didik. Program ini merupakan langkah inovatif dalam menghadirkan literatur berkualitas bagi perkembangan metodologi berpikir peserta didik.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Program &quot;Pustaka Keliling&quot; ini diinisiasi oleh Tim Gerakan Literasi Sekolah (GLS) bersama dengan Organisasi Siswa Intra Sekolah (OSIS) serta dukungan dari para guru pendamping.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">&quot;Pustaka Keliling&quot; adalah program yang menghadirkan perpustakaan keliling ke dalam lingkungan sekolah. Melalui program ini, siswa dapat dengan mudah mengakses beragam buku dan materi literatur tanpa harus pergi ke perpustakaan.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Pelaksanaan program ini berlangsung setiap hari Jumat. Hari ini, Jumat (3/11), hari pertama pelaksanaan Program &quot;Pustaka Keliling.&quot; Kegiatan ini dibuka dalam dua sesi, sesi pertama dimulai pada pukul 07.00 - 07.30 WIB dan sesi kedua pada pukul 12.00 - 13.00 WIB. Lokasi kegiatan mencakup area hutan sekolah, lapangan indoor, dan depan bengkel Teknik Pemesinan (TPM).</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Program &quot;Pustaka Keliling&quot; ini bertujuan untuk meningkatkan minat baca peserta didik. Akses yang mudah ke berbagai jenis buku dan literatur diharapkan dapat mengubah pandangan peserta didik terhadap membaca menjadi lebih positif.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Salah satu peserta didik, Gede Ngurah Manik, dari kelas X DKV-1, berbagi pengalaman, &quot;Saya awalnya hanya ingin numpang absen ke sini, karena dipaksa Pak Ketua. Tapi ya akhirnya sekarang saya baca buku &#39;Membangun Kembali Jati Diri Bangsa&#39;, soalnya penasaran.&quot; Program ini mendorong minat baca siswa dengan cara yang menarik dan menyenangkan.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Program &quot;Pustaka Keliling&quot; di SMK Negeri 1 Purwosari adalah contoh nyata upaya sekolah untuk meningkatkan minat baca peserta didik dengan memberikan akses mudah ke berbagai jenis literatur dan menciptakan lingkungan yang mendukung. Program ini diharapkan akan memberikan dampak positif terhadap perkembangan literasi peserta didik. Semangat dan partisipasi peserta didik seperti Gede Ngurah Manik menjadi bukti bahwa program ini mampu merangsang minat baca yang awalnya mungkin tidak begitu tertarik.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Semoga &quot;Pustaka Keliling&quot; di SMK Negeri 1 Purwosari dapat menjadi inspirasi bagi sekolah lainnya dalam menjalankan program serupa. Peningkatan minat baca siswa adalah investasi penting dalam pembentukan generasi yang cerdas dan berpengetahuan.</span></span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\"><span style=\"font-family:&quot;Times New Roman&quot;,serif\">Salam Literasi!</span></span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 108, NULL, 1, NULL, '2024-03-04 06:13:33', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (508, 'Prestasi Gemilang : 3 Siswa SMKN  1 PURWOSARI Kembali Raih Juara', '', '1699256792.jpg', '', NULL, NULL, '<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Siswa Siswi SMKN 1 Purwosari kembali mengukir prestasi dalam KEJURDA INKAI Jawa Timur yang berlangsung pada tanggal 4 &ndash; 5 November 2023 di GOR UNESA Surabaya. </span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Mereka adalah :</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">1. Tsania Mukamila kelas XII RPL, Juara 1 KATA Perorangan Putri under 21 tahun</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">2. I Gede Dava Pe kelas XI RPL, Juara 1 KATA perorangan Putra SMA</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">3. Jihan Nurfadila, Kelas XI DKV2, Juara 2 KUMITE 47 Kg&nbsp; Putri SMA</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Kepala SMKN 1 Purwosari, Rudi Trisantoso, S.Pd, M.Pd menyatakan kebanggaannya atas prestasi luar biasa para siswa ini. Ia berharap prestasi mereka akan menjadi berkah dan manfaat bagi diri mereka sendiri, keluarga, sekolah, dan masyarakat.</span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Prestasi ini merupakan bukti nyata bahwa dengan kerja keras, ketekunan, dan keyakinan, kita semua dapat meraih prestasi gemilang. </span></span></span></p>\n\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"font-size:14.0pt\">Selamat kepada&nbsp; para juara,&nbsp; semoga terus menginspirasi dan mencapai prestasi yang lebih tinggi lagi. Aamiin</span></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 144, NULL, 1, NULL, '2024-02-29 16:08:03', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (509, 'Pelatihan Produksi Konsorsium Bakt Bioferrtilizer Dan Bioprotektan Berbasis Sensor Suhu Dari UMM', '', '1699846920.jpg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksanaan PMBS (Pengabdian Masyarakat Berbasis Sekolah): Rabu, 01 November 2023</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Di SMKN 1 Purwosari</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pemateri dan Tim dari UMM (Universitas Muhammdiyah Malang):</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">1. Dr. Ir. Agus Zainudin, MP. (Ketua prodi agroteknologi)</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. Erfan Dani Septia, SP., MP. ((Sekretaris prodi agroteknologi sekaligus ketua tim pengabdian)</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3. Aulia Zakia, SP., MSi. </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">4. Muammar Habibi Irsyad, SP. </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">5. Akhmad Rizal Oktafian, SP. </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6. Sohnif Nurwicahyo Putra</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">7. Iqbal Maulana Zulfan</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pertanian organic dan urban farming (hidroponik) telah banyak dikembangkan sebagai salah satu upaya untuk menarik generasi muda agar tidak enggan bertani, dengan memanfaatkan teknologi yang disesuaikan dengan perkembangan zaman.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kegiatan pelatihan ini dilakukan dengan memberikan materi dan praktik produksi bakteri yang memiliki kemampuan sebagai biofertilizer maupun bioprotektan. Produksi dilakukan dengan menggunakan bakteri koleksi UMM dan telah teruji baik skala laboratorium maupun screen house. Produk pupuk hayati yang akan dikembangkan berupa konsorsium bakteri, sehingga dalam satu produk memberikan dua keunggulan sekaligus yaitu sebagai biofertilizer (pupuk) dan bioprotektan (antagonis).</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksanaan kegiatan pelatihan diawali dengan materi secara klasikal dari UMM, sebagai pengantar pada siswa sebelum praktik secara langsung. Kegiatan praktik dibagi menjadi beberapa sesi yaitu eksplorasi, persiapan/preparasi, isolasi, perbanyakan, produksi.</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelatihan isolasi dan perbanyakan dilakukan dalam 1 hari, untuk kegiatan pemantauan dilakukan 14 hari setelah praktik isolasi.</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 67, NULL, 1, NULL, '2024-02-26 23:44:10', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (510, 'Kunjungan Belajar dari SDN Purwosari II Terkait Pembelajaran IPA Materi Perkembangbiakan Tumbuhan', '', '1699851679.jpg', '', NULL, NULL, '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pelaksanaan Kunjungan Belajar : Selasa, 07 November 2023</span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Peserta :</span></span></p>\n\n<ul style=\"list-style-type:square\">\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kepala SDN Purwosari II</span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Tenaga Pendidik dan Kependidikan SDN Purwosari 2 sebanyak 10 orang</span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Siswa siswi kelas VI, sebanyak 24 siswa</span></span></li>\n</ul>\n\n<p style=\"text-align:justify\">&nbsp;</p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pembelajaran kontekstual (<em>contextual learning</em>) adalah konsep belajar yang membantu guru mengkaitkan antara materi yang diajarkanya dengan situasi dunia nyata siswa dan mendorong siswa membuat hubungan antara pengetahuan yang dimilikinya dengan penerapan dalam kehidupan sehari-hari. Pada kesempatan ini, SDN Purwosari II mengadakan outing class untuk siswa kelas VI, untuk menguatkan pengetahuan dan keterampilan siswa pada kegiatan pembelajaran IPA di jurusan ATPH. </span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kegiatan di jurusan ATPH ini dilakukan dengan suasana yang menyenangkan, bermakna, mudah dipahami oleh siswa dengan memanfaatkan teknologi yang ada. Kegiatan yang dilakukan yaitu :</span></span></p>\n\n<ul style=\"list-style-type:square\">\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pengenalan sekolah</span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pemaparan materi dan tanya jawab terkait perkembangbiakan tanaman</span></span></li>\n	<li style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Praktik siswa dengan didampingi oleh siswa kelas XI ATPH</span></span></li>\n</ul>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Siswa kelas VI dibagi menjadi 4 kelompok, masing-masing praktik perbanyakan tanaman secara generative (dari biji), perbanyakan vegetative dengan Stek, Cangkok, grafting dan hidroponik. Praktik dilakukan secara bergantian (<em>rolling</em>), sehingga semua siswa dapat melakukan praktik tiap kegiatan. Diharapkan setelah belajar dari SMK, mereka dapat mempraktikkan di sekolah. </span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 112, NULL, 1, NULL, '2024-03-01 06:45:58', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (511, 'Gelar Karya P5 Tahun Ajaran 2023-2024 dengan Tema \"Gaya Hidup Berkelanjutan\"', '', '1700663180.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hari ini, tanggal 21 November 2023 bertempat di Lapangan Indoor SMK Negeri 1 Purwosari, telah dilaksanakan Gelar Karya Projek Penguatan Profil Pelajar Pancasila (P5) yang diikuti dan dimeriahkan oleh seluruh peserta didik SMK Negeri 1 Purwosari terutama kelas X dan XI beserta pembimbingnya.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Gelar karya P5 kali ini bertema &ldquo;Gaya Hidup Berkelanjutan : Kreativitas Daur Ulang Limbah Menjadi Kerajinan Bernilai Jual&rdquo;.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kegiatannya meliputi : daur ulang limbah menjadi kerajinan bernilai jual, lomba pidato bahasa Jawa, lomba musikalisasi puisi, lomba news anchor, dan dimeriahkan juga dengan lomba fashion daur ulang.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Adapun tujuan dari Gelar Karya P5 adalah </span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">1. Membangun kesadaran untuk bersikap dan berperilaku ramah lingkungan, mempelajarai potensi krisis keberlanjutan yang terjadi di lingkungan sekitarnya serta mengembangkan kesiapan untuk menghadapi dan memitigasinya.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. Mampu mengurangi limbah dan meningkatkan kreativitas mengolah limbah menjadi kerajinan</span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>&ldquo;DAUR ULANG SAMPAHMU, JANGAN KOTORI BUMIMU&rdquo;</strong></span></span></p>\n', 1, 'Lapangan Indoor SMKN 1 Purwosari', 3, 9, 120, NULL, 1, NULL, '2024-03-03 00:19:07', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (512, 'JOB FAIR DAN CAREER EXPO: SMKN 1 PURWOSARI MENJADI JEMBATAN BAGI PENCARI KERJA', '', '1700798011.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Purwosari -&nbsp; Di masa sulitnya mencari pekerjaan, SMKN 1 Purwosari mengadakan <em>Job Fair</em> dan <em>Career Expo</em>. Kegiatan yang dilaksanakan di lapangan <em>indoor </em>SMKN 1 Purwosari pada tanggal 23 &ndash; 24 November 2023 ini mendapat perhatian yang luar biasa dari para pelamar kerja. Tujuan diadakannya <em>Job Fair</em> dan <em>Career Expo</em> ini adalah ingin menjalin kerjasama dengan industri agar bisa memberikan kesempatan pada alumni dan masyarakat umum untuk membuka lowongan pekerjaan.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ada 22 perusahaan atau industri yang terlibat dalam <em>Job Fair</em> dan <em>Career Expo</em> di SMKN 1 Purwosari, anatar lain:</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">1. PT Mitra Tata Kerja&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">2. PT Amerta Asa Media&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">3. PT Wijaya Plywood&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">4. PT Kartika Sari Mulia&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">5. PT FIF Group&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">6. PT Morodadi Prima&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">7. PT JIAEC&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">8. PT Wijaya Kusumo Joyo Mulyo&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">9. PT Micro Madani Institute</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">10. Kebun Pak Budi</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">11. PT ayora Indah, Tbk</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">12. PT Cisarua Mountain Dairy</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">13. PT Handal Yesindo Sejahtera</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">14. PT Daniza Prima Sentosa</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">15. PT Cisangkan</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">16. PT Buana Megah</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">17. PT Inti Dynamica System</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">18. PT KArya Idaman Sejahtera</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">19. PT Tentrem</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">20. PT Satoria Aneka Industri</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">21. PT Royal Caravan Hotel</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">22. PT Bagong Dekaka Makmur</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Di awali dengan pembukaan oleh Ibu Kepala Cabang Dinas Pendidikan Wilayah Pasuruan di Aula SMKN 1 Purwosari, kemudian dilanjutkan dengan pemotongan pita sebagai tanda <em>Job Fair </em>dan <em>Career Expo </em>telah dibuka.&nbsp; Antusiasme alumni dan masyarakat tampak jelas dari jumlah pendaftar hingga mencapai 2.478 peserta pada gelombang pertama. Pelamar dari berbagai daerah bukan hanya wilayah Kabupaten Pasuruan, tetapi banyak dari wilayah di luar Kabupaten Pasuruan, seperti Blitar dan Malang Selatan. &nbsp;Terlihat semangat para pelamar begitu besar untuk memperjuangkan kelayakan bagi hidupnya.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Saat akan melamar pekerjaan, para pelamar mempersiapkan segala sesuatunya dengan harapan yang terbaik agar bisa diterima pada perusahaan yang mereka inginkan. &ldquo;Butuh sebuah persiapan, seperti membuat lamaran pekerjaan sesuai dengan posisi yang diinginkan. Selain itu, juga perlu adanya sebuah pengalaman agar dapat menambah nilai lebih di mata perusahaan,&rdquo; ujar Dimas, salah satu pelamar. Mereka berkunjung dari satu <em>stand </em>&nbsp;ke <em>stand</em> lainnya untuk menaruh harapan besar berupa surat lamaran berserta berkas-berkas yang telah disiapkan.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Harapan yang besar pula dari penyelenggara <em>Job Fair </em>dan <em>Career </em>Expo agar bisa memberi kesempatan pekerjaan dan pengalaman bagi siswa siswi SMKN 1 Purwosari. &ldquo;Selain bermanfaat bagi alumni dan masyarakat umum, kegiatan <em>Job Fair</em> dan <em>Career Expo</em> ini juga salah satu program untuk mengenalkan kepada siswa khususnya kelas XII yang nantinya akan lulus agar bisa belajar dari apa yang mereka lihat. Apa saja persiapan yang dibutuhkan untuk melamar sebuah pekerjaan, serta strategi agar perusahaan tertarik dengan lamaran yang dibuat,&rdquo; ujar Bapak Muhadi, selaku ketua panitia.</span></span></p>\n', 1, 'Lapangan Indoor SMKN 1 Purwosari', 3, 9, 82, NULL, 1, NULL, '2024-03-01 02:02:57', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (513, 'Hari Guru Nasional 2023: Bergerak Bersama, Rayakan Merdeka Belajar', '', '1701143639.jpg', '', NULL, NULL, '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><span style=\"color:#212529\">Hari Guru Nasional 2023 jatuh pada hari ini, Sabtu (25/11/2023) yang selalu diperingati pada tanggal 25 November setiap tahunnya. Tema Hari Guru Nasional 2023 kali ini yakni &quot;Bergerak Bersama, Rayakan Merdeka Belajar&quot;. </span></span></span></p>\n\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><span style=\"background-color:white\"><span style=\"color:black\">Hari Guru di Indonesia juga sekaligus sebagai bentuk perayaan untuk organisasi yang menghimpun guru se-Indonesia, yaitu Persatuan Guru Republik Indonesia (PGRI). Organisasi ini resmi berdiri pada 25 November 1945 sebagai hasil keputusan dalam Kongres Guru Indonesia yang dilaksanakan pada 24 s.d. 25 November 1945.</span></span></span></span><br />\n<span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><span style=\"color:black\"><span style=\"background-color:white\">Bertepatan dengan hari lahir PGRI itu, Hari Guru Nasional pun selalu diperingati dan dirayakan setiap tahunnya. Berbagai kegiatan seperti upacara dan kegiatan kreatifitas di lingkungan sekolah adalah agenda wajib. Kegiatan tersebut diharapkan dapat mendorong semangat dan apresiasi untuk guru yang senantiasa membantu anak-anak Indonesia dalam menemukan potensi diri dan meraih cita-cita.</span></span></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\"><strong><span style=\"color:#212529\">&ldquo;WAHAI PARA GURU INDONESIA SELALU TERANG DALAM KEGELAPAN, PENYEDIA EMBUN UNTUK PELEPAS DAHAGA DAN TETAP SEMANGAT BERJUANG UNTUK MENCERDASKAN DAN MEMBANGUN INSAN CEDEKIA&rdquo;</span></strong></span></span></p>\n', 0, 'Lapangan Indoor SMKN 1 Purwosari', 3, 9, 38, NULL, 1, NULL, '2024-03-03 21:16:06', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (514, 'Raih Prestasi Kembali: Dua Siswa SMKN 1 Purwosari Juara dalam Festival Basa UNIWARA', '', '1701146981.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Siswa SMK Negeri 1 Purwosari kembali meraih prestasi dalam Festival Basa yang diselenggarakan oleh Himpunan Mahasiswa Pendidikan Bahasa dan Sastra Indonesia Universitas PGRI Wiranegara.&nbsp;<br />\nDiikuti sekitar 15 peserta SMA/SMK/MA sederajat dan Mahasiswa, dua anak dari SMK Negeri 1 Purwosari berhasil meraih juara, yakni Dhiya&rsquo; Ulchak Firmansyah dari kelas X DKV 1 sebagai Juara 1 dan Dharmazka Arsyadani dari kelas XI RPL sebagai Juara 3.&nbsp;</span></span><br />\n<span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Walaupun lawan mereka tidak sederajat, tetapi tidak mematahkan semangatnya untuk berkarya dalam menulis. Festival Basa yang diselenggarakan oleh HMPBSI UNIWARA ini terdapat dua bidang lomba yakni, Esai dan Infografis. Esai sendiri merupakan karangan prosa yang membahas suatu masalah secara sepintas lalu dari sudut pandang pribadi penulisnya. Dalam hal ini Dhiya&rsquo; mengangkat judul &ldquo;Tari Terbang Laro dalam Harmoni Literasi Digital&rdquo; dan Dharmazka mengangkat judul &ldquo;Adat Petik Laut di Pesisir Utara Pasuruan: Sebuah Tradisi Turun Temurun untuk Mencari Berkah dan Menolak Bala&rdquo; &nbsp;</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Ulcha&rsquo; mengatakan bahwa lomba ini dapat menjadi sarana siswa untuk berlatih membuat karya tulis. Hal tersebut terbukti Dhiya&rsquo; sering diminta untuk menjadi penulis artikel laman web milik sekolah. Rasa bangga yang luar biasa dari kami keluarga besar SMKN 1 Purwosari semoga bisa menjadi motivasi bagi siswa-siswa yang lain.</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 70, NULL, 1, NULL, '2024-02-24 03:56:11', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (515, 'TAK HANYA SISWA,KEPALA SMKN 1 PURWOSARI IKUT MENOREHKAN PRESTASI KEPALA SMK PK TERBAIK TINGKAT JATIM', '', '1701249069.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Purwosari &ndash; Dinas Pendidikan Provinsi Jawa Timur mengadakan ajang bergengsi bagi seluruh guru dan tenaga pendidikan. Namun, tidak hanya guru dan tendik, kepala sekolah pun mempunyai bagian dalam lomba tersebut. Lomba yang diselenggarakan oleh Bidang GTK Dinas Pendidikan Provinsi Jawa Timur&nbsp; mempunyai bidang lomba penilaian kepala SMK PK terbaik tingkat Jawa Timur. Bertempat di Hotel Harris, Surabaya (28/11), sebagai saksi bahwa Bapak Rudi Trisantoso meraih penghargaan sebagai Kepala SMK PK Terbaik Tingkat Provinsi Jawa Timur Tahun 2023.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Cabang Dinas merekomendasikan tiga kepala sekolah (SMK dari SMK PK, SMA dan SLB dari Sekolah Peggerak) untuk dinilai di tingkat provinsi. Untuk SMK PK, Pak Rudi Trisantoso, kepala SMKN 1 Purwosari terpilih mewakili SMK PK di wilayah Pasuruan. </span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Sebuah kerja keras terbayar dengan sangat luar biasa tak lepas dari usaha&nbsp; yang telah dilakukan. Sama halnya seperti lomba-lomba lain, persiapan dan tantangan dalam perlombaan pasti ada. &ldquo;Harus berupaya maksimal karena harus bersaing dengan beberapa kepala sekolah yang lebih senior dan juga SMK unggulan dari kabupaten dan kota lain,&rdquo; ujar Pak Rudi. </span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Selain itu, penilaian lomba yang dilaksanakan pada 15 November 2023 berpedoman pada rapor mutu sekolah menjadi dasar wawancara kepala sekolah terkait program dan inovasi yang dilakukan. Mencapai hasil yang maksimal menunjukkan bahwa rapor mutu SMKN 1 Purwosari memang layak dan sesuai dengan program-program unggulan yang dijalankan.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kabar membahagiakan dan membanggakan juga langsung terdengar oleh seluruh warga sekolah, guru, pegawai, dan siswa, serta pengawas sekolah. &ldquo;Selamat dan sukses untuk Pak Rudi Trisantoso sebagai juara 1 kepala sekolah terbaik program SMK PK,&rdquo; ucap Pak Rifai selaku pengawas SMKN 1 Purwosari.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">&ldquo;Bangga terhadap Bapak Rudi Trisantoso yang telah meraih juara 1 kepala SMK PK tingkat Provinsi Jawa Timur. Semoga dengan diraihnya prestasi tersebut menjadi motivasi untuk meraih prestasi-prestasi selanjutnya,&rdquo; kata Bu Julia, selaku guru MKT.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Peserta didik pun turut merasakan bangga dan apresiasi setinggi-tingginya untuk Pak Rudi. &ldquo;Sebagai siswa SMKN 1 Purwosari&nbsp; sangat bangga dengan diraihnya juara 1 sebagai kepala SMK PK terbaik tingkat Provinsi Jawa Timur karena bisa menjadi motivasi untuk siswa agar terus menorehkan prestasi seperti yang dilakukan Pak Rudi,&rdquo; ujar Dea salah satu siswa kelas XI.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Kesan, harapan dan pengaruh atas dirainya prestasi ini, sejalan dengan harapan yang disampaikan Pak Rudi Trisantoso. Beliau mengatakan bahwa siswa, sekolah, guru dan kepala sekolah bersama-sama bisa menunjukkan prestasi di bidangnya masing-masing untuk nama baik sekolah. Selain itu, pengaruh untuk pribadi Pak Rudi bisa menjadi penyemangat agar ke depannya selalu bisa berupaya lebih baik lagi dalam menjalankan tugas sebagai kepala sekolah.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Rasa bangga yang luar biasa terucap dari seluruh keluarga besar&nbsp; SMKN 1 Purwosari, semoga dapat membawa SMKN 1 Purwosari menjadi lebih baik dan terus menorehkan prestasi.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>SELAMAT KEPADA BAPAK RUDI TRISANTOSO, S.Pd., M.Pd</strong></span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><strong>ATAS DIRAIHNYA JUARA 1 KEPALA SEKOLAH MENENGAH KEJURUAN PUSAT KEUNGGULAN JENJANG SMK TINGKAT PROVINSI JAWA TIMUR TAHUN 2023</strong></span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 92, NULL, 1, NULL, '2024-03-04 02:47:39', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (516, 'Kegiatan Istighosah dan DoÃ¢â‚¬â„¢a Bersama Untuk Keselamatan dan Kesuksesan Semester Gasal Tahun 20', '', '1701748075.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pada hari Jum&rsquo;at, tanggal 01 Desember 2023 bertempat di Lapangan Indoor SMK Negeri 1 Purwosari telah dilaksanakan kegiatan istighosah dan do&rsquo;a bersama yang bertujuan untuk Keselamatan dan Kesuksesan segenap Keluarga Besar SMK Negeri 1 Purwosari.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Hadir pula Ustadzah Siti Khurrotin, S.Pd., M.Pd, putri dari K.H. Sholeh Bahruddin, pengasuh Pondok Pesantren Ngalah Purwosari-Pasuruan sebagai penceramah.</span></span></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n', 0, 'Lapangan Indoor SMKN 1 Purwosari', 3, 9, 31, NULL, 1, NULL, '2024-02-10 18:34:45', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (517, 'AYO RAMAIKAN PERPUSTAKAAN SEKOLAH', '', '1701847316.jpeg', '', NULL, NULL, '<p>&ldquo;Perpustakaan adalah jendela dunia yang membuka pikiranmu.&rdquo; &ndash; Artinya, menjelajahi dunia melalui buku-buku di perpustakaan merupakan cara paling murah dan paling cepat.<br />\n<br />\n&ldquo;Datanglah ke perpustakaan dan temukan kisah yang belum pernah kamu dengar sebelumnya.&rdquo; Ajakan ini berisi pesan bahwa setiap bulannya pasti ada saja buku baru yang belum sempat dibaca, jadi jika ingin mendapatkan kisah baru, bisa datang ke perpustakaan.</p>\n', 0, 'SMKN 1 Purwosari', 3, 11, 65, NULL, 1, NULL, '2024-03-03 19:08:14', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (518, 'Peringatan Hari Anti Korupsi Sedunia Tahun 2023 di SMKN 1 Purwosari', '', '1702428217.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Pada peringatan Hari Anti Korupsi Sedunia (Hakordia) tahun ini, Kejaksaan Negeri Kabupaten Pasuruan mengadakan Sosialisasi tentang pencegahan tindak korupsi &nbsp;dengan tema &ldquo;Sinergi Berantas Korupsi, Untuk Indonesia Maju&rdquo;. Sosialisasi diberikan kepada 250 siswa&nbsp; dan 37 orang guru BK SMA/SMK Negeri dan Swasta &nbsp;di Kabupaten Pasuruan bertempat di aula SMKN 1 Purwosari. &nbsp;Hadir sebagai narasumber dalam kegiatan ini Bapak. Agung Tri Radityo, SH, MH (Kasi Intelejen Kejari Pasuruan) dan Bapak Roy Adrian (Kasi Pidsus Kejari Pasuruan). &nbsp;Dalam pemaparannya, narasumber menyampaikan bahwa Korupsi adalah suatu bentuk ketidakjujuran atau tindak pidana yang dilakukan oleh seseorang atau suatu organisasi yang dipercayakan dalam suatu jabatan kekuasaan, untuk memperoleh keuntungan yang haram atau penyalahgunaan kekuasaan untuk keuntungan pribadi seseorang. Beberapa contoh bentuk korupsi adalah menyuap dan memberi hadiah, menerima suap dan menerima hadiah. </span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Dari sisi ekonomi, korupsi juga berdampak, antara lain : lesunya pertumbuhan ekonomi dan investasi, penurunan produktivitas, rendahnya kualitas barang dan jasa publik, menurunnya pendapatan negara dari sektor pajak dan meningkatnya hutang pemerintah.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Upaya pencegahan yang bisa dilakukan adalah : &nbsp;hidup sesuai kemampuan, &nbsp;disiplin mengatur waktu fokus dengan kegiatan yang harus dilakukan, &nbsp;mengatur pengeluaran sesuai dengan kebutuhan (memperhatikan gaya hidup).</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Bapak Rudi Trisantoso, S.Pd, M.Pd (Kepala SMKN 1 Purwosari), dalam sambutannya menyampaikan terima kasih untuk kesempatan yang telah diberikan kepada SMKN 1 Purwosari sebagai tuan rumah kegiatan sosialisasi tersebut dan berharap semoga dapat bermanfaat bagi seluruh peserta yang hadir.</span></span></p>\n', 1, 'Aula SMKN 1 Purwosari', 3, 9, 71, NULL, 1, NULL, '2024-02-29 10:39:15', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (519, 'Signing of Implementation Agreement and Merchandise Giving antara KT&G TSPM dan SMKN 1 Purwosari', '', '1702534285.jpg', '', NULL, NULL, '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Hari Kamis, 14 Desember 2023 bertempat di Ruang sidang 1 SMK Negeri 1 Purwosari telah dilaksanakan &ldquo;Signing of Implementation Agreement and Merchandise Giving&rdquo; antara KT&amp;G Tri Sakti Purwosari Makmur dengan SMK Negeri 1 Purwosari.</span></span></p>\n\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Acara ini di hadiri dari KT&amp;G TSPM : Mr. Byun Bong Jin (Head of Factory Management Directorate), Ibu Riwayati Triswanti (Head of Suistainability Division), Ibu Ernawati Amir (Manager General Affair), Ibu Amalia (Corporate Social Responsibility), sedangkan dari SMKN 1 Purwosari di hadiri oeh Bpk. Rudi Trisantoso, S.Pd., M.Pd (Kepala SMK Negeri 1 Purwosari) beserta Management Sekolah dan perwakilan siswa penerima beasiswa dari KT&amp;G TSPM. Serta terdapat 80 siswa penerima beasiswa, baik siswa yang berprestasi maupun siswa yang kurang mampu.</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 129, NULL, 1, NULL, '2024-03-03 22:30:15', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (520, 'Kegiatan Guru Tamu Oleh KT&G TPSM yang Diikuti Siswa Siswi SMKN 1 Purwosari', '', '1703145520.jpg', '', NULL, NULL, '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Pada tanggal 20 Desember 2023 telah dilaksanakan kegiatan Guru Tamu bertempat di Aula SMK Negeri 1 Purwosari dan diikuti oleh siswa siswi kelas XII. Hadir sebagai pemateri Bapak Anggara Prihardana dan Bapak Herry Prakoso selaku Manager HSE dari KT&amp;G TPSM.</span></span></p>\n\n<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Materi yang disampaikan adalah &lsquo;Budaya Industrial K3 dan Strategi Sukses dalam Proses Rekrutmen di Perusahaan&rsquo;. </span></span></p>\n', 0, 'Aula SMKN 1 Purwosari', 3, 9, 73, NULL, 1, NULL, '2024-03-04 02:08:36', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (521, 'Campus Expo 2024: Show Your Creativity and Passion for Future Changes', '', '1705153849.JPG', '', NULL, NULL, '<p><span style=\"font-family:Times New Roman,Times,serif\"><span style=\"font-size:18px\">Hari Rabu, 10 Januari 2024 bertempat di aula SMK Negeri 1 Purwosari telah dilaksanakan&nbsp;<em>Campus Expo&nbsp;</em>2024 dan diikuti oleh beberapa Perguruan Tinggi.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Campus Expo</em>&nbsp;adalah wujud berbagi informasi alumni bagi adik kelas tentang studi lanjut, tip dan trik masuk PTN serta bagaimana menumbuhkan daya saing dan prestasi. Kegiatan ini diikuti oleh alumni yang tersebar di berbagai kampus, tidak hanya di Jawa Timur tetapi juga luar Jawa Timur. </span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\"><em>Campus Expo</em>&nbsp;kali ini&nbsp;bertema &quot;<em>Show Your Creativity and Passion for Future Changes</em>&quot; karena berpendidikan tidak menjamin kesuksesan tapi dengan berpendidikan dapat membuka banyak peluang. </span></span></p>\n', 1, 'Aula SMKN 1 Purwosari', 3, 9, 86, NULL, 1, NULL, '2024-03-04 21:49:32', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (522, 'SMKN 1 Purwosari Mendapatkan Penghargaan Bintang 5 Apresiasi SMK BISA 2023', '', '1705688905.jpg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Alhamdulilah SMK Negeri 1 Purwosari berhasil mendapatkan penghargaan Bintang 5 Apresiasi SMK BISA tahun 2023.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Apresiasi SMK BISA 2023, diselenggarakan oleh PT. ASTRA Internasional melalui program Rejuvinasi Kriteria SMK BISA Astra. Bekerja sama dengan Kementrian Pendidikan dan Kebudayaan serta Kementrian Perindustrian Republik Indonesia, event ini diikuti oleh 20 SMK binaan PT. ASTRA melalui perusahaannya di seluruh Indonesia, salah satunya SMK Negeri 1 Purwosari.</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">SMK dinilai berdasarkan 4 pilar , yaitu status kelayakan dan kinerja sekolah, terkait komitmen <em>link and match</em> SMK-Industri serta fasilitas sekolah, selanjutnya adalah budaya industri di lingkungan sekolah, implementasi <em>development link and match </em>kerja sama dengan industri, serta penerapan <em>teaching factory</em>.</span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">SELAMAT ATAS DIRAIHNYA BINTANG 5 SMK NEGERI 1 PURWOSARI</span></span></p>\n\n<p style=\"text-align:center\"><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">JAYA...JAYA...JAYA...LUAR BIASA</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 67, NULL, 1, NULL, '2024-03-03 04:12:59', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (523, 'Turut Berdukacita Atas Berpulangnya Ananda Auliyah Maghfiroh Rahmadani Siswi SMKN 1 Purwosari ', '', '1706409806.JPG', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Innalillahi wa inna ilaihi rajiun</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Allahumma firlaha warhamha wa&rsquo;aafihii wa&rsquo;fu &lsquo;anha</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Keluarga Besar SMK Negeri 1 Purwosari Berduka Atas Meninggalnya :</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">AULIYAH MAGHFIROH RAHMADANI (XI TKJ-1) Beserta Ibunda Tercinta (IBU ANIK)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Semoga husnul khotimah, amal dan ibadah almarhumah di terima Allah Subhanahu Wa Ta&rsquo;ala dan keluarga yang ditinggalkan diberikan ketabahan dan keikhlasan&hellip;Aamiin</span></span></p>\n', 1, 'Pasuruan', 3, 9, 66, NULL, 1, NULL, '2024-03-03 02:17:52', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (524, 'Turut Berdukacita Atas Berpulangnya Bapak Dadik Hariyadi ', '', '1708571161.jpeg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Innalillahi wa inna ilaihi raji&#39;un Allahummaghfirlahu warhamhu wa&#39;afihi wa&#39;fu&#39;anhu</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Keluarga besar SMK Negeri 1 Purwosari berduka cita atas meninggalnya Bpk Ir. Dadik Hariyadi (Kepala SMK Negeri 1 Purwosari 2000-2011)</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Semoga Alloh menerima amal ibadah beliau dan keluarga yang ditinggalkan diberikan ketabahan dan keikhlasan....aamiin</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 9, 44, NULL, 1, NULL, '2024-03-03 07:00:57', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (525, 'SMKN 1 Purwosari Merebut Juara 1 dan 3 Lomba Karya Tulis Ilmiah (KTI) Inovasi Teknologi', '', '1709518852.jpeg', '', NULL, NULL, '<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Lomba Karya Tulis Ilmiah (LKTI) Inovasi Teknologi diselenggarakan oleh Fakultas Pertanian, Univ. Tribhuwana Tunggadewi, &nbsp;bidang lomba ide inovasi dan teknologi presisi dalam karya tulis tingkat SMA/SMK sederajat, Se Jawa Timur</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Juara 1 : Tim 2 SMKN 1 Purwosari&nbsp;<br />\n(Muhammad Avicenna Bukhari Islami, Siti Muslihah , Tasya Dhafina Ramadhani), dengan guru pembimbing bu Umi Prastiwi E, S.P</span></span></p>\n\n<p><span style=\"font-size:18px\"><span style=\"font-family:Times New Roman,Times,serif\">Juara 3 : Tim 1 SMKN 1 Purwosari<br />\n(Jia Nurlailah, M. Deviansyah Perwira, M. Rendra Sefrizal), dengan guru pembimbing Bu Fatimah Zahro, S.P., M.Agr</span></span></p>\n', 1, 'SMKN 1 Purwosari', 3, 10, 6, NULL, 1, NULL, '2024-03-04 21:52:17', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (547, 'Laravel', '', '1598502726.jpeg', '', '0000-00-00', NULL, '<p>Wahh setelah berjuang di SMKCoding dan WDC, kini kamu balik lagi dengan melanjutkan sesi tutorial Laravel 7, namun kami mohon maaf untuk kali ini kami memanfaatkan fitur dari website medium untuk membuat tutorial karena banyaknya kegiatan diluar dugaan..<br />\n<br />\nPembahasan kali ini adalah structure directory dan migrations. Bermanfaat pastinya buat kalian yang masih beginner dalam programming php dengan framework Laravel<br />\n<br />\nBtw, makasih yah.. yang udah ucapin congrats buat kami. Semoga sehat selalu ?<br />\n<br />\nCheck link di bio yah.. ?<br />\n<br />\nhttps://medium.com/<a href=\"https://www.instagram.com/khusnul20032002/\" tabindex=\"0\">@khusnul20032002</a>/migration-pada-laravel-a9149736bc0d<br />\n&nbsp;</p>\n', 0, NULL, 1, 3, 372, NULL, 1, NULL, '2024-03-04 08:28:50', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (548, 'Praktek Laravel', '', '1598502781.jpeg', '', '0000-00-00', NULL, '<p>Framework Laravel adalah sebuah alat bantu untuk memudahkan kita agar bisa lebih mudah saat membuat website, Framework Laravel ini merupakan framework berbasis PHP ( maksutnya Pakek bahasa PHP) yang sedang populer dikalangan programmer PHP setelah CI (CodeIgniter), Rekomendasi banget deh buat kalian yang masih baru masuk ke dalam dunia web programming, buat belajar framework Laravel ini.. .<br />\n.<br />\nPada seri kali ini kami membuat feed tentang Aplikasi Perpustakaan menggunakan Laravel. Step by step pastinya. Dari instalasi, Layouting, sampai nanti finishing. Jadi.. tetep pantau terus. Walaupun lagi karantina tetep rebahan secukupnya, ngoding selelahnya hehe ?<br />\n&nbsp;</p>\n', 0, NULL, 1, 3, 371, NULL, 1, NULL, '2024-03-01 03:54:38', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (549, 'Javascript', '', '1598502819.jpeg', '', '0000-00-00', NULL, '<p>Pada javascript masa sekarang ini terutama pada ES6, asynchronous lebih banyak digunakan oleh pengguna javascript, yah buat kalian yang baru terjun di pemrograman javascript, tidak ada salahnya jika membuat planning untuk mempelajari Asynchronous ini ?<br />\n<br />\nLet&#39;s code<br />\n&nbsp;</p>\n', 0, NULL, 1, 3, 323, NULL, 1, NULL, '2024-03-01 03:55:02', NULL);
INSERT INTO `tb_pemberitahuan` (`id_pemberitahuan`, `nama`, `target`, `thumbnail`, `banner`, `date`, `time`, `text`, `level`, `location`, `type`, `category`, `viewer`, `created_by`, `approved`, `Approved_by`, `created_at`, `updated_at`) VALUES (550, 'Merdeka Belajar di Era New Normal Covid 19', '', '1598764805.jpg', '', '0000-00-00', NULL, '<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a class=\"badge badge-primary\" href=\"https://cabdinpasuruan.id/\">#Cabang_Dinas_Pasuruan</a></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Berdasarkan anjuran pemerintah tentang belajar, bekerja dan beribadah dari rumah pada tanggal&nbsp; 16 Maret 2020 sesuai Surat Gubernur Jawa Timur Nomor : 420/1780/101.1/2020, perihal Peningkatan Kewaspadaan terhadap <em>Corona Virus Disease (Covid-19) </em>Jawa Timur, SMK Negeri 1 Purwosari melaksanakan kegiatan belajar mengajar di rumah masing-masing&nbsp; termasuk kegiatan PKL atau prakerin.</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Pelaksanaan kegiatan belajar mengajar dengan menggunakan media teknologi informasi internet memunculkan kebiasaan yang baru bagi pendidik dan pelajar. Biasanya siswa dan guru berada dalam ruangan yang sama dan berinteraksi secara langsung, kini siswa dan pendidik diharuskan untuk beradaptasi dengan pembelajaran berbasis <em>digital</em> yang dilakukan di rumah masing-masing, ini memacu para guru untuk lebih mengenal dan mempelajari teknologi informasi serta merancang pembelajaran yang mudah dipahami oleh siswa. Aplikasi&nbsp; yang digunakan pada awal Pendidikan Jarak Jauh (PJJ) yaitu aplikasi<em> Microsoft Teams </em>dan <em>Edmodo </em>tapi, pada saat Tahun ajaran baru 2020-2021 hanya menggunakan Mi<em>crosoft Teams</em>.&nbsp;</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">SMK Negeri 1 Purwosari melakukan perubahan dan penambahan aturan untuk pemberlakuan program Pendidikan Jarak Jauh dengan&nbsp; penjadwalan 1 minggu pertama di isi oleh kegiatan produktif lalu minggu kedua melaksanakan pembelajaran normatif, dalam satu hari maksimal dilakukan 2 pelajaran. Dengan ini, diharapkan siswa-siswi SMK Negeri 1 Purwosari dapat memahami materi lebih baik dan Melakukan 2 kali absensi untuk memastikan siswa selalu hadir di setiap jam pelajaran, link presensi dibagikan melalui grub WhatsApp kelas</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Kebiasaan belajar siswa juga berubah, kini ia diharuskan untuk belajar tanpa diajari secara langsung oleh guru, tanpa terjalin komunikasi secara langsung dengan teman-temannya dan di samping itu ada hambatan lain yaitu beberapa siswa mengalami kekurangan kuota internet dan sinyal yang kurang bagus membuat proses pembelajaran tidak berlangsung dengan optimal.&nbsp;</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Saat memasuki new normal, adapula beberapa protokol kesehatan di lingkungan sekolah yang wajib dipatuhi setiap siswa antara lain; penyemprotan disinfektan yang dilakukan pada saat memasuki gerbang sekolah, pengecekan suhu tubuh setiap siswa, selalu mencuci tangan dengan sabun dan air mengalir pada tempat cuci tangan yang sudah disediakan atau menggunakan hand sanitizer, jaga jarak setiap siswa maksimal 2 meter untuk menghindari kontak antar siswa dan juga siswa diwajibkan selalu memakai masker.</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Walaupun masih banyak kekurangan, SMK Negeri 1 Purwosari tetap berusaha mengoptimalkan proses pembelajaran untuk menciptakan penerus bangsa yang cerdas dan berkarakter. Karena SMK Negeri 1 Purwosari mengedapankan siswa yang mandiri dan berprestasi.&nbsp;</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Sejatinya Virus <em>Corona </em>bukanlah penghadang misi dan visi SMK Negeri 1 Purwosari.&nbsp; Dari pembelajaran daring ini siswa lebih mandiri, lebih bisa mencari materi sendiri, lebih kreatif dan lebih menghargai waktu kebersamaan dengan teman-teman mereka.&nbsp;</span></span></span></p>\n\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><span style=\"font-family:Calibri,sans-serif\"><span style=\"color:#000000\">Pesan kami, <em>Corona </em>hanya virus yang mampu memisahkan jarak kita. Kita yang terbiasa saling merangkul satu sama lain menjadi duduk berjarak satu meter.&nbsp; Tapi ilmu buta jarak, ilmu bisa menyebar di mana saja, sekalipun di rumah saja. Jangan jadikan <em>corona</em> sebagai penumpul semangat belajar kita. Tetap semangat dan di rumah saja!</span></span></span></p>\n\n<p style=\"text-align:justify\"><br />\n<span style=\"font-size:18px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Author Extrakurikuler MCC(Jurnalistik)&nbsp;:<br />\n1.&nbsp;Febiana Angga Lestari 12 atph 1<br />\n2.&nbsp;Maria Helena Kristianti Wangge 12 atph 3<br />\n3.&nbsp;Mukhammad Thoriq Aziz 12 ATPH 2</span></span></p>\n', 0, NULL, 1, 1, 282, NULL, 1, NULL, '2024-03-01 03:54:57', NULL);
